INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (5, "Abort", "Таслах зогсоох", "", "Программ эсвэл системийн үйл ажилгааг гүйцэд дуусахаас өмнө үйл ажилгааг зогсоох,тасалдуулах ажилгаа нь программаар үйлдлийн системээр эсвэл хэрэглэгчээр хийгдэж болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (6, "Absolute address", "Зөвшөөрөгдсөн хаяг ", "", "Программын заавар өгөгдлийн хэсэг бүр компьютерийн санах ойгоос салангад агуулагддаг.Энэ нь адресс хаяг дотор агуулагдаж байршина.Адрес хаяг нь дугаар болон үгийг гол санах ойдоо байрлуулна.Энэ дугаар ялангуяа программын байршлийг тогтоох, аль өгөгдлийн төрөл агуулагдаж байгаа, программын бүтцийн код хэзээ үүсэж салаалсанг агуулна.Хэрэв хаяг нь жинхэнэ хаяг бол компьютерийн дотоод электрон хэсгүүдтэй хамтран хэрэглэгдэнэ.Энэ нь алдартай учир жинхэнэ зөвшөөрөлтэй хаяг юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (7, "Absolute code", "Бүрэн төгс код", "", "Кодон доторх аль нэг хаяг бүрэн төгс байна.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (8, "Absolute instruction", "Бүрэн төгс заавар", "", "Компьютерын заавар доторх аль нэг хаяг нь бүрэн төгс байна. Жишээ нь: шууд заавар% ашигтай заавар, шуурхай заавар, шууд бус заавар", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (9, "Absolute loader", "Бүрэн төгс ачаалах төхөөрөмж", "", "Ачааллах төхөөрөмж нь машины санах ойг эхлүүлж, сүлжээн дэх хаягаас код оруулах юм уу эсвэл ассемблерийг цуглуулж хийх үйлдлийг тааруулна.", "", "");
