INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (11, "Absolute reference", "Абсолют жишиг", "", "Хүснэгттэй ажиллах програм нь нэг нүдэнд нь томъёог хувилах(хуулбарлах)  үед абсолют жишиг ашиглаж болно.Нэр томъёо үргэлж үүрэнд хамааруулна энэ нь чи тайлбарыг үүрэнд байлгах үгүй юу гэдгээ нарийн мэдэж байх ёстой гэсэн үг юм.Эсвэл өөрөө хүсч байвал тайлбарыг өөрийн хүсэннээр дурын мөр баганалуу хөдөлгөж зөөж болно.", "", "");
