INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (55, "Active window", "Идвэхитэй цонх ", "", "Цонх нь түр зуурын дэлэгцэнд харагддаг бөгөөд ялангуяа программуудыг нүүр зургыг харуулдаг.Зөвхөн нэг цонхыг ямар ч үед нэг удаа гаргах боломжтой юм.Аль програмтай хамтран ажилахыг хэрэглэгч идвэхтэй цонхноос өөрөө харж болдог.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (56, "Activity", "Идэвхжүүлэх", "", "Багцын нэгдсэн ажилуудын үйл явц.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (57, "Activity attributes", "Идэвхтэй шинж чанарууд ", "", "Жагсаалтанд байх идэвхтэй олон шинж чанараас хуваарийн дагуу оруулж болно. Идэвхтэй шинж чанарыг кодийг идэвхжүүлэх , өмнөх үеийн замчлагчийг идэвхжүүлэх , логик харилцаа , сэлгэлт болон хоцролт таамаглал . ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (58, "Activity code", "Код идэвхжүүлэх ", "", "Нэг болон хэд хэдэн тоон болон текстэн утгуудыг тодорхойлж хуваарь болон тайланд тусгадаг. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (59, "Activity duration", "Хугацааны идэвхжүүлэлт ", "", "Цаг хугацаа болон календар хооронд хуваарийн дагуу идэвхжүүлэлт эхэлнэ. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (60, "Activity group", "Хэсэг идэвхжүүлэх ", "", "Холбогдох үйл ажиллагаа тусгагдаж байгаа программ хангамжын ажиллах хугацаа мөчлөг ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (61, "Activity identifier", "Үйл ажиллагаа идэвхжүүлэх ", "", "Богино тоон болон текстэн мэдээлэл нь тодорхой оноосон хугацааны дагуу ялгаатай. Ерөнхий онцгой аль нэг төсөл нь схем хүрээнд хуваарилагдана. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (62, "Activity list", "Жагсаалтыг идэвхжүүлэх ", "", "Баримтжуулсан үйл ажиллагааны хүснэгтэн хуваарийн дагуу нарийвчилсан цар хүрээ төслийн багийн ямар гишүүд ямар ажил хийгдэх гэдгийг ойлгож болохоор тодорхойсон байна. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (63, "Activity type", "Төрөл идэвхжүүлэх ", "", "Үйл ажиллагааны ангилалыг 1 алгоритм гүйцэтгэнэ. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (64, "Actor", "Жүжигчин", "", "Үүрэг нь салбар объектийг биелүүлхэд оролцох ажиллагаа.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (65, "Actual cost", "Бодит зардал", "", "Хувиарын дагуу тухайн цаг хугацаанд хийгдсэн ажил , ажлын бүтэц бүрэлдхүүн болон нийт гаргасан зардал , бодит зардал тэмдэглэгдсэн байна. Бодит зардал нь зарим тохиолдолд шууд дангаараа ажилласан хугацаа болон дангаар гаргасан зардал мөн шууд бус нийт зардалыг тооцсон байна .", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (66, "Actual duration", "Бодит үргэжлэх хугацаа", "", "Тухайн ажлын өгөгдөл бүрэлдэхүүн ажилууд нь хувиарын дагуу заагдсан хугацаанд эхэлсэн мөн төлөвлөгөөний дагуу хийгдсэн хугацаа ажил бүрэн биелэгдсэн дууссан хугацаа зэрэг нь багтана .", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (67, "Actual parameter", "Бодит параметр", "", "Функц, процедур нь ашиглаж байх үед,дуудаж програм түүнд параметр дамжуулах шаардлагатай.Албан ёсны параметр нь функц болон ажиллагаанд түүнийг ашиглахад дуудаж програмд өгөгдлийг холбодог.Өгөгдлийн төрөл хангагдсан,бодит параметр эсвэл аргументаар гэгдэн, локал хувьсагч рүү дамжуулдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (68, "Adaptive Differential Pulse Code Modulation", "", "An encoding technique using differential encoding and variable sized quantizing steps. The variance of the step sizes are based on estimates of past signal samples.", "", "ADPCM", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (69, "Adaptive equalizer", "", "A channel equalizer whose parameters are updated automatically and adaptively during the transmission of data. These equalizers are commonly used in fading channels to improve transmission performance.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (70, "Adaptive maintenance", "Тааруулах засвар үйлчилгээ", "", "Программ хангамжийн бүтээгдэхүүнд нэмэлт өөрчлөлт оруулах , дамжуулсаны дараа хийх , өөрчлөгдсөн ашиглах боломжтой программ хангамжийн үр дүнг хадгалах эсвэл орчныг өөрчлөх . ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (71, "Additive White Gaussian Noise", "", "Statistically random radio noise characterized by a wide frequency range with regards to a signal in a communications channel.", "", "AWGN", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (72, "Address", "Хаяг", "", "Мэйл хаяг нь мэйл явуулахад бас хүлээн авахад ашиглагддаг.Мэйл хаяг нь хэрэглэгчийн нэр, @ тэмдэг,эзэмшил буюу домэйн нэр агуулдаг  (e.g.john@nowhere.co.uk).", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (73, "Address book", "Хаягын жагсаалт", "", "Файлд аль мэйл хаягаа эзэмшиж байгааг жагсаадаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (74, "Address field", "Хаягын талбар", "", "Хаягууд, хаягуудыг олж авах шаардлагатай мэдээлэл зэргийг багтаасан компьютерийн удирдлагын талбарыг хаягийн талбар гэнэ. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (75, "Address format", "Хаягийн хэмжээ ", "", "PC-н удирдамжин дах хаягийн талбаруудын эмх цэгцтэй дугаар ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (76, "Address space", "", "", "1.Энэ хаяглалт нь компьютерийн програмийн энэ хаяглалтаар хийдэг   2. Дэс дугаарыг санах ойн төвд боловсруулан хаяглаж чаддаг", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (77, "Addressing exception", "Онцгой хаяглалт ", "", "Энэ програм нь програмийн хэмжээг тоцоолох гадна талыг хэрхэн хаяглах хадгалалт болон боломжит чадлыг гаргадаг.Үүнд өгөгдлийн хасалт үйлдэлийн үгүйсгэл ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (78, "Adjacent channel", "", "In a given set of radio channels, the RF channel whose characteristic frequency is situated next above or next below that of a given channel.", "", "", "Note 1 – The adjacent channel situated above the given channel is known as the “upper adjacent channel” and the one below it as the “lower adjacent channel”. Note 2 – Two adjacent channels may have part of the frequency spectrum in common and this may be referred to as frequency overlap.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (79, "Adjacent channel interference", "", "Out of band power generated in adjacent channels by transmitters operating in their assigned channel. The amount of adjacent channel interference a receiver sees is a function of transmitter and receiver filter characteristics and the number of transmitters operating in the area.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (80, "Adjacent Channel Interference Ratio", "", "The ratio of wanted power to the interference power from the adjacent channel(s).", "", "ACIR", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (81, "Adjacent Channel Leakage Ratio", "", "ACLR is a measure of transmitter performance for WCDMA. It is defined as the ratio of the transmitted power to the power measured after a receiver filter in the adjacent RF channel. This is what was formerly called Adjacent Channel Power Ratio. ACLR is specified in the 3GPP WCDMA standard.", "", "ACLR", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (82, "Adjacent Channel Power Ratio", "", "A measurement of the amount of interference, or power, in the adjacent frequency channel. ACPR is usually defined as the ratio of the average power in the adjacent frequency channel (or offset) to the average power in the transmitted frequency channel. It is a critical measurement for CDMA transmitters and their components. It describes the amount of distortion generated due to nonlinearities in RF components. The ACPR measurement is not part of the cdmaOne standard.", "", "ACPR", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (83, "Adjacent Channel Selectivity", "", "A measurement of a receiver's ability to process a desired signal while rejecting a strong signal in an adjacent frequency channel. ACS is defined as the ratio of the receiver filter attenuation on the assigned channel frequency to the receiver filter attenuation on the adjacent channel frequency.", "", "ACS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (84, "Adjusted funcfion point count ", "Засах үйл ажиллагааны үе шатын тоо", "", "Засваргүй  /тохируулаггүй/ үйл ажиллагааны үе шатын тоог утгын тохируулгын хүчин зүйлээр нэмэгдүүлнэ /IS.IEC 20926:2003/ Компьютеийн систем хангамжийн инжинэрчлэл- lf-PUG", "AFP", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (85, "Adjusted size", "Тохируулгын хэмжээ", "", "Хэмжээ нь техникийн цогц засваруудыг нэмэгдүүлсэн хэмжээний үйлдлээр  тодорхойлогдоно . /ISO-IEC-:/ Компьютерийн систем хангамжийн инжинерчлэл-МК II үил ажиллагааны үе шат. Шинжилгээ: тооцооллын практик заавар . 10", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (86, "Admin site", "Админ сайт", "", "Сайт эсвэл  контентууд вэб сайтын нэг хэсэгийг засварлах эдитлэх нь хязгаарлагдмал байдаг зөвхөн удирддаг админ хүмүүст л нэвтрэх хийх эрх байдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (87, "Admin site", "Админ сайт", "", "Сайт эсвэл  контентууд вэб сайтын нэг хэсэгийг засварлах эдитлэх нь хязгаарлагдмал байдаг зөвхөн удирддаг админ хүмүүст л нэвтрэх хийх эрх байдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (88, "Adoption process", "Сонгох үил ажиллагаа", "", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (89, "Advanced Communications Technology and Services", "", "One of the groups spearheading the development of 3G technologies in Europe. ACTS succeeded RACE and is focusing on wideband multiple access techniques.", "", "ACTS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (90, "Advanced Intelligent Network", "", "A network of equipment, software and protocols used to implement features on the network and support switching and control functions.", "", "AIN", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (91, "Advanced Mobile Phone System", "", "The original standard specification for analog systems. Operates in the frequency range of 800 MHz, with a bandwidth of 30kHz. Used primarily in North America, Latin America, Australia and parts of Russia and Asia.", "", "AMPS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (92, "Advanced Multi Rate Codec", "", "During 1999, ETSI standardized this new speech codec for GSM. The codec adapts its bit-rate allocation between speech and channel coding, thereby optimizing speech quality in various radio channel conditions. For this reason, 3GPP (under which the next stage GSM speech quality will be realized) has selected the AMR codec as an essential speech codec for the next generation system. AMR was jointly developed by Nokia, Ericsson and Siemens.", "", "AMR", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (93, "Advanced Radio Data Information Systems", "", "A radio system developed jointly by Motorola and IBM to provide mobile data services. The system is now operated solely by Motorola.", "", "ARDIS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (94, "Advanced search", "Нарийвчилсан хайлт", "", "Энгийн хайлт,жишээлбэл интернетээс хөдөлгүүрийн хүрээнд хайлт хийх нэмэлт сонголтоор эхний хайлтаас илүү нарийн хийж болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (95, "Aeronautical station", "", "A land station in the aeronautical mobile service.", "", "", "Note – In certain instances, an aeronautical station may be located, for example, on board ship or on a platform at sea.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (96, "Agent", "Агент", "", "Програм хангамж нь хэрэглэчийн заасны дагуу өөрийн  вэб-с хайлт хийнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (97, "Aggregate power flux-density", "", "Sum of the power flux-densities produced at a point in the geostationary-satellite orbit by all the earth stations of a non-geostationary-satellite system.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (98, "Aggregate responsibility ", "Нэгтгэн дүгнэх үүрэг үйл ажиллагаа", "", "Мэдээллийн тусгай шинж чанар , шаардлагын эцсийн боловсруулалтыг харуулах өргөн цар хүрээ бүхий ажиллагаа юм. IEE-1320.2-1998/R2004/ стандартаар баталгаажсан ,", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (99, "Agreement", "Зохицол тохиргоо", "", "Ажлын чиглэл хамаарал дах нэр томъёо, төлөв байдал нь харилцан хүлээн зөвшөөрөгдсөн  байх явдал юм, /ISO/IEC 12207:2008/. /IEEE/ стандарт 12207-2006/", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (100, "Aircraft station", "", "A mobile station in the aeronautical mobile service, other than a survival craft station, located on board an aircraft.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (101, "A-law companding", "", "A type of non-linear (logarithmic) quantizing, companding and encoding technique for speech signals based on the Alaw. This type of compandor is used internationally and has a similar response as the m-law compandor, except it is optimized to provide a more nearly constant signal-toquantizing noise ratio at the cost of some dynamic range.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (102, "Algebraic Code Excited Linear Predictive", "", "An algebraic technique used to populate codebooks for CELP speech coders. This technique results in more efficient codebook search algorithms", "", "ACELP", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (103, "Algebraic language ", "", "", "Программын хэл нь мэдээллүүдийг Y=X+5 гэх мэдчилэн тооны илэрхийлэлтэй адилтгасан утгаар илэрхийлдэг", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (104, "Algebraic notation", "Алгебрийн бичэлт", "", "Математикийн бас логикийн үйл явц ү.а-г тодорхойлон бичэхэд ком-н програм бичнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (105, "Algorithm", "Алгоритм", "", "Алгоритм нь ажлыг гүйцэтгэхэд хийхэд хэрэгтэй алхамуудын дараалал юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (106, "Algorithmic language", "Алгоритмын хэл", "", "Програмын хэлний боловсруулалтыг илэрхийлэх алгоритмууд Адил нэршил: Тооны хэл , дугаарлалтын хэл ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (107, "Alias", "Зохиомол нэршил", "", "Хоёрдогч буюу өөр нэршилүүд /байршил , оршихуй буюу домайн/ IEEE стандарт 1320,2-1998/R2004/", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (108, "Aliasing", "", "A type of signal distortion that occurs when sampling frequency of a signal is less that the Nyquist rate.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (109, "Aligment", "Эгнүүлэх ", "", "Энэ нь текст эсвэл зургийн байршлыг тодорхойлно.Aligment зөвхөн зүүн,баруун,төв эсвэл бүхэлдэнэ хөдөлгөнө.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (110, "Allocated beseline", "Хувирлагдсан суурь шугам", "", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (111, "Allocated configuration identification", "Хуваарилах шаардлага ", "", "Бага түвшний архитектурын элемент буюу зураг төслийн  бүрэлдэхүүн хэсэг дээрх ажиллагаа нь дээд шатны ажиллагааг бүхэлд нь эсвэл хэсэгчлэн ноогдуулах шаардлага юм", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (112, "Allocation", "Хуваарилалт", "", "Систем эвсэл програмийн бүрэлдэхүүн хэсгүүдийн хоорондох шаардлагууд, нөөцүүд эсвэл бусад нэгжүүдийн боловсруулалт Функцийг зааж өгөх шийдвэр эвсэл техник хангамж, программ хангамж эсвэл хүний шийдвэр гаргах үйл явц. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (113, "ALOHA", "", "A packet-based radio access protocol developed by the University of Hawaii where every packet sent is acknowledged. Lack of an acknowledgement is an indication of a collision and results in a retransmission.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (114, "Alpha testing", "Алфа тест", "", "Бүтээгдэхүүнийг худалдаанд гаргах эсвэл ашиглахад бэлэн болсон эсэхийг шалгах тестийн өмнөх үе шатыг алфа тест гэж нэрлэнэ. Ихэвчлэн програмыг хөгжүүлж буй байгууллагын ажилчид туршдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (115, "Alphanumeric", "", "", "Үсэг, тоо, бусад тэмдэгтүүд, цэг таслалын тэмдэгтүүд, боловсруулах хэсэг зэргээс ирсэн мэдээллийг боловсруулах. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (116, "Alternate flow ", "", " The part of a use case that describes its alternative implementations. ", "Нөхцөл байдлаас хамааран гүйцэтгэх аргыг сонгох хэсэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (117, "Alternate key ", "", "A candidate key of an entity other that the primary key.", "Өмнө хэрэглэж байсан түлхүүрийг орлуулах өөр түлхүүр.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (118, "Alternated", "", "In a given set of radio channels, this term refers to an arrangement of channels in which two adjacent channels have orthogonal polarizations.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (119, "altitude of the apogee [perigee]", "", "The altitude of the apogee [perigee] above a specified hypothetical reference surface serving to represent the surface of the Earth.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (120, "American National Standards Institute", "", "A non-profit organization in the US which pursues standardization within the industrial sector. It is also a member of ISO (International Standard Organization). ANSI itself, however, does not establish standards. Instead, it assists in reviewing proposals put forth by various standardizing bodies in the US and accordingly assigns a category code and number after approval.", "", "ANSI", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (121, "American National Standards Institute.", "Америкын үндэсний стандартын хүрээлэн", "", "Америкын үндэсний стандартын хүрээлэн. АНУ дахь технологийн стандартын анхдагч байгуулга юм.", "ANSI ", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (122, "Amplitude Modulation", "", "CW modulation using amplitude variation in proportion to the amplitude of the modulating signal; usually taken as DSB-LC for commercial broadcast transmissions and DSB-SC for multiplexed systems.", "", "AM", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (123, "An upgrade of the SIM card.", "", "This upgrade enables use with IMT-2000.", "", "USIM", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (124, "Analog ", "", "Pertaining to continuously variable physical quantities or to data presented in a continuous from, as well as to processes and functional units use the data. ", "Үргэллжилсэн хувьсах физик хэмжигдэхүүн , үргэлжилсэн мэдээллийг боловсруулах.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (125, "Analog computer ", "", " A computer whose operations are analogous to the behavior of another system and that accepts, processes, and produces analog data.", "Удирдлага болон бусад системийн шинж чанар , зөвшөөрөл , боловсруулалт , боловсруулсан мэдээлэл нь аналог байдаг компьютер.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (126, "Analog system", "", "A transmission method or way of sending voice, video and data-using signals (such as electricity or sound waves) that are continuously variable rather than discreet units as in digital transmissions. The first networks for mobile phones, built in the 1980s, were analog. Analog systems include AMPS, NMT and ETACS.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (127, "Analogous estimating ", "", "[Technique] an estimating technique that uses the values of parameters, such as scope, cost , budget, and duration or measures of scale such as size , weight , and complexity from a previous , similar activity as  the basis for estimating the parameters or measure for a future activity. A guide to the project management body of knowledge(PMBOK GUIDE ) – Fourth Edition", " Далайц , үнэлгээ , нөөц , хугацаа , хэмжээ , жин г.м өмнөх хэмжигдэхүүнүүдийн  утгыг  ашиглан цаашид хэрэглэгдэх техникийн боловсруулалт хийх.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (128, "Analog-to-Digital Converter", "", "Converter that uniquely represents all analog input values within a specified total input range by a limited number of digital output codes.", "", "ADC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (129, "Analysis ", "", " The process of studying a system by partitioning the system into parts (functions, components, or objects) and determining how the parts relate to each other.  Investigation and collection phase of user documentation development that aims to specify types of users and their information needs.", "Системийг үүрэг , бүрдэл хэсгүүдээр нь хэсэг болгон хувааж , хоорондын холбоо хамаарлыг тодорхойлох судалгааны системийн явц. Хэрэглэгч болон тэдгээрт шаардлагатай мэдээллийг төрөлжүүлэхэд чиглэсэн хэрэглэгчийн судалгаа , цуглуулгын үе шат.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (130, "Analysis model ", "", "  Algorithm or calculation combining one or more base and/or derived measures with associated decision criteria.", "  Нэг болон хэд хэдэн суурь баазууд , шийдвэр хэмжигдэхүүнүүдийг нэгтгэн тооцоолох.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (131, "Analyst ", "", "", " Техникийн байгууллагын гишүүн (системийн инженер , бизнес шинжээч , програм хөгжүүлэгч г.м)  алгоритмыг илэрхийлэх , тулгарсан асуудлыг шийдэх , хянах , боловсруулах , хөгжүүлэх зэрэгт мэргэшсэн ажилтан.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (132, "Ancestor (of a class)", "", " A generic ancestor of the class or a parent of  the class or an ancestor of a parent of the class. ", "Бүх class-уудын эх анги. Бүх удамшсан class-уудын эх , анхдагч ерөнхий class", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (133, "Ancestral box ", "", " A box related to a specific diagram by a hierarchically consecutive sequence of one or more parent/child relationships.", "Хоорондоо удамшсан өвөрмөц шинжүүдээрээ  эцэг , хүү холбоосоор холбогдсон хайрцгууд.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (134, "Ancestral diagram", "", "A diagram that contains an ancestral box.", "ancestral box-г агуулсан диаграм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (135, "Angle diversity", "", "A technique using multiple antenna beams to receive multipath signals arriving at different angles.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (136, "Annotate", "", " A command used for listing the latest version of each program’s source code line, along with the date, the file version it was introduced, and the person who committed it.", "Програм бүрийн  эх код , хувилбарын танилцуулагдсан он , бичсэн хүн г.м жагсаалтуудыг гаргахад хэрэглэгддэг команд.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (137, "Annotation ", "", " Further documentation accompanying a requirement such as background information and/or descriptive material.", "Үндсэн мэдээлэл , тайлбар материал зэргийг агуулсан дагалдах мэдээлэл.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (138, "Announcement ", "", "An interaction – the invocation – initiated by a  client object resulting in the conveyance of information from that client object to a server object, requesting a function to be performed by that sever object.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (139, "Anomaly ", "", "Condition that deviates from expectations, based on requirements specifications design documents, user documents, or standards, or from someone’s perceptions or experiences. Anything observed in the documentation or operation of software or system that deviates from expectations based on previously verified software products, reference documents, or other sources of indicative behavior.", " Хүлээлтээс зүрүүтэй нөхцөл байдал шаардлага тодорхойлолтын загварын бичигбаримт эсвэл хэн нэгний үзэл бодол болон туршлага дээр тулгуурладаг.  Хүлээлтээс зүрүүтэй юугаар ч ажиглагдсан бичиг баримт эсвэл программ хангамж системийн үйл ажилгаань шалгах программ хангамжын бүтээгдхүүн лавлах бичиг баримт эсвэл зайлшгүй шалгах шаардлагатай бусад эх үүсвэрт тулгуурласан байдаг", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (140, "Antenna", "", "The part of a radio transmission system designed to radiate or receive electromagnetic waves.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (141, "Antenna beamwidth", "", "More properly referred to as the half-power beamwidth, this is the angle of an antenna pattern or beam over which the relative power is at or above 50% of the peak power.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (142, "Antenna birectivity diagram", "Антенн чиглүүлэлтын диаграмм", "", "Муруй шугамыг илэрхийлэхэд туйлын дотор кординатын тэгш өнцөгт, тоо хэмжээ нь тэнцүү өсгөлт антенн чиглүүлэгийг өөр өөр хавтгай гадаргуун конусд тодорхойлно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (143, "Antenna directivity", "", "This is the relative gain of the main beam of an antenna pattern to a reference antenna, usually an isotropic or standard dipole.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (144, "Antenna directivity diagram", "", "A curve representing, in polar or cartesian coordinates, a quantity proportional to the gain of antenna in the various directions in a particular plane or cone.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (145, "Antenna gain", "", "The ratio, usually expressed in decibels, of the power required at the input of a loss free reference antenna to the power supplied to the input of a given antenna to produce, in a given direction, the same field strength of the same power flux-density at the same distance. When not specified otherwise, the gain refers to the direction of maximum radiation. The gain may be considered for a specified polarization. Depending on the choice of the reference antenna, a distinction is made between: (a) absolute or isotropic gain (Gi), when the reference antenna is an isotropic antenna isolated in space; (b) gain relative to a half-wave dipole (Gd), when the reference antenna is a half-wave dipole isolated in space whose equatorial plane contains the given direction; (c) gain relative to a short vertical antenna (Gv), when the reference antenna is a linear conductor, much shorter than one quarter of the wavelength, normal to the surface of a perfectly conducting plane which contains the given direction.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (146, "A-O context diagram", "", "", "IDEFO загвар нь зөвхөн контент диаграммыг шарддаг: А-О диаграмм нь нэг хайрцагийг байгуулна. Тэрхүү контент диаграмм нь дээд зэргийн загварлагдсан оролт, дохио, гаралт мөн  механизмын хайрцагтай холбогдсон бүтэн загварын нэр юм. Загварын товчилсон нэр нь зорилго үзэл бодлыг баримталсан байна.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (147, "Application Layer Structure", " Хэрэглээний түвшний бүтэц", "", "", "ALS ", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (148, "Application-Specific Integrated Circuit", "", "An integrated circuit designed to perform a specific set of functions, usually within a specific device.", "", "ASIC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (149, "Assigned frequency", "", "The centre of the assigned frequency band.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (150, "Assigned frequency band", "", "The frequency band within which the emission of a station is authorized; the width of the band equals the necessary bandwidth plus twice the absolute value of the frequency tolerance. Where space stations are concerned, the assigned frequency band includes twice the maximum Doppler shift that may occur in relation to any point of the Earth’s surface", "", "", "Note 1 – For certain services, the term “Assigned channel” is equivalent. Note 2 – For the definition of “Frequency tolerance” see § D.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (151, "Association of Radio Industries and Businesses (Japan)", "", "An incorporated body designated by the Ministry of Posts and Communication of the Japanese government to pursue effective radio utilization in the radio communication and broadcast sector. With regard to standardization, ARIB is currently primarily engaged in standardizing procedures for IMT-2000 (next generation mobile communication system) and digital TV broadcasting.", "", "ARIB", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (152, "Asymmetrical Digital Line Subscriber", "", "A method to increase transmission speed in a copper cable. ADSL facilitates the division of capacity into a channel with higher speed to the subscriber, typically for video transmission, and a channel with significantly lower speed in the other direction.", "", "ADSL", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (153, "asynchronous mode", "", "A way to send transmissions by starting and stopping transmissions with a code rather than sending transmissions at specific time intervals as in synchronous mode. Asynchronous communication devices do not have to be synchronized with a clocking signal, which is required with synchronous transmission. Also frequently referred to as ATM or Asynchronous Transfer Mode. Can also mean that there are different capacities for data transfer in each direction, for example the old 90/200 baud modems and the new ADSL. See also synchronous mode.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (154, "Asynchronous Transfer Mode", "", "A technology for broadband transmission of high-capacity telecommunications signals. In addition to high-capacity signal transmission, ATM provides considerable flexibility, since the individual subscriber is able to adapt the capacity of a switched connection to current requirements.", "", "ATM", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (155, "Atomicity Consistency Isolation Durability", "Ажиллах хугацаанд холболт найдвартай байх", "", "-          Өгөгдлийн баазтай харилцах холболт найдвартай байх", "ACID", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (156, "Attenuation", "", "A decrease in signal magnitude between two points. These points may be along a radio path, transmission line or other device.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (157, "Attitude-stabilized satellite", "", "A satellite with at least one axis maintained in a specified direction, e.g. toward the centre of the Earth, the Sun or a specified point in space.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (158, "Authentication Center", "", "A device, usually located in the HLR of a GSM system that manages the authentication or encryption information associated with individual subscribers.", "", "AUC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (159, "Autocorrelation", "", "The complex inner product of a sequence with a shifted version of its self. It is a measure of how closely a signal matches a delayed version of itself shifted n units in time.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (160, "Automatic Gain Control", "", "System which holds the gain and, accordingly, the output of a receiver substantially constant in spite of input- signal amplitude fluctuations.", "", "AGC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (161, "Automatic Power Control", "", "A technique of measuring the performance of a radio channel and adjusting the power of the transmitter to a level appropriate for link characteristics.", "", "APC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (162, "Automatic Retransmission Request", "", "A signal used in digital communications systems used to signal the transmitting device to retransmit a block of data.", "", "ARQ", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (163, "Average power", "", "An indication of the peak power averaged over time. Usually applied to pulsed systems where the carrier power is switched on and off.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (164, "Back", "Буцах", "", "Browser-н урагш бас хойшлуулах товч нь дараагийн болон өмнө үсзсэн хуудсыг харуулна.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (165, "Backbone", "", "", "Backbone нь өндөр хурдны харилцааны холбоо хэрэглэхэд үндсэн холбоос хооронд том сүлжээнд жижиг дэд сүлжээ бий болно.Холболт нь суваган оптик кабель юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (166, "Backbone", "Цөм сүлжээ", "", "Backbone нь өндөр хурдны харилцааны холбоо хэрэглэхэд үндсэн холбоос хооронд том сүлжээнд жижиг дэд сүлжээ бий болно.Холболт нь суваган оптик кабель юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (167, "Background job", "", "", " Хэрэглэгч өөр ажил хийж байх үед компьютерт өөр үйлдэл хийгдэж байхыг хэлнэ.Хэвлэх үйл явц нь backround job-ийн энгийн жишээ юм .", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (168, "Background job", "", "", " Хэрэглэгч өөр ажил хийж байх үед компьютерт өөр үйлдэл хийгдэж байхыг хэлнэ.Хэвлэх үйл явц нь backround job-ийн энгийн жишээ юм .", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (169, "Backing store", "Арын нөөц", "", "Програмын эх хувилбарын мэдээлэл,өгөгдлийг хуулахад програм эвдэрч эсвэл алга болдог.Хэрэв систем бүтэлгүйтвэл алдагдвал дахин сэргээхэд нягт нарийн зөв өгөгдөл хийх хэрэгтэй.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (170, "Backing store", "Арын нөөц", "", "Энэ нь томоохон их хэмжээтэй мэдээлэл гаднаас орж ирэхэд шууд нөөцөд нэвтэрдэг.Ихэнх арын нөөц хадгаламж нь соронзон орон эсвэл оптик багтаамжинд хэрэглэдэг. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (171, "Backround", "Фон", "", "Вэб дизайнд, хуудасны фон арын хэв маяг эсвэл арын фон өнгө.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (172, "Backup", "Нөөц", "", "Програмын эх хувилбарын мэдээлэл,өгөгдлийг хуулахад програм эвдэрч эсвэл алга болдог.Хэрэв систем бүтэлгүйтвэл алдагдвал дахин сэргээхэд нягт нарийн зөв өгөгдөл хийх хэрэгтэй.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (173, "Backword compatible", "Хуучинтай тохирч ажилна", "", "Компьютерийнн систем эсвэл програм хангамж загварчилагдахдаа хуучин систем эсвэл програм хангамжтай хамтран,тэхээр энэ нь техник хангамж шинийг авах шаардлаггүй гэсэн үл мөн өгөгдөл нь хуучин ком-н системтэй үргэлжилж ашиглагдана.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (174, "Band", "", "In wireless communication, band refers to a frequency or contiguous range of frequencies. Currently, wireless communication service providers use the 800 MHz, 900 MHz and1900 MHz bands for transmission in the United States", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (175, "Band width", "Зурвасын өргөн ", "", "Зурвасын өргөн  нь холбооны сувгийн багтаамжийн хэмжигдэхүүн юм. Энэ нь сувгийн давтамжтай далайцыг гараар хийж болно. BW нь магадгүй давтамж өгдөг, иймээс 3 килогерц эсвэл дамжуулах чанарыг 1сек-н хэсэг бүрээр, тийм учир 63 кило дамжуулагчын сувгийн багтаамж. Дамжуулах чадварын сүлжээний хурдны талаар олон удаа хийдэг.Ж нь:суваг тодорхойлоход байж болох нь сүлжээний хурд энэ нь 56кг эсвэл 64к,өөрөөр хэлбэл 56 kbps эсвэл 64 kbps.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (176, "Band-pass filter", "", "A radio wave filter having a specific range of frequencies it is designed to pass, while rejecting frequencies outside the pass-band.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (177, "Bandwidth", "", "The information-carrying capacity of a communications channel. Usually expressed in Hertz (cycles per second) for analog circuits and in bits per second (bps) for digital circuits.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (178, "Bandwidth Time Product", "", "The result obtained by multiplying the system bandwidth by the signal duration. As a general rule, the system bandwidth must be approximately equal to the reciprocal of the signal duration to produce an output signal of the same general form as the input, i.e., BT»1.", "", "BT", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (179, "Banner ad", "", "", "Вэб хуудсан дээр дарж дөрвөлжин хэлбэртэй зарлал гарж ирдэг", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (180, "Bar  Chart ", "Шугаман хүснэгт", "", "Мэдээллийг хүснэгт хэлбэрээр харуулах график арга мэдээллийн бүх хэсэг нь давхарга хэлбэртэй (төлөвтэй) урт нь мэдээллийн илэрхийллийн хэмжээтэй пропорциональ (тэнцүү)", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (181, "Bar code", "Шугаман код", "", "Бүтээгдэхүүн дээр хэвлэгдсэн дундаа зайтай өөр өөр давтамжтай босоо зураасан байгуулалт. Зай болон зураасан давтамж нь тоон коп ба компьютер бүтээгдэхүүнийг тодорхойлох боломжийг олгодог. Скайннер ( дүрс буулгагч ) нь кодыг уншдаг. Зураасан код нь ихэнхдээ. Нөөцийг хянадаг", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (182, "Base station", "", "A land station in the land mobile service.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (183, "Base station", "", "The central radio transmitter/receiver that maintains communications with a mobile radio telephone within a given range.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (184, "Base Station", "", "The equipment on the network side of a wireless communications link. The base station contains the tower, antennas and radio equipment needed to allow wireless communications devices to connect with the network.", "", "BS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (185, "Base Station Controller", "", "A device and software associated with a base station that permits it to register mobile phones in the cell, assign control and traffic channels, perform handoff and process call setup and termination", "", "BSC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (186, "Base Station Identity Code", "", "A unique code contained in messages on the broadcast channels of a cell or base station that uniquely identifies the base station.", "", "BSIC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (187, "Base Station Subsystem", "", "That portion of a GSM network that includes the base station, base station controller and transcoders (if used).", "", "BSS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (188, "Base Transceiver Station", "", "Although specifications differ for each system, the BTS effects radio communication with mobile stations (MS) via its respective radio access system and transmits/receives signals to/from connected radio network controllers (RNC) located along transmission routes.", "", "BTS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (189, "Baseband signal", "", "A signal with frequency content centered around DC. Typically the modulating signal for an RF carrier.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (190, "Baseline", "Эхлэлийн шугам", "", "Том үсэг байрлах харагдахгүй шугам", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (191, "Basic MUF", "", "The highest frequency at which a radio wave can propagate between given terminals below the ionosphere on a specified occasion, by ionospheric refraction alone.", "", "", "Note – The acronym MUF stands for “Maximum Usable Frequency”.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (192, "Basic Trading Area", "", "A geographic area over which a PCS operator is licensed to provide service. BTAs are a group of counties in metropolitan areas having common financial, commercial and economic ties and were first used to license PCS service in the middle '90s. BTAs are about the size of a cellular MSA and cross state lines in some instances. BTAs are used by the Rand-McNally corporation to summarize economic data. BTAs are grouped into larger areas called MTAs", "", "BTA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (193, "Basic transmission loss", "", "The transmission loss that would occur if the antennas were replaced by isotropic antennas with the same polarization as the real antennas, the propagation path being retained, but the effects of obstacles close to the antennas being disregarded.", "", "", "Note 1 – The basic transmission loss is equal to the ratio of the equivalent isotropically radiated power of the transmitter system and the power, available from an isotropic receiving antenna. Note 2 – The effect of the local ground close to the antenna is included in computing the antenna gain, but not in the basic transmission loss.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (194, "Basig", "үндсэн", "", "Бүх зориулалтанд ашиглах анхан шатны тэмдэгтэн зааварчилгааны код. Өндөр түвшний програмын хэл. Dartmouth коллежд анх үүссэн.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (195, "Batch file", "Батч файл", "", "Өгөгдлийн файл гэдэг нь ганц файл бөгөөд нэг эсвэл олон үйлдлийн системийн command-г багтаах. Өгөгдлийн файл нь ажилангуут бүх тушаал ( үйлдэл) нь гүйцэтгэгддэг  Гэхдээ файлийн өргөтгөл нь .bat-аар илэрхийлэгддэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (196, "Batch Mode", "Багцлах горим", "", "Бүх бүлэг нь нийлээд нэг үйл ажиллагаа болно", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (197, "Batch processing", "Багцлах боловсруулалт", "", "Ганц ашигтай боловсруулалт хийхээс нь өмнө бүх мэдээллийн оролтууд нь нэгдэхийг багцлах боловсруулалт гэнэ. Энэ арга нь мөн компьютер хэрэглэгч нь салангад ажилуудыг тушаасан үед нийлээд багц болон боловсруулахыг хэлнэ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (198, "Batch upload", "Багцлан илгээх", "", "Олон файлийг нэг зэрэг явуулах процесс нэг нэгээр нь явуулсанаас дээр. Энгийн нь PTP.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (199, "Battery", "Цэнэг хураагуур", "", "Зөөврийн компьютер гэх мэт хэрэгслүүдэд ашигладаг цахилгаан тэжээлд залгаагүй ч гэсэн ажиллах боломжтой бие даасан тэжээлийн үүсгүүр.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (200, "Baud", "Бауд", "", "цуваа өгөгдлийг дамжуулах хурдыг хэмжихэд ашигладаг нэгж. Утасны шугамаар өгөгдөл дамжуулах хурд. Ойролцоогоор 1 секундэд 1 bit-ийн хурдта тэнцүү.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (201, "Baud rate", "Бауд үзүүлэлт", "", "Цуваа өгөгдөл дамжуулах хурдыг хэмжих нэгж.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (202, "Benchmark", "Харьцуулсан үзүүлэлт", "", "Компьютерийн үзүүлэлтийг хэмжих зорилготой компьютерийн үүрэг бүхий загвар цогц юм. Энэ үүрэг ( шинжилгээ) нь янз бүрийн програм хангамж эсвэл техник хангамжийг үзүүлэлтийн хоорогд харьцуулах. Жишээ нь : Сүлжээгээр 1mb файлийг хэр хурдан хуулах вэ, нэг минутанд хэдэн хуудас хэвлэж чадах вэ, 1000 мэдээллийн санг дискдээ хуулхад хэр их хугацаа шаардагдах вэ. Харьцуулсан үзүүлэлт нь мөн компьютерийн системийг шалгахад ашигладаг систем зогсохоос өмнөх амжилтай гүйцэтгэгдсэн үзүүлэлтийг харуулдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (203, "Bespoke system", "Захиалгат систем", "", "Энэ систем нь тодорхой хэрэглэгчдэд зориулагдсан Энгийн биш техник хангамжийг багтаасан ба тусгайлан програмчлагдсан байдаг Энэ нь маш үнэтэй боловч маш том байгуулагуудад зориулагдсан цорын ганц арга юм", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (204, "Beta test", "Бета тест", "", "Бета шалгалт нь онцгой эрхтэй хэрэглэгчдэд өөрсдийн саналаа солилцох хувилбар юм.  Альфа шалгалт хийгдээд судалсаны дараа тодорхой хэмжээний өөрчлөлт оруулсаны дараах үр дүн нь Бета шалгалт юм.  Бета шалгалтын хувилбар нь ихэвчлэн бүтээгдэхүүний сүүлийн үр дүн байдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (205, "Beta Version", "Бета хувилбар", "", "Энэ бол програм хангамжийн дуусаагүй хувилбар юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (206, "Binary", "Хоёртын тоолол", "", "2тын тоон тооллын систем 1 эсвэл 0-ээр илэрхийлэгддэг", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (207, "Binary operator", "Хоёртын тоололын оператор", "", "This is an operator (such as + or -) which operates on two items of data.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (208, "Binary Phase Shift Keying", "", "A type of phase modulation using 2 distinct carrier phases to signal ones and zeros.", "", "BPSK", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (209, "Binary representation", "Хоёртын тоололын илэрхийлэл", "", "Тоололын систем нь 0 ба 1 гэсэн 2 тоон дээр суурилдаг. Энэ нь 2 суурьт систем ба хүмүүсийн мэдэх 10 суурьт ( 0-оос 9) системтэй харьцуулагддаг. Компьютердэх бүх мэдээлэл нь хамгийн бага түвшний хоёртын томъёололоор илэрхийлэгддэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (210, "Binary Search", "Хоёртын тоололын хайлт", "", "Мэдээллийг хайх энэ арга нь ямар нэг дараалалд багтсан алгоритмийг хайх хугацааг бууруулахын тулд хагас өгөгдлийн сонгон авч хайх ба мэдээллийг хаана байрлаж байгааг олох хүртэл давтана", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (211, "Binary tree", "Хоёртын тоололын мод", "", "Хоёртын тоололын мод гэдэг нь зангилагаар хийгдсэн мэдээллийн бүтэц юм. Бүх зангилаа нь эцэг зангилаатай ба хамгийн ихтэй 2 удамшсан зангилаатай байна", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (212, "BIOS", "", "", "Энгийн оролт гаралтын системийн товчилсон хэлбэр юм. Компьютерийн оролт гаралтыг зохицуулах үйлдлийн системийн хэсэг. Энэ техник хангамжийн тодорхой шинжийг ашигладаг үйлдлийн систем. Нэр нь үйлдлийн системийн нэг хэсэгтэй холбогдолтой Янз бүрийн техник хангамжийн загвар эсвэл боловсруулалтыг хэрэглэдэг олон техник хангамжийн үйлдлийн системүүд солигддог. Хэрвээ BIOS нь солигдвол энэ үйлдлийн системийн өөр хэсгүүд нь солигдох шаардлаггүй.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (213, "Bit", "Бит", "", "Бит бол компьютерийн өгөгдлийг тодорхойлдог хэмжигдэхүүний  хамгийн бага нэгж юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (214, "Bit Energy-to-Noise Density", "", "The ratio of bit energy to noise density. This value is used to specify the lower limit of operation in most digital communications systems and is also used to measure radio channel performance.", "", "Eb /N0", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (215, "Bit Error Rate", "", "A ratio of the number of errors to data bits received on a digital circuit. BER is usually expressed in exponential form.", "", "BER", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (216, "Bit Error Rate Tester", "", "A device used to measure the bit error rate performance of a data circuit.", "", "BERT", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (217, "Bit interval", "", "The amount of time, usually in milliseconds or microseconds, a binary one is in the 'on' position.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (218, "Bitmap", "Бит зураг", "", "Мэдээллийг бүтэцийг харуулдаг битийн дүрслэл. Зурсан эсвэл будсан багц нь график дэлгэц дээр битийн буулгалтанд нөлөөлдөг эхэвчлэн дэлгэцийн буулгалт гэж нэрлэдэг бүх бүт нь дэлгэцэн дээр байрлах тусгай пикселийг харуулдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (219, "Bitmapped graphics", "Бит зураг нь график", "", "A method of creating images where a picture is held as a bit map that is, the state of each individual pixel is stored. Text in such a picture would also be stored in the same bit map. Such drawings use a lot of storage space, because even.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (220, "Bits per second", "", "", "Өгөгдөл дамжуулах хурдыг хэмжих нэгж.", "BPS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (221, "Bits per Second", "", "The units usually used to express data transmission speed; the number of pieces of information transmitted per second.", "", "BPS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (222, "Bitwise operator", "Ганц битийн ажиллах түвшэний оператор", "", "Янз бүрийн битийн түвшинд мэдээллийг чиглүүлэх оператор.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (223, "Blind Carbon Copy", "", "", "Майл хаягт хэн нэгэнд bcc mail явуулна гэдэн нь үндсэн хүлээн авагч мэдэхгүйгээр хуулбарийг явуулахын хэлнэ ( Үүнийг сайн нягтал!!!)", "BCC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (224, "Block code", "", "A family of codes having a one-to-one mapping between ksymbol source words and n-symbol code words.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (225, "Block diagram", "Блок схем", "", "Хайрцагаар хийсэн диаграмм ба янз бүрийн техник болон програм хангамжийн эд анги ба холболтыг харуулсан шугаман хаягын диаграмм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (226, "Block Error Rate", "", "A ratio of the number of erroneous blocks to the total number of blocks received on a digital circuit. Block error rate (BLER) is used for W-CDMA performance requirements tests (demodulation tests in multipath conditions, etc). BLER is measured after channel deinterleaving and decoding by evaluating the Cyclic Redundancy Check (CRC) on each transport block.", "", "BLER", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (227, "Blocking probability", "", "The statistical probability that a telephone connection cannot be established due to insufficient transmission resources in the network. Usually expressed as a percentage or decimal equivalent of calls blocked by network congestion during the busy hour.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (228, "Blog", "Блог", "", "Вэб протоколийн нэг. Энэ нь хувийн вэб дээрх тэмдэглэл бүх хэрэглэгчидэд зориулагдсан маш хурдан бас энгийн хувийн мэдээлэлээ нийтлэх боломжтой вэб хуудас юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (229, "Bluetooth", "", "", "Bluetooth бол техник загвар ба утасгүй төхөөрөмжүүдийг холбодог. Жишээ нь: Утас, хувийн болон суурин компьютер ба төхөөрөмжүүдийг хооронд нь холбодог байгууламж юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (230, "Bluetooth", "", "A short range radio technology developed by Ericsson and other companies that makes it possible to transmit signals over short distances between telephones, computers and other devices without having to interconnect them with wires.", "", "", "");
