INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (252, "Broadcast Channels", "", "A group of downlink point to multipoint logical channels used by mobiles to synchronize to and receive information necessary to access a cell in GSM, cdma2000, and WCDMA systems", "", "BCH", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (253, "Broadcast Control Channel", "", "A downlink point to multipoint logical channel in GSM and cdma2000 systems used to send identification and organization information about common control channels and cell services", "", "BCCH", "");
