INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (263, "Cable", "Кабель", "", "Төхөөрөмжүүдийн хэд хэдэн хэсгүүдийг холбох бүлэг утсыг кабель гэнэ. Зэрэгцээ кабель нь ерөнхийдөө  принтерийг компьютерт холбодог. Цуваа кабель нь модемийг компьютерт холбодог.", "", "");
