INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (265, "Cache ", "Кэш Ram", "", "Үндсэн санах ойн хэсэг ба CPU ба нөөцийн хооронд байдаг хэсэг. Энэ нь өгөгдлийн маш өндөр хурд үзүүлдэг учир программын хэсгүүд ба түүнд хамаарах өгөгдлүү энэ өндөр хурд авахын тулд кэш дээр хадгалагддаг. Кэш санах ой ашигласнаар боловсруулах хугацааг маш их хэмжээгээр багасгаж чадна.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (266, "Cancel ", "Цуцлах", "", "Ажиллагааг бүрэн дуусахаас өмнө зогсоох, жишээ нь хэвлэх ажиллагааг дуусгах", "", "");
