INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (268, "Capslock", "Том үсэгний товчлуур", "", "Энэ нь бүх үсгийг томоор бичихийн тулд дардаг гарны товчлуур", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (269, "Capture area", "", "Area associated with a receiving station for a given service and a specified frequency within which, under specified technical conditions, radiocommunications may be established with one or several transmitting stations.", "", "", "Note – The notes concerning the coverage area (of a transmitting station) are valuable also, mutatis mutandis, for the capture area.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (270, "Capturing", "Өгөгдөл оруулалт", "", "Компьютерт оруулах өгөгдлийн цуглуулга. Дэлгүүрт бар код уншуулах, шатахууны тоолуурыг гараар бичих зэрэгт баар код автоматаар хийгдэж болно.", "", "");
