INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (347, "Column", "Багана, мөр, цуваа", "", "Мэдээлэл нь  босоо байрлалтай  жишээ нь хүснэгттэй ажиллах програм нь өгөгдлийг багана буюу хоёр ба түүнээс дээш багана болгон  боловсруулсан өгөгдлийн хуудас бэлтгэх үйл явц.", "", "");
