INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (354, "Comment", "Тайлбарлах", "", "Компьютерийн програмдхүмүүсийн уншигчдад тодорхой хугацаанд оруулсан мэдээ, мэдээллүүдийн хяналт ба тоо баримттай байрлуулсан мэдээний тайлбар,тодруулага өгөх зорилгоор бичигдсэн хүмүүс хоорондын  санал хүсэлтийн илэрхийлэл", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (355, "Comment a program", "Нэмэлт мөр бичиж тайлбарлах", "", "Хөтөлбөрийн хүрээнд ямар нэгэн програмыг гүйцэтгэхгүйгээр тухайн программын тайлбрыг өөр өөрөөр тайлбарлах. Програм: програм хангамжийн сери код нь компьютерийн үйл ажиллагаа болон бусад машины үйл ажиллагааг хянаж удирддаг. Жишээлбэл Оксфордын их сургуулийн хэвлэлийн зөвшөөрөлөөр  Е (1989) Чөлөөт зөвшөөрөл Simpson, J & Weiner, хянан тохиолдуулсан 'Оксфордын англи хэлний толь бичиг' Инк.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (356, "Comment box", "Санал бичих хайрцаг", "", "Хавтасд тэмдэглэл нь нэмэгдэн ордог бөгөөд ерөнхийдөө бичвэрийг цахим төхөөрөмжөөр боловсруулсан хүснэгтийн хуудаснаас хайснаар санал хүсэлт ил гарч ирдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (357, "Commit", "Хийх", "", "Хувийн хөгжүүлэгчдийн хийсэн эх кодуудаас харж, хэрэглэж болохыг нь авч  нэгтгэх, өөрчлөлт хийж удирдах системийн сан, хувилбар гаргах", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (358, "Commit privileges", "Давуу эрх эдлэх ", "", "өөрчлөлт хийх хүний эрх мэдэл  Тэмдэглэл: Заримдаа давуу эрхүүд нь тодорхой салбар эсвэл бүтээгдэхүүний тодорхой нэг хэсэгтэй холбоотой байдаг. (жишээлбэл, урлагийн бүтээл эсвэл бичиг баримт)", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (359, "Commit window", "Цаг үеийг тохируулах ", "", "Тодорхой салбарт зөвшөөрдөг үйлдлүүдийн үед Тэмвэглэл: Зарим хөгжүүлэлтийн орчинд үйлчилгээний салбарт зориулсан тохиромжтой цаг үе нь жилд цөөхөн удаа тохионо. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (360, "Commitment", "Амлалт ", "", "Мэдээллийн технологи – Нээлттэй хуваарилалтын үйл явц- Жишиг загвар – Аж ахуй нэгжийн хэл  гэрээг гүйцэтгэх эсвэл дүрэм журмыг дагаж мөрдөн нэг болон түүнээс дээш оролцогчдын үүрэг хариуцлагын үр дүн  Тэмдэглэл: Аж ахуй нэгж амлалтын үйл ажиллагааг хэрэгжүүлэхдээ намынхаа нэрийн өмнөөс ажиллаж байгаа агентууд эсвэл намууд байж болно. Агентын амлалтын үйл ажиллагаанд оролцогчийн үүрэгтэй байдаг. ", "", "");
