INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (363, "Common Air Interface ", "", "A set of open standards describing the physical and logical characteristics of a link between a base station and mobile station. These standards are used by infrastructure and handset manufactures to design and build equipment that is capable of interoperating with each others systems", "", "CAI", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (364, "Common ancestor constraint", "Нийтлэг загварын шаардлага ", "", "Шаардлага нь тус бүрийн замаар дамжуулан ижил загварын жишээтэй холбогддог удамшлын тохиолдол болон тус бүрийн замаар дамжуулан өөр өөр загварын жишээтэй холбогддог удамшлын тохиолдлын аль алиныг тодорхойлдог , ижил загварын ангилалд хоёр болон түүнээс дээш холбоо харилцаа бүхий замуудад хамааралтай байдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (365, "Common cause", "Нийтлэг шалтгаан ", "", "Өөрчлөлтийн эх сурвалж нь системд болон урьдчилан таамаглалд өөрчлөгдөггүй. Хяналтын зураг дээр үүнийг санамсаргүй үйл явцын өөрчлөлтөөр харуулав. (Үйл явцаас гарч буй өөрчлөлтийг албан эсвэл албан бус гэж үздэг. Мэдлэгийн Төслийн Менежментийн гарын авлага- 4-р хэвлэл) санамсаргүй шалтгаан. Үйл явцын өөрчлөлтийн эх сурвалж гэж байдаггүй учир нь тусгай шалтгаант үйл явцын бүрэлдэхүүн хэсгүүдийн хоорондын харилцан ажиллагаанаас хамаардаг. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (366, "Common environment coupling", "Нийтийн орчны холбоо хамаарал ", "", "Нийтийн дата талбарт ханддаг хоёр програм хангамжийн модулиудын холбоо хамаарлын төрөл. Ижил утга: нийтлэг холбоо хамаарал, нийтийн эзэмшил-хүрээлэн буй орчны холбоо хамаарал  Агуулгын холбоо хамаарал, удирдах багж хэрэгслийн холбоо хамаарал, өгөгдлийн холбоо хамаарал, холимог холбоо хамаарал, эмгэгийн холбоо хамаарал ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (367, "Common gateway interface ", "Нийтлэг гарцын интерфейс", "", "Энэ нь сервер дээр байрладаг бөгөөд вэб хуудаснаас оролтыг хүлээн авдаг мөн  түүнийг боловсруулдаг жижиг программ юм.", "", "");
