INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (369, "Common storage", "Нийт багтаамж ", "", "Програм хангамжийн системд хоёр болон түүнээс дээш модулиуд хандалт хийж болдог үндсэн багтаамжийн хэмжээ. Ижил утга: Нэгдсэн талбай, нийтлэг хар  Глобал дата", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (370, "Communication interface", "Холбооны интерфейс ", "", "Завсрын обьект болон протокол обьектын аль алинтай холбогдож болдог протокол обьектын интерфейс. Мэдээллийн Технологи – Нээлттэй хуваарилттай үйл ажиллагаа – Жишиг загвар Архитектур ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (371, "Communication management plan", "Холбооны менежментийн төлөвлөгөө ", "", "[Гаралт Оролт] ямар форматтай мэдээллийг хэрхэн холбох, холболт бүрийг хэзээ, хаана хийж болох, холболтын төрлийн хэн хариуцах гэх мэт асуудлуудыг шийдэж холболт хэрэгтэй болохыг тодорхойлдог бичиг баримт. Холбооны менежментийн төлөвлөгөө нь төслийн менежмент төлөвлөгөөний дэд төлөвлөгөөнүүдээс бүрддэг. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (372, "Communicationalcohesion", "Холболтын нэгдмэл байдал ", "", "Даалгаврыг ижил төрлийн оролтын өгөгдөл ашигладаг эсвэл ижил төрлийн гаралтын өгөгдөл боловсруулдаг програм хангамжийн модуль ашиглан гүйцэтгэх нь нэгдмэл байдлын нэг төрөл  Давхардсан нэгдмэл байдал, үйл ажиллагааны нэгдмэл байдал, логик нэгдмэл байдал, процедурийн нэгдмэл байдал, дараалсан нэгдмэл байдал, түр зуурын нэгдмэл байдал ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (373, "Communications domain", "Холбогдох домайн", "", "Харилцан ажиллах чадвартай протокол обьектуудын иж бүрдэл: Мэдээллийн Технологи – Нээлттэй хуваарилттай үйл ажиллагаа ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (374, "Communications protocol", "Төхөөрөмж хооронд дамуулах стандарт", "", "Төхөөрөмжүүдийн хооронд өгөгдлийг зөв зохистойгоор дамжуулахад ашигладаг стандарт багц юм. протоколууд нь өгөгдлийн хэлбэрийг тогтоож, дохионуудыг эхлэл, мөн шилжүүлгийн эцсийг хянадаг , жишээ нь Transmission Control Protocol.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (375, "Communications software ", "Програм хангамжийн харилцаа холбоо", "", "Энэ нь интернетэд холбогдох, майл хүлээж авах илгээх, Факс болон утасны холбоо гэх мэт компьютерийг ашиглах бүхий л зүйлд хэрэглэдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (376, "Community", "Нэгдэл", "", "Зорилгод нийцэхүйц обьектуудын бүрдэл. Мэдээллийн Технологи – Нээлттэй хуваарилттай үйл ажиллагаа – Жишиг загвар Архитектур  Тэмдэглэл: Зорилго нь гэрээг илэрхийлдэг, гэрээ нь зорилгод хэрхэн нийцүүлж болохыг тодорхойлдог. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (377, "Community object", "Нэгдлийн обьект ", "", "Нэгдлийг төлөөлдөг нийлмэл бүтэцтэй аж ахуй нэгж обьект. Мэдээллийн Технологи – Нээлттэй хуваарилттай үйл ажиллагаа  Тэмдэглэл: Нэгдлийн обьектуудын бүрэлдэхүүн нь нэгдлийг төлөөлдөг обьектууд юм. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (378, "community reception", "", "The reception of emissions from a space station in the broadcasting-satellite service by receiving equipment, which in some cases may be complex and have antennae larger than those used for individual reception, and intended for use: – by a group of the general public at one location; or – through a distribution system covering a limited area.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (379, "Compaction", "Шахалт", "", "Бичил програм нь эх хувиасаа илүү хурдан товчхон байдаг үүнтэй уялдуулан үйл ажиллагааг нь бичил програм руу хөрвүүлэх үйл явц  Локал шахалт, глобал  шахалт ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (380, "Comparator", "Харьцуулагч", "", "Хоёр компьютерын програмууд, файлууд болон ижил эсвэл ялгаатай өгөгдлийн иж бүрдлийг харьцуулдаг програм хангамж.  Тэмдэглэл: Харьцуулалтын энгийн обьектууд нь эх код, обьект код, өгөгдлийн сангийн файлуудын хувилбар адилхан байдаг. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (381, "Comparison operator ", "Харьцуулах үйлдэл", "", "Энэ үйлдэл нь =<+,> гэх мэт тэмдэгүүд ашиглан харьцуулалт хийдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (382, "Compatible", "Нийцтэй байх", "", "Тусгай өөрчлөлт эсвэл тохируулга хийлгүйгээр тоног төхөөрөмж мөн програм хагамжийн хэсгүүдийг хамтад нь ашиглах боломжтой.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (383, "Competent", "Өрсөлдөх чадвартай", "", "Ижилхэн програм хангамж болон техник хангамжийн орчинд share-лэхэд шаардлагатай функцыг ажиллуулах хоёр болон түүнээс дээш тооны системд зохицох чадвар. 2. хоёр болон түүнээс дээш тооны системд мэдээлэл солилцох чадвар 3. тодорхой интерфейсын шаардлагад нийцэх үйл ажиллагааны нэгжийн чадвар. Даалгаврыг гүйцэтгэхэд шаардлагатай зан характер, ажлын туршлага, сургалт, албан болон албан бус ур чадварууд болон мэдлэгтэй байх. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (384, "Competent assessor", "Өрсөлдөх чадвартай зөвлөх ", "", "Үнэлгээний явцыг хянан баталгаажуулж, дүгнэхэд өрсөлдөх чадвартай гэдгээ харуулсан зөвлөх. ", "", "");
