INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (388, "Compile and go", "Хөрвүүлж  үргэлжлүүлэх", "", "Компьютерийн програмыг ачаалах, холбох, гүйцэтгэх, хөрвүүлэх явцад зогсолтгүй ажиллаж байдаг арга техник", "", "");
