INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (390, "Compiler", "Хөрвүүлэгч", "", "Энэхүү программ нь хэлний өндөр түвшин эсвэл бага түвшинг орчуулах кодны нөөцтэй бөгөөд ерөнхийдөө өндөр түвшин бүр кодын зааврын дагуу үечилдэг. Тиймээс энэхүү программ нь бие даан ажиллах боломжтой байдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (391, "Compiler code", "Хөрвүүлэгч код ", "", "Хөрвүүлэгчийн боловсруулсан компьютерийн заавар болон өгөгдлийн тодорхойлолт дуудах код, тайлбар код, машины код ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (392, "Compiler directive source statement", "Хөрвүүлэгч удирдамжийн анхны илэрхийлэл", "", "Анхны илэрхийлэл, лабелуудыг тодорхойлдог кодынилэрхийлэл, гадаад кодын илэрхийлэл оруулах (include-ийн тайлбар) нөхцөлт хөрвүүлэлтийг илэрхийлэх, харин бусад төрлийн атрибутуудыг нэг бүрчлэн тодорхойлдоггүй. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (393, "Complementary Cumulative Distribution Function", "", "A method used to characterize the peak power statistics of a digitally modulated signal. The CCDF curve can be used to determine design parameters for CDMA systems (such as the amount of back-off to run in a power amplifier).", "", "CCDF", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (394, "Compliant ", "Ажиллах", "", "Стандартаар тодорхойлогдсон арга замаар хийх үйл ажиллагаа", "", "");
