INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (396, "Composite key", "Таних дугаар", "", "бичлэгийг танихын тулд ашигладаг тухайн бичлэг доторх гол түлхүүр, жишээлбэл тухайн хэрэглэгчийн  банкны дансны дугаарыг таних тэмдэг буюу бар код", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (397, "Compressing and Expanding", "", "A technique for reducing the dynamic range of a baseband signal. This reduces the number of quantizing steps needed and reduces noise in the process. Most digital systems include nonlinear quantizing to achieve this effect.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (398, "Compuring center", "Тооцооллын төв", "", "Туслах техник хангамж, тохирсон төхөөрөмжөөр хангагдсан үйл ажиллагаа болон  олон янзын хэрэглэгчдийн компьютерийн үйлдлийг, компьютерийн үйл ажиллагааг хангахад зориулагдсан тоног төхөөрөмж юм. ", "", "");
