INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (400, "Computer aided", "Компьютерийн туслах хэрэгсэл", "", "Компьютерийн гүйцэтгэдэг ажилын үйл явц болон техникийн хамаарал зэрэг орно. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (401, "Computer aided design (CAD) ", "Компьютерийн туслах хэрэгслийн дизайн", "", "Төхөөрөмж эсвэл системийг загварчлахад ашиглагддаг компьютерийн програм ба үүн дээр компьютерийн дэлгэц эсвэл принтер дээр дүрслэн, үйл ажиллагааг загварчлах ба үзүүлэлтүүдийн  статистикаар хангах боломжтой.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (402, "Computer aided design(CAD) ", "", "", "компьютерийн загвар дизайныг хийх систем CAD бол  зураг төсөл гарган боловсруулах хүснэгтийн оронд компьютерийн дэлгэцийг ашиглах боломжийг олгодог тогтолцоо юм", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (403, "Computer- aided software engineering ", "Компьютерийн туслах хэрэгслийн програм хангамжийн инженерчлэл", "", "Програм хангамжийн инженерчлэлийн үйл явц дахь туслах компьютерийн хэрэглээ зэрэг орно.", "", "");
