INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (408, "Computer resource allocation", "Компьютерийн нөөцийн хуваарилалт", "", "Одоогийн болон хүлээгдэж буй ажлуудад зориулагдсан компьютерийн нөөцийг хуваарьлах үүрэг гүйцэтгэнэ. ЖИШЭЭ: Гол санах ойн үүрэг, оролт гаралтын төхөөрөмж, компьютерийн системд нэгэн зэрэг  ажилыг хэрэгжүүлэхэд туслах тэдгээрийг агуулах.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (409, "Computer resources", "Компьютерийн нөөц", "", "Өгөгдсөн зорилгод нийцсэн компьютерийн тоног төхөөрөмж, багаж хэрэгсэл, програм, баримт бичиг, байгууламж, хангамж, бүрэлдэхүүн зэрэг орно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (410, "Computer science ", "Компьютер шинжлэх ухаан", "", "Компьютерийн мэдээлэл боловсруулалттай  холбоотой шинжлэх ухаан, технологийн салбар. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (411, "Computer software component (CSC) ", "Компьютер прогамм хангамжийн бүрэлдэхүүн хэсэг", "", "Функц болон логикийн хувьд компьютерийн програм хангамжийн тохируулгын зүйлийн хэсэг нь өөр, ерөнхийдөө 2 болон түүнээс дээш программ хангамжийн нэгжүүдийн цогц, нэгдсэн үзүүлэлт", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (412, "Computer software configuration item (CSCI) ", "Компьютер програм хангамжийн тохируулгын зүйл ", "", "Програм хангамжийн нэгдсэн үзүүлэлт нь тохируулгын менежментэд зориулагдан загварчлагддаг бөгөөд тохируулгын менежментийн үйл явц дахь нэгэн гэж авч үздэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (413, "Computer system", "Компьютерийн систем", "", "1 ба түүнээс дээш компьютерүүд болон нэгдсэн програм хангамжуудыг агуулдаг систем юм. ", "", "");
