INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (415, "Computerize", "компьютержүүлэх, автоматжуулах", "", "Компьютероор автоматжуулах", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (416, "Computing system specification concept ", "Тооцооллын системийн онцлог зарчмууд", "", "Тодорхой болон тоологдох тооцооллын системийн ойлголт нь тусгаарлалт болон хамаарлын шинж чанарыг агуулсан байдаг. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (417, "Computing system tool", "Тооцооллын системийн хэрэгсэл", "", "Динамик системийг хөгжүүлж бүтээхэд байгууламж болон боловсруулагчдад хэрэглэгддэг компьютерт суурилсан төхөөрөмж юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (418, "Comunications planing", "Холбооны төлөвлөлт", "", "Оролцогч талуудын холболтын хэрэгцээ шаардлага болон мэдээлэл хэрхэн нийцэж байгаагаас хамаарах процесс. Хэнд ямар холболт хэрэгтэй, тэдэнд хэзээ тухайн холболт шаардлагатай ба тэдэнд холболтыг хэрхэн хийж өгөх талаар төлөвлөлтөд багтана. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (419, "Concatenated coding", "", "The use of two codes, an inner and outer code, to further improve transmission performance. Using this technique, a data stream is encoded with the outer code and then the coded data is further encoded with the outer code. This technique is particularly effective in bursty environments. The use of concatenated codes is most common in space communications and usually involves a convolutional inner code and Reed-Solomon outer code.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (420, "Concatenation ", "Хэлхээ гинж цуваа", "", "2 болон түүнээс дээш цуваа утаснууд хамтдаа нэгдсэн хэлхээ процесс.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (421, "Concept of operations ( ConOps ) document", "Үйл апжиллагааны баримтын зарчим", "", "Хэрэглэгчид чиглэсэн баримт нь эцсийн хэрэглэгчийн байр суурьнаас системийн үйл ажиллагааны шинж чанарыг тодорхойлдог. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (422, "Concept phase", "Зарчмын үе шат", "", "Системийн амьдралын цикл дэх хугацааны үечлэл нь хэрэглэгчдийн хэрэгцээ тодорхойлогдох болон системийн зарчмууд тодорхойлогдох, үнэлэгдэх зэргээр тодорхойлогдоно. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (423, "Conceptual data model", "Зарчмын дата загвар", "", "Дата загвар нь хэрэглэгчдэд танигдсан дата групп мэдээллүүдэд загвар болж өгдөг. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (424, "Concurrent thread", "Давхацсан утас", "", "2 болон түүнээс дээш холбогдсон шугамууд ижил цаг хугацаанд олон төрлийн үүргийг гүйцэтгэн биелүүлэх", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (425, "Condition", " Нөхцөл", "", "Нөхцөл, томъёоны төрөлд тухалбал ‘IF нас >17  гээд 17 оос бага нөхцөлүүд гарч ирэх", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (426, "Conferencing software", "Хуралдааны програм хангамж", "", "Ямар ч газраас  виртуал бага хуралд оролцох системтэй хэрэглэгчид холбогдох боломжтой байгаа нь Программ хангамж юм. Програм хангамж нь цахим бүрдэл хэсэг доторх текст мессежүүдийг хэрэглэгчид харах боломжтой юм.Хуралдааны програм хангамж нь харэглэгчид хоорондын дүрс бичлэгийн болон дуу бичлэгийн харилцааг хүртэл өөртөө багтааж байдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (427, "Configuration", "Тохиргоо", "", "Компьютерийн систем дэхь програм хангамж болон техник хангамж зэргийг өөртөө багтаасан бүрэлдэхүүн хэсгүүдээс сонгох буюу тохиргоо хийх.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (428, "Configure", "Боловсронгуй хэлбэр дүрст орох үйл явц", "", "техник хангамж болон програм хангамжийн тохиргоог өөрчлөх үйл явц нь таны тооцоолж байдаг платформ эсвэл хувийн сонголтонд тань тохирохыг хэлнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (429, "Connect ", "Холболт", "", "2 болон түүнээс дээш зүйлүүд нэгдэх тухайлбал холбогдсон компьютерууд хоорондоо   сүлжээгээр холбогдох.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (430, "Connection speed ", "Холболтын хурд", "", "Компьютерт нэмэлт өгөгдөл татаж суулгах хурд.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (431, "Constant", "Тогтмол хэмжигдхүүн", "", "Өгөгдөл нь тогтсон үнэлэмж буюу хэмжээтэй байна.Хэрэв тогтмол бус хувьсах хэмжигдхүүн байвал үүнийг ашиглан хувиргана.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (432, "Constellation", "", "A graphical representation of signal states for a digital system.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (433, "Contacts", "Нэрсийн жагсаалт", "", "Хэрглэгчийн цахим шуудан ба нэрсүүдийн жагсаалт(Хувь хүний)-г хэлнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (434, "Content", "Өгөгдлийн агууламж", "", "Өгөгдөлийн агууламж гэдэг нь веб хуудсанд байрлах бичвэрэн ба дүрсийн өгөгдөл юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (435, "Contention Ratio", "Хандалтын хэмжээ(хүрээ)", "", "Хандалтын хүрээ гэдэг нь илгээн түгээсэн мэдээлэл болон өгөгдлийг нэгээс олон буюу хэр олон хэрэглэгч хүлээн авч буцаан хандаж буйг илэрхийлэх хэмжигдхүүн юм.Жишээ нь Интернэт сервист холбогдон цацагдсан өгөгдлийг үзэж байгаа хэрэглэгчдийн тоо.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (436, "Continuous Phase Modulation", "", "A phase modulation technique employing smooth transitions between signal states. This reduces sidelobe spectral energy and improves co-channel performance.", "", "CPM", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (437, "Continuous Stationary", "Тасралтгүй хэсэг", "", "Уг командыг ашигласнаар принтерт салангид хуудас хамт өгөгдсөн тохиолдолд дан хуудсыг хэвлэнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (438, "Continuous Wave", "", "The term commonly gives to an unmodulated RF carrier.", "", "CW", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (439, "Contrast(1)", "Өнгөний нарийвчлал", "", "Өнгөний нарийвчлал гэдэг нь дүрс зурагны харагдах байдлийг тохируулах,гэрэлтүүлэг ба харанхуйлалтын түвшинг нарийвчилж тодруулах үйлдэл.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (440, "Contrast(2) ", "Өнгөний нарийвчлал", "", "Өнгөний нарийвчлал гэдэг нь фото зурагны харагдах байдлийг тохируулах,гэрэлтүүлэг ба харанхуйлалтыг нарийвчлан сайжруулах үйл явц.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (441, "Control Channel", "", "A general term used to describe the channels that transmit signaling and control information between the network and the mobile stations", "", "CCH", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (442, "Control channel", "", "A channel, usually logical, used to send administrative and supervisory signals between a base station and a mobile station.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (443, "Control Characters", "Удирдах тэмдэгтүүд", "", "Олон тэмдэгтүүд нь тус бүрдээ удирдах тэмдэгтүүдтэй байна.Эдгээрийг хэвлэгдэх боломжгүй тэмдэгтүүдийг орлуулах зорилгоор ашигладаг.Жишээ нь: Уг тэмдэгт баримт болон файлыг дуусч буйг илэрхийлдэг.Үргэлж уг ^ тэмдэгтээр илэрхийлнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (444, "Control Panel", "Удирдах самбар", "", "Энэ хэрэгсэлээр хэрэглэгч компьютерийн программууд болон тохиргоог хялбараар хянах ба тохируулах боломжтой.Жишээ нь:Хулганыг хэр хурдан хөдөлгөх,дэлгэцийн гэрлийг(өнгө) тохируулах,чанга яригчийн түвшинг тохируулах,шинээр программ суулгах эсвэл хуучин программаа устгах гэх мэт үйлдлүүдийг хийнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (445, "Control Unit", "Удирдах байгууламж", "", "Төв процессорын хэсэг бөгөөд байгууламжаас ирсэн командыг хянах буюу гүйцэтгэнэ.Удирдах байгууламж нь өгөгдлийг зөөвөрлөхдөө дэс дараалалд оруулдаг.Мөн удирдах төв компьютероос ирсэн дохиог дахин кодчлох буюу кодийг тайлах үүргийг гүйцэтгэнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (446, "Control+Alt+Delete", "Контрол Алт Делете товчлууруудийн хослол", "", "Уг товчлууруудын хослол нь компьютерийн үйл ажиллагааг тасалдуулж буй программыг зогсоох эсвэл компьютероо дахин унтраах асаах үйлдлийг гүйцэтгэнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (447, "Conversational mode(1) ", "Ярианы горим", "", "Зурвас болон дохион мэдээллийг хавтангаар дамжуулан дэлгэцэнд гаргах арга(зам).Яриаг  хугацаа ба цагын дарааллаар дэлгэцэнд харуулна.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (448, "Conversational mode(2) ", "Ярианы горим ", "", "Хэрэглэгч эцсийн төхөөрөмж(гар утас) ашиглан сүлжээнд нэвтрэх үед төв процессороос ярианы горимыг тодорхойлж тэр даруй холбон үргэлжлүүлнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (449, "Converter", "Хувиргуур", "", "Хүлээн авч буй дохиог өөр дохиоруу хувиргагч төхөөрөмж.Ихэвчлэн дижитал дохионоос аналог дохио руу эсвэл эсрэгээр нь хувиргах үйл явц.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (450, "Convolutional code", "", "A type of forward error correction code using a shift register containing a number of stages to shift the input bits one at a time to produce a coded output.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (451, "Cookie", "Хөтөчийн санах ойд хадгалагдах мэдээлэл", "", "Хөтөчийн санах ойд агуулагдах мэдээлэл нь хандалт хийсэн веб сайтын серверлүү хэрэглэгчийн мэдээллийг хөтөчөөр дамжуулан илгээдэг.Дараа нь хөтөчөө ашиглан өмнөх хандалт хийсэн сайтруугаа хандалт хийхэд веб серверээс өгөгдлийг шалган буцаан илгээдэг.Жишээ нь онлайн дэлгүүрүүд үүнийг ашигладаг бөгөөд сайт руу нэвтэрхэд хэрэглэгчийн мэдээллийг хөтөчийн санах ойгоор дамжуулан шалгаж тухайн хэрэглэгчийг таниж хариу өгөгдлийг илгээдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (452, "Coordinated Universal Time", "", "The time scale, maintained by the BIPM and the International Earth Rotation Service (IERS), which forms the basis of a coordinated dissemination of standard frequencies and time signals. UTC corresponds exactly in rate with TAI, but differs from it by an integral number of seconds. The UTC scale is adjusted by the insertion or deletion of seconds (positive or negative leap seconds) to ensure approximate agreement with UT1.", "", "UTC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (453, "Co-processor", "Зэрэгцээ процессор", "", "CPU-н компьютерийн процессорийн математик үйл явцыг боловсруулдаг ө/х зорилтыг боловсруулдаг компьютерийн хоёрдогч  процессор юм", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (454, "Copy", "Хувилах", "", "Файл(Баримт)-ыг ижил хувилбараар хувилах процесс(үйл явц).", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (455, "Copy and Paste", "Хувилан хуулах", "", "Текст,зураг,архив гэх мэт өгөгдлүүдийг хувилж хуулах үйл явц.Бичиг баримт эсвэл компьютерийн бүх төрлийн өгөгдлүүдийг мөн адил хуулна.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (456, "Copy Protection", "Хуулбар хамгаалах", "", "Хуулбар хамгаалах нь өгөгдлийг дискрүү хуулах үед алдаа гарахаас сэргийлэн хамгаалах үйл явц.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (457, "Copyright", "Зохиогчийн эрх", "", "Зохиогч болон хэвлэгчид өөрсдийн зохион бүтээсэн компьютерийн программ хангамж болон мэдээллийг зөвшөөрөлгүй ашиглахаас хамгаалах хууль ёсний эрх авхыг хэлнэ.Зохиогчийн эрх нь 1988 онд загвар хийц ба зохиогчийн эрхийн гэрчилгээгээр батлагдсан.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (458, "Cordless Telephone 2", "", "A second generation cordless telephone system that allowed users to roam away from their home base stations and receive service in public places. Away from the home base station, the service was one way outbound from the phone to a telepoint within range.", "", "CT-2", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (459, "Corruption", "Алдагдал", "", "Мэдээлэл эсвэл өгөгдлийг хадгалах ба хуулах үед гарах алдааг илэрхийлдэг.Энэ нь ихэнхдээ цахилгааны нөлөөлөл эсвэл эвдэрсэн төхөөрөмж гэх мэт физик нөлөөллөөс болж бий болдог.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (460, "Coverage area", "", "Area associated with a space station for a given service and a specified frequency within which, under specified technical conditions, it is feasible for radiocommunications to be established with one or several earth stations, either for reception or transmission or both.", "", "", "Note 1 – Several coverage areas may be associated with one and the same station, for example, a satellite with several antenna beams. Note 2 – The technical conditions include the following: characteristics of the equipment used both at the transmitting and receiving stations, how it is installed, quality of transmission desired, e.g., protection ratios and operating conditions. Note 3 – The following may be distinguishable: – interference free coverage area, i.e., that limited solely by natural or artificial noise; – the nominal coverage area: it is defined, when establishing a frequency plan, by taking into account the foreseen transmitters; – the actual coverage area, i.e., with allowance made for the noise and interference which exist in practice. Note 4 – The concept of “coverage area” cannot be simply applied to a space station on board a non-geostationary satellite for which further study is necessary. Note 5 – Furthermore, the term “service area” should have the same technical basis as for “coverage area”, but also include administrative aspects.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (461, "coverage area", "", "Area associated with a transmitting station for a given service and a specified frequency within which, under specified technical conditions, radiocommunications may be established with one or several receiving stations.", "", "", "Note 1 – Several coverage areas may be associated with one and the same station. Note 2 – The technical conditions include the following: characteristics of the equipment used both at the transmitting and receiving stations, how it is installed, quality of transmission desired, e.g., protection ratios and operating conditions. Note 3 – The following may be distinguishable: – interference-free coverage area, i.e., that limited solely by natural or artificial noise; – the nominal coverage area: it is defined, when establishing a frequency plan by taking into account the foreseen transmitters; – the actual coverage area, i.e., with allowance made for the noise and interference which exists in practice. Note 4 – Furthermore, the term “service area” should have the same technical basis as for “coverage area”, but also include administrative aspects.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (462, "Coverage area", "", "The geographical reach of a mobile communications network or system.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (463, "Coverage hole", "", "An area within the radio coverage footprint of a wireless system in which the RF signal level is below the design threshold. Coverage holes are usually caused by physical obstructions such as buildings, foliage, hills, tunnels and indoor parking garages.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (464, "CPU (Central proccessing unit)", "", "", "CPU нь бүртгэл, арифметик логик нэгж, удирдлагын нэгж зэргийг багтаасан компьютерийн үндсэн хэсэг юм. Голчлон CPU нь үндсэн санах ойн хэсэгт ажиллана. Үүнийг заримдаа төв процессор эсвэл процессор гэж нэрлэдэг. Ихэнх компьютерүүд нэгээс илүү процессортой байдаг. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (465, "Cradle", "Тулгуур", "", "Хувын дижитал туслах гэдэг үгийн товчлол эсвэл адилхан төхөөрөмж байж болно.Мөн  давхцсан дохиог компьютерлуу холбоно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (466, "Crop", "Тайрах", "", "Тайрах гэдэг нь графикийн програм хангамж.Энэ нь ихэнхдээ зургын хэрэгцээгүй хэсгийг  арилгахын тулд тодорхой хүрээг сонгон авах үйлдэл юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (467, "Crop Tool", "Тайрах Хэрэгсэл", "", "Тайрах хэрэгсэл нь мөн адил графикийн программ хангамжюм.Уг хэрэгсэл нь зурагнаас тасдахад зөвшөөрөгдсөн хэсгүүдийг дэлгэцэнд харуулна.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (468, "Cross correlation", "", "The complex inner product of a first sequence with a shifted version of a second sequence. Sequences are considered to have good cross correlation properties when there is very little correlation between the sequences as they are shifted against each other.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (469, "Cross Posting", "Давхар илгээх", "", "Давхар илгээх гэдэг нь хэрэглэгч 1 ижил мессежийг нэгээс олон хаяг руу зэрэг илгээх үйлдэл юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (470, "Cross talk", "", "The ability for unwanted information from one channel to 'spill over' into an adjacent channel.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (471, "Cross-polarization", "Хөндлөн туйлшрал ", "", "Хөндлөн туйлшралтай долгион нь цахилгаан ба соронзон орон орших хавтгайд пэрпендукляр чиглэлд тархдаг. Цахилгаан соронзон долгион нь хөндлөн туйлшралтай байдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (472, "Cross-polarization", "", "The appearance, in the course of propagation, of a polarization component which is orthogonal to the expected polarization.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (473, "Cross-polarization discrimination", "Хөндлөнтуйлширлын ялгаа ", "", "Өгөгдсөн туйлшралтай дамжуулагдсан радио долионы хувьд тэгш өнцөгт туйлшралыг  хүлээн авах ба хүлээгдэж буй туйлшарлыг дамжуулдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (474, "Cross-polarization discrimination", "", "For a radio wave transmitted with a given polarization, the ratio at the reception point of the power received with the expected polarization to the power received with the orthogonal polarization.", "", "", "Note – The cross-polarization discrimination depends both on the characteristics of the antennas and on the propagation medium.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (475, "Cross-polarization isolation", "Хөндлөнтуйлшралыг тусгаарлах ", "", "Адилхан чиглэлд тархаж буй туйлширсан долгионууд долгионы тархалтын чиглэлд пэрпендүклүяр орших хавтгайд хэлбэлзэлийн чиглэлд хамааран ялгаддаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (476, "Cross-polarization isolation", "", "For two radio waves transmitted at the same frequency with the same power and orthogonal polarization, the ratio of the co-polarized power in a given receiver to the cross-polarized power in that receiver.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (477, "CRT Monitor ", "Катодан цацаргалтын хоолойн дэлгэц", "", "Катодан цацаргалтын хоолойн дэлгэц гэдэг нь катодан цацаргалтын хоолойг ашиглан дүрсийг дэлгэцэнд харуулах үйл явц юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (478, "Cumulative Distribution Function", "", "The cumulative probability that a parameter will be less than a given value X.", "", "CDF", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (479, "Currency Data", "Өгөгдөлийн хүчинтэй байх хугацаа", "", "Хадгалагдсан мэдээллийн төрөл багтаамж зэргээр нь эргэлтэд оруулах.(Жишээ:Тэмдэгтэд хөрвүүлэх эсвэл эсрэгээр нь хийх үйлдэл)", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (480, "Cursor Control Key", "Курсорон сумаар удирдах товчлуур", "", "Хулганы сумтай адилхан харагддаг товчлуур.Сумны чиглэлийн дагуу дээшээ болон доошоо гүйлгэж удирдана.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (481, "Customise", "Өөрчлөх", "", "Хэрэглэгчийн сонголтонд тодорхой өргөдөл болон компьютерын систем тохируулдаг. Ихэвчлэн хэрэглэгчийн гоо зүйн өөрчлөлт түүнчлэн тэнхлэгт үйл ажиллагааг хийх боломжтой. Жишээ нь график, өнгө, байршлыг өөрчилж болох юм", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (482, "Cut and paste", "Таслах мөн наах  ", "", "Өгөдлийг устгахгүйгээр ижил эсвэл өөр файлыг компьютэрийн өөр газар руу шилжүүлэх үйл явц. (текст зураг файл гэх мэт)", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (483, "Cyberspace", "Кибер огторгуй /орон зай/", "", "Интернэт ашиглан мэдээлэлрүү хандаж болно. Энэ хуурмаг виртуаль ертөнц нь бүх  сайт, бүх файлуудаас бүрдсэн гэж үзэж болно. Энэ виртуаль цахим ертөнцөөр тойрон явж, ижил аргаар сонирхолтой зүйлийг хайж дэлхийн бодит талбайг судалж болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (484, "Cyclic codes", "", "These are a subclass of linear block codes with an algebraic structure that enables encoding to be implemented with a linear shift register and decoding to be implemented without a lookup table.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (485, "Cyclic redundancy (CRC)", "", "", "Найдвартай мэдээлэл гарган баталгаажуулах шалгалтын төрөл нийцэж байгаа болон гэмтсэн байна гэсэн үг. CRC нь түгээмэл дискүүд дээр хадгалагдсан өгөдлийн блокуудыг сүлжээгээр дамжуулах буюу шалгахад ашигласан байдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (486, "Cyclic Redundancy Code", "", "The use of the syndrome of a cyclic block code to detect errors.", "", "CRC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (487, "Cylinder", "Цилиндр", "", "Цилиндр гэж нэрлэдэг тэжээлийн цагираг нь диск дээр хадгалагдаж байдаг. Диск бүр дэх арав дахь мөрийг нь цилиндр гэж нэрлэдэг. Жишээ нь нэг нь нөгөөгөөсөө олонлог багц юм. Энэ нь толгой мэдээллийг олж авахын тулд уншиж бичих шаардлаггүй учир нь цилиндр дээр биш харин нэг диск дээр мэдээллийг хамт хадгалах нь хэвийн үзэгдэл юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (488, "Cymomotive force", "", "The product formed by multiplying the electric field strength at a given point in space, due to a transmitting station, by the distance of the point from the antenna. This distance must be sufficient for the reactive components of the field to be negligible; moreover, the finite conductivity of the ground is supposed to have no effect on propagation.", "", "(c.m.f", "Note 1 – The cymomotive force (c.m.f.) is a vector; when necessary it may be expressed in terms of components along axes perpendicular to the direction of propagation. Note 2 – The c.m.f. is expressed in volts; it corresponds numerically to the field strength in mV/m at a distance of 1 km.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (489, "Data", "Өгөгдөл", "", "Олон тооны өгөгдөл ба мэдээллийн боловсруулагдаагүй хэлбэр юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (490, "Data capture", "Өгөдөл авах ", "", "Компьютерт оруулах өгөгдлийн цуглуулага. Дэлгүүрт бар код уншуулах шатахууны тоолуурыг гараар бичих зэрэгт бар код автоматаар хийгдэж болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (491, "Data collection satellite", "", "A satellite whose main purpose is the collection of data from stations on the Earth or in the atmosphere of the Earth, and subsequent forwarding of those data to one or more earth stations. It may also provide for communication in the other direction.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (492, "Data compression", "Өгөгдлийн нягтруулга ", "", "Том хэмжээний байдлаар эзлэгдсэн зайг багасгахыг өгөгдлийн нягтруулга гэнэ. Өгөгдлийн төрлүүдэд зориулсан янз бүрийн арга. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (493, "Data entry", "Өгөгдлийн оролт ", "", "Компьютер руу өгөгдөл  оруулж боловсруулалт хийх.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (494, "Data integrity", "Өгөгдлийн бүрэн бүтэн байдал", "", "Энэ үед боловсруулалт хийсний дараа аль нэг мэдээллийг үнэн зөв тодорхойлно. Мэдээллийг өөрчилснөөр бүрэн бүтэн байх болно. Аюулгүй мэдээллийг ямар нэгэн тохиолдлоор, эсвэл буруу өөрчлөлтийг илрүүлэх замаар  өгөгдлийн бүрэн бүтэн байгаа эсэхийг шалгах хэрэгтэй.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (495, "Data logging", "Өгөгдөл бүртгэх ", "", "Цаг хугацаанд нь усны температурын өөрчлөлтийг бүртгэх ө.х фото электроныг ашиглах замаар мэдээллийг хадгалах юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (496, "Data processing manager", "Өгөгдөл боловсруулах менежер ", "", "Өгөгдөл  боловсруулах нийт ажиллагааг хариуцсан хүн.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (497, "Data projector", "Өгөгдлийн проектор", "", "Компьютер руу холбодог том дэлгэц, мөн проектор руу шууд хянах чадварыг хэмэгдүүлдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (498, "Data Protection Act", "Өгөгдлийг хамгаалах тухай хуйл", "", "Өгөгдлийг хамгаалах тухай хуйл 1998 оны компьютерийн системүүд дээр өгөгдлийн хяналтыг тогтоосон УИХ-ын Их Британийн хууль юм.   Хууль мэдээ нь хувийн олон асуудлыг хамардаг. Хууль нь өгөгдлийн контроллер компьютержсэн мэдээллийг хийж ашиглах үүрэгтэй хүмүүсээр тодорхойлогддог. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (499, "Data redundancy", "Өгөгдлийг цомхотгох", "", "Мэдээллийн систем дээр ихэвчлэн хэрэгцээгүй үед хоёр удаа гарч ирэх тохиолдол.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (500, "Data relay satellite", "", "A satellite whose main purpose is the relay of data from one or more mission satellites or space probes to one or more earth stations. It may also provide for communication in the other direction. Additionally, it may be used as a relay for the space operation service.", "", "", "Note – Data relay satellites are generally geostationary.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (501, "Data retrieval", "Өгөгдлийг олж авах", "", "Мэдээлллийн санд хадгалагдсан их хэмжээний өгөгдөл нь ашигтай мэдээлэл олборлох үйл явц юм. Энэ нь ихэвчлэн дэлгэцэнд хайлт хийх зааврыг ялгах явдал юм. Энэ нь өгөгдлийн их хэмжээний хайлт хийснээр үр дүнг гаргаж авах боломжтой. Мөн мэдээллийг сэргээх гэж нэрлэдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (502, "Data security", "Өгөгдлийн аюулгүй байдал", "", "Төрөл бүрийн аргыг ашиглах нь аюулгүй зөв хадгалагдсан байгаа эсэхийг шалгах                          хэрэгтэй. (Өгөгдлийг хамгаалах) Өгөгдлийн бүрэн бүтэн байдал аюулгүй байдлын       мэдээллийн нууцлалыг хангах, түүнчлэн өгөгдлийн алдагдалд буюу устгахаас                        урьдчилан сэргийлэх хэрэгтэй.                                                                                                                                      Өгөгдлийн аюулгүй байдал нь тухайн хэсэг өгөгдлийн төлөвлөгөөтэй бөгөөд энэ нь үр дүнтэй сэргээн босгох ажиллагааг шалгаж байдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (503, "Data subject", "Өгөгдлийн сэдэв ", "", "Мэдээлэл хамгаалах хуульд өгөгдлийн сэдэв нь хүнд холбогдолтой байхаар.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (504, "Data transmission", "Өгөгдөл дамжуулах", "", "Нэг төхөөрөмжөөс өгөгдөл авах. Энэ нь компьютерийн сүлжээний системийн хэсгүүд хооронд өгөгдлийг дамжуулах.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (505, "Data type", "Өгөгдлийн төрөл", "", "Өгөгдлийн төрөл албан ёсны тайлбар хадгалж эсвэл програмын систем дотор удирдаж байгаа цагаан толгойн тоо баримт буюу логик  тоо баримт. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (506, "Data user", "Хэрэглэгчийн тоо баримт", "", "Мэдээлэл хамгаалах хуулийн зорилгоор тоо баримтын агуулга, хэрэглэгчийн хувийн мэдээллийг ашиглахад хяналт тавих.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (507, "Database", "Өгөгдлийн сан ", "", "Энэ нь янз бүрийн програмууд хэд хэдэн замаар хийх боломжыг олгох байдлаар зохион байгуулалттай тоо баримт, тэдгээрийн хооронд уялдаа холбоотой юм. Өгөгдлийг хугацаанд нь аль нэг аргаар тодорхойлохдоо чөлөөтэй ашиглаж болно .", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (508, "Database management system (DBMS", "", "", "Мэдээлэл дэх шинэ өгөгдлийг олж нэмэх, одоо байгаа тоо баримтыг өөрчлөх боломжтой програм хангамж юм. Өгөгдлийн сангийн тенежментийн систем хэрхэн автоматаар ажилладаг болохыг мэдэж авах. Энэ өгөгдлийн шинэчлэлтүүд нь хүссэн тоо баримтыг олж шийдвэрлэпдэг. Өгөгдлийн сангийн сисмемийн менежментийн систем нь өөрийн энгийн хэрэглэгчийн интерфэйсийг өгч болно эсвэл зүгээр л байгууллагад байгаа мэдээллийг ашиглан програмуудтай харьцаж болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (509, "Database manager", "Өгөдлийн сангийн менежер", "", "Өгөгдлийн сангийн адимнистратор юмуу эсвэл мэдээллийн ажилтан гэгддэг өгөгдлийн сангийн менежир бүтэц, зохион байгуулалт нь байгууллагад өгөгдлийн хяналт тавих үүрэг бүхий хүмүүс юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (510, "Dataflow diagram", "Өгөгдлийн урсгал диаграм", "", "Энэ нь дамжуулах үйл явцад мөн мэдээллийн систем хэрхэн хаашаа зөөх зарчимийг харуулж байдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (511, "Date data", "Өгөгдлийн хугацаа", "", "Өдрийг илэрхийлэхэд ашиглагдаж байгаа өгөгдлийн төрөл. Жишээ нь 01022007 гэж бичнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (512, "Datum", "Анхны мэдээлэл", "", "Өгөгдлийн ганц тоо нь тэдээллийн боловсруулагдаагүй материал.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (513, "Dead spot", "", "An area within the coverage area of a wireless network in which there is no coverage or transmission falls off. Dead spots are often caused by electronic interference or physical barriers such as hills, tunnels and indoor parking garages. See also coverage area.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (514, "Debug", "Алдаа засах", "", "Энэ програмууд нь зөв илрүүлсэн ямар нэгэн алдаануудыг залруулах үйл явц юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (515, "DeciBel", "", "A technique for expressing voltage, power, gain, loss or frequency in logarithmic form against a reference. Typical references include volts, watts or Hz. DeciBels are calculated using the expression: dB = 10*log(x/y).", "", "dB", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (516, "DeciBels referenced to a dipole antenna", "", "A technique for expressing a power gain measurement in logarithmic form using a standard dipole antenna as a reference.", "", "dBd", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (517, "DeciBels referenced to a milliWatt", "", "A technique for expressing a power measurement in logarithmic form using 1 mW as a reference.", "", "dBm", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (518, "DeciBels referenced to an isotropic antenna", "", "A technique for expressing a power gain measurement in logarithmic form using a theoretical isotropic antenna as a reference", "", "dBi", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (519, "DeciBels referenced to the carrier", "", "A technique for expressing a power measurement in logarithmic form using the carrier power as a reference.", "", "dBc", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (520, "Decimal", "Аравт", "", "Цифр 0-9 ыг ашиглан 10-тын системийг дугаарлах. Энэ нь ихэвчлэн хүмүүсийн хэрэглэдэг систем.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (521, "Decimal point", "Аравтын функц", "", "Аравтын тоонд бүхэл тоог цэгийн зүүн талд бутархай тоог цэгийн баруун талд хэсэгчлэн хуваавал тохироммжтой.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (522, "Declare a variable", "Хувьсагч зарлах ", "", "Ямар нэг зүйлийг зарлах програмчлал нь хувьсагч массив болон бусад ижил төрлийн объектын нэр болон өгөгдлийн төрлийг тодорхойлох Бүгд биш ч гэсэн олон програмчлалын хэл тэдгээрийг хэрэглэхээсээ өмнө хувьсагчыг зарлахыг хэрэглэгчээс шаардана.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (523, "Decode", "Декодлох", "", "Энэ цифрлэгдсэн буюу кодлосны дараа уншигдана. Жишээ нь өгөгдлийг буцаахтай  төстэй.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (524, "Decrement", "Багасалт", "", "Ямар нэгэн зүйлийн нэг нэгээр багасгах", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (525, "Decryption", "Код тайлалт, түлхүүр тайлалт", "", "Нууцлалт нь код тайлалтгүй ойлгомжгүй компьютерийн системд өгөгдөл үүсгэдэг. Шифрлэгдсэн өгөгдөл нь агуулгагүй харагддаг ба замбараагүй өгөгдлөөр тодорхойлогддог. Нууцлалт нь өгөгдлийг хамгаалалттай үүсгэдэг энэ нь ойлгомжтой байхаас урьдчилан сэргийлж байгаа юм. Электроникт хоёулаа агуулагдах ба компьютерийн системүүдийн хооронд мэдээллэнэ. Код тайлалт нь уншигдах боломжгүй өгөгдлийг ойлгомжтой хэлбэрт хөрвүүлнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (526, "Dedicated Control Channel", "", "A dedicated channel used to carry signalling information in active GSM and cdma2000 traffic channels.", "", "DCCH", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (527, "Deep space", "", "Space at distances from the Earth equal to, or greater than, 2 × 106 km.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (528, "Default", "Өөрчлөх, зөрчих , өгөгдмөл ", "", "Хэрэглэгч тодорхой байдлаар тэдгээрийг өөрчлөхөд зориулагдсан компьютерын техник хангамж эсвэл програм хангамж дахь тохиргоонууд", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (529, "Default settings", "Анхдагч тохиргоо ", "", "Хард дискийг түр хугацаагаар ашигласаны дараа үүн дээр файлууд нь ижил файлууд өөр өөр нэр дээр олон хэсэгт хуваагдаж хадгалагдах нь дискний үйл ажиллагааг удаашруулдаг. Тухайн хэсэгчилэгдсэн файлууд нь дахин нэгдэх үед диск эргээд хэвийн үйл ажиллагаандаа ордог.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (530, "Defragment", "Задлах, хуваах", "", "Дараа нь хатуу дискэн дэх файлууд дах илүү хуваагддаг бөгөөд ижил файлуудын өөр хэсгүүд дискэн дэх өөр газар хадгалагдахыг хуваах буюу задлах гэнэ. Энэ нь дискний үйл ажиллагааг удаашруулдаг. Эдгээр эд ангиудыг дахин хамтад нь цуглуулах үе шатыг диск задрах гэнэ. Тиймээс дискний хүрэлцээг хурдлуулна.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (531, "Delay spread", "", "A type of distortion due to multipath resulting in the spreading out or 'smearing' of the received signal. It occurs when identical signals arrive via different paths and have different time delays.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (532, "Delete", "Устгах ", "", "Дискээс устгах болон тэмдэгтүүд мэдээллүүдийг арилгах үйл явц юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (533, "Delete key", "", "", "Тэмдэгтийг устгадаг гол товчлуур", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (534, "Demo version", "Демо хувилбар ", "", "Програмын бүрэн онцлогыг ашигладаг жишээ програм", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (535, "Demodulation", "", "Process of recovering the original modulating signal from a modulated carrier. The original modulating signal is usually the information being transmitted, typically voice or data.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (536, "Depolarization", "Туйлгүй болох", "", "Тодорхой туйлшралын радио долгионы урт ямар ч байж болно. Дамжуулсан туйлшарлын дараах үүрэг нь тодорой тархалтын үзэгдэл юм. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (537, "Depolarization", "", "A phenomenon by virtue of which all or part of the power of a radio wave transmitted with a defined polarization may no longer have a defined polarization after propagation.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (538, "Descendantе", "Удам", "", "Гол мэдээний бүтцэд удам нь илүү өндөр түвшинд өөр горимд холбоотой байдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (539, "Desk top publishing", "Ширээн дээрх хэвлэлт", "", "Энэ нь бусад текст боловсруулагчтай төстэй бөгөөд энэ нь зохиомжийн дизайныг илүү зохион байгуулалттай болгохыг санал болгодог.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (540, "Desktop", "Дэлгэц", "", "Хэрэглэгчийн схемийн хэрэгсэл дэх дэлгэцэн дээрх үндсэн ажлын байр. Хэрэглэгчид дэлгэцэн дээрх файл програмуудыг ашиглах ба файлыг товчлох хадгалах зэргийг хийж болно. Мөн хэрэглэгч дэлгэцний дүрс зураг фон зэргийг өөрчилж болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (541, "Desktop computer", "Ширээний компьютер", "", "Хувийн хэрэглэгчийн хувьд ширээний компьютер нь ширээн дээр ашиглахад зориулагдсан. Энэ нь бүрэн хэмжээний гартай үүнд эрчим хүчний гарны шугам ашиглагддаг. Төв процессор диск уншигч болон бусал зүйлийг агуулна. Хайрцаг нь заримдаа дэлгэцэнд дэмжлэг өгөхөөр хадгалагддаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (542, "Destination address", "Очих хаяг", "", "Өгөгдлийн багц илгээх үйлдэл юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (543, "Detect a virus", "Вирус илрүүлэх", "", "Компьютерйин хэрэглэж буй вирусны эсрэг програм дээрх вирусыг илрүүлэх", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (544, "Developer", "Хөгжүүлэгч", "", "Систем, програм, вебсайт зэргийг хөгжүүлэх үүрэгтэй хүн", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (545, "Device Under Test", "", "An acronym used to describe some type of electrical apparatus connected to test instrumentation. The apparatus can range from a single component to a complex subsystem such as a mobile phone, base station or MSC", "", "DUT", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (546, "DHC servers", "ДНС сервер", "", "Домайныг IP хаяг луу хөрвүүлэгч", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (547, "Dialogue box", "Харилцан ярианы хайрцаг", "", "Сонголтын талаар мэдээлэл хэрэгтэй үед эсвэл хувилбаруудыг сонгож байх үед гарч ирэх цонх. Жишээлбэл PRINT FILE гэсэн цонхыг сонгоход гарч ирэх харилцан ярианы цонх эсвэл PAGES PRINT дугаарууд руу хандахад гарч ирэх цонх.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (548, "Dial-up ", "Тооцоолууртай утсыг холбох", "", "Ердийн телефон утасны шугам ашиглан орон нутгийн компьютер терминал интернэт зэрэг алсын компьютерийн үйлчилгээг холбох. Ихэнх тохиолдолд компьютерийн системд байгууламж залгах нь тооцоолууртай утсыг холбох үйлчилгээ эрхэлж буй үйлчилгээ үзүүлэгчийн өгөх үйлчилгээ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (549, "Different backup", "Ялгавартай нөөц", "", "Сүүлийн бүрэн нөөцлөлтөөс хойшхи зөвхөн хувь өгөгдлийн файлууд өөрчлөгдсөнийг ялгавартй нөөц гэнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (550, "Differential detection", "", "An encoding and detection technique that uses phase changes in the carrier to signal binary 'ones' and 'zeros'. The signal is sampled every T seconds, and a phase change of 180 degrees could be set to be a 'zero' and no phase change would then be a 'one'.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (551, "Differential Quadrature Phase Shift Keying", "", "QPSK modulation using differential encoding of the digital information stream.", "", "DQPSK", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (552, "Diffraction", "", "A propagation phenomenon that allows radio waves to propagate beyond obstructions via secondary waves created by the obstruction. Classic types of diffractions are smooth earth and knife-edge.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (553, "Digest", "Товч мэдээлэл", "", "Захидлын жагсаалтанд нэг том захидал дах илгээж буй захидлаасаа сонгодог харин энэ нь нэг цагт тэдгээрийг бүгдийг нь хүлээн авах боломжтой.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (554, "Digit", "Уртын нэгж , нэг оронт тоо", "", "0 9 хүртэлх тоонуудын 6 бол нэг оронт тоо 422 бол 3 оронт тоо", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (555, "Digital", "Тоон", "", "Мэдээлэл нь хоёртын тооллын 0 ба 1 гэсэн импульсэн төсөөллөөр цувран холбогдсон. Аналог дохионы хувьд ялгаатай нь байнга өөрчлөгдөж байдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (556, "Digital", "", "Describes when information - speech, for example - is encoded before transmission using a binary code - discrete, non-continuous values. Digital networks are rapidly replacing analog ones as they offer improved sound quality, secure transmission and can handle data as well as voice. Digital networks include mobile systems GSM 900, GSM 1800, GSM 1900, D-AMPS and the cordless DECT system.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (557, "Digital audio tape", "Дижитал аудио хальс", "", "Соронзон хальс нь анх зөвхөн бичлэг дуу хөгжүүлж байсан. Гэвч одоо компьютерийн нөөцлөлтөнд илүү хэрэглэдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (558, "Digital camera", "Дижитал камер", "", "Зураг дардаг электрон камер буюу зураг нь диск дижитал файлууд дээр хадгалагддаг. Харин киноны хувьд арай илүү. Эдгээрийг компьютерт хадгалж үзэх засварлах хэвлэхэд ашиглаж болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (559, "Digital Communications System - 1800", "", "A delta spec to the GSM specification dealing with the aspects of operating in the 1800 MHz band with an increased number of RF channels. This spec also included features for pedestrian operation of small portable devices.", "", "DCS-1800", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (560, "Digital European Cordless Telecommunications", "", "A common standard for cordless personal telephony originally established by ETSI, a European standardization body. Standard based on a micro-cellular radio system that provides low-power cordless access between subscriber and base station up to a few hundred meters. Also known as DCT-900 and CT-3.", "", "DECT", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (561, "Digital image", "Дижитал зураг", "", "Дижитал хэлбэрээр хадгалагдсан зураг мөн тэдгээрийг компьютерт засварлах зэргийг хийж болно. Энэ нь дижитал камераар авагдсан нэг төрөл", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (562, "Digital Phase Modulation", "", "A form of CPM in which the shaped symbol pulses are directly applied to the phase modulator. This technique provides the advantages of CPM techniques and is easily implemented in VLSI. It is also easier to demodulate than other types of CPM.", "", "DPM", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (563, "Digital signature", "", "An electronic signature. A technology used to guarantee the reliability of information during electronic transactions. Digital signaturing is enabled through the application of open key encryption technology, and comprises electronic data verifying the identity of the user. A digital signature is created by coding data using an encryption key. Since only the user him/herself is in possession of the corresponding encryption key, the digital signature is essentially unforgeable. The digital signature is subsequently attached to data transmitted to another party to guarantee that the individual sending the message really is who he or she claims to be. [", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (564, "Digital subscriber line", "Хэрэглэгчийн тоон шугам", "", "Өндөр хурдтай компьютероос өгөгдлийг дамжуулах ердийн телефон утасны арга. Өргөн зурвасын интернэт гэж нийтлэг хэрэглэгддэг ба гэр болон бизнесд илүү хэрэглэгддэг..", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (565, "Digital to Analog Converter", "", "A device that takes a digital representation of a signal and transforms it into a facsimile of its original form.", "", "DAC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (566, "Digital versatile disk(DVD)", "Дижитал болон олон талын диск", "", "Урьд нь дижитал видео диск харин одоо дижитал болон олон талын диск, энэ нь үндсэндээ хурдан өгөгдөл дамжуулах томоохон хүчин чадал бүхий CD юм. Энэ нь видеог илүү удаан хийх боломжийг олгодог, жишээлбэл кино. Мөн DVD-г өгөгдлийн хадгалалтын хувьд ашиглаж болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (567, "Digital video", "Дижитал видео", "", "Дижитал видео соронзон хальсны нийтээр хүлээн зөвшөөрсөн стандарт", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (568, "Digital-Advanced Mobile Phone System", "", "Earlier designation of American standard for digital mobile telephony used primarily in North America, Latin America, Australia and parts of Russia and Asia. Also known as (North American) TDMA. See also TDMA and IS-136.", "", "D-AMPS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (569, "Dimension(1)", "Хэмжээс", "", "Програмчлалд массивийн бүтцэд хамааруулан хэрэглэнэ. Нэг хэмжээст массиваар жишээ жагсаалтыг харж болно, хоёр хэмжээст массиваар хүснэгтийн байдлаар нь дамжин багана мөрөөр нь харж болно. Массивын элемэнтүүд нь массив тус бүрээр нь тэмдэглэгээг илэрхийлдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (570, "Dimension(2)", "Хэмжээс", "", "Массивын бүтэц нэмж хэлбэл нэг хэмжээст массиваар жишээ жагсаалтыг харж болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (571, "Direct access", "Шууд хандалт", "", "Шууд хандалт нь ямар нэгэн зүйлийг хаанаас ч шууд татаж авч болдог, файл нь мэдэгдэж байгаа тохиолдолд байр сууриа өгсөн. Энэ нь ихэвчлэн зүйлс нь тодорхой урттай байх ёстойг илэрхийлнэ, тиймээс програм хангамж шаардлагатай зүйлс хаана байгааг тодорхойлж чадна. Шууд хандалтын файлуудыг ихэвчлэн санамсаргүй хандалтын файлууд гэж нэрлэдэг учир нь тэд нар  хадгалалтанд диск зэрэгт санамсаргүй боломж олгодог.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (572, "Direct distribution", "", "Use of a satellite link of the fixed-satellite service to relay broadcasting programmes from one or more points of origin, directly to terrestrial broadcasting stations without any intermediate distribution stages (possibly including other signals necessary for their operation).", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (573, "Direct Sequence", "", "A process of spectrum spreading where the digital information stream is multiplied, using an exclusive OR technique, by a high speed pseudorandom code (spreading sequence) to generate a spread spectrum signal.", "", "DS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (574, "Direct Sequence Spread Spectrum", "", "A type of spread spectrum modulation using a direct sequence technique to achieve spreading.", "", "DSSS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (575, "Disable", "Хориглох(блоклох)", "", "Ямар нэгэн юмыг ажиллах үйл явцыг зогсоох", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (576, "Discontinuous Transmission", "", "A feature in mobile systems where transmitters mute when there is no information to send, such as during periods of silence. This feature prolongs battery life in portable phones and reduces interference in wireless systems", "", "DTX", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (577, "Disinfect ", "", "", "Компьютерээс вирусыг устгах үйл явц", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (578, "Disk", "Диск", "", "Компьютерийн өгөгдөл болон програмыг хадгалах зориулалтай хэсгийг диск гэнэ.       ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (579, "Disk drive", "Дискний төхөөрөмж", "", "Диск нь бичих болон дискнээс уншихад зориулагдсан төхөөрөмж", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (580, "Disk pack", "", "", "Дискний багц.Олон диск уншихад зориулагдсан төхөөрөмж", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (581, "Dispersive channel", "", "A radio channel that not only introduces AWGN, but also the effects of multipath and frequency selective fading.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (582, "Display", "Дэлгэц", "", "Гаралтыг харуулах төхөөрөмж жишээ нь:монитор", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (583, "Distributed antenna system", "", "A type of antenna system that is distributed or remotely located away from the transmitter. Such an antenna or series of antennas can be connected via coaxial cable, leaky feeder or optical fiber link.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (584, "Diversity", "", "A technique to reduce the effects of fading by using multiple spatially separated antennas to take independent samples of the same signal at the same time. The theory is that the fading in these signals is uncorrelated and that the probability of all samples being below a threshold at a given instant is low.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (585, "Diversity reception", "", "A reception method in which one resultant signal is obtained from several received radio signals which convey the same information but for which the radio path or the transmission channel differs by at least one characteristic such as frequency, polarization, or the position or orientation of antennas.", "", "", "Note 1 – The quality of the resultant signal can be higher than that of the individual signals, due to the partial decorrelation of propagation conditions over the different radio paths or transmission channels. Note 2 – The term “time diversity” is sometimes used to refer to the repetition of a signal or part of a signal over a single radio path or transmission channel.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (586, "Document", "Бичиг баримт", "", "Програм ашиглан Ворд болон HTML хуудас дээр бичигдсэн текст", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (587, "Documentation", "Баримт бичиг", "", "Системийг ажиллуулах мэдээлэл. Хэрэглэгчид рүү чигэлсэн, програмыг хэрэглэхэд амархан болгоход  бэлтгэгдсэн заавар", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (588, "Domain", "Домайн(салбар. эзэмшил)", "", "Домайн нь интернэт хаягийн дэд олонлог юм. Домайн нь шаталсан хэлбэртэй, доод  болон дээд түвшиний гэж ангилдаг. Жишээ нь : .com, .org, .uk", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (589, "Domain name", "Домайн нэр", "", "Интернет дэх нөөцийн тодорхой хэсэг.эсвэл талбай нэр.  Байршил бүр өөрийн гэсэн өвөрмөц  домайн нэртэй.  Жишээ нь: bbc.co.uk хаягийг BBC эзэмшдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (590, "Domain name system (DNS)", "", "", "Домайн нэршилийн систем домайн нэршилийг ямар зохион байгуулалттайг тодорхойлдог. Домайн нэр нь компани  гэрээ хийж бүртгүүлж авдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (591, "Dongle", "Хамгаалах түлхүүр", "", "Програмыг хулгайлах магадлалыг багасгах төхөөрөмж. Ихэвчлэн компьютерийн стандарт оролтонд ордог.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (592, "Doppler Shift", "", "The magnitude of the change in the observed frequency of a wave due to the relative velocity of a transmitter with respect to a receiver.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (593, "DOS prompt ", "Диск үйлдлийн системийн промт-(сануулга)", "", "MS-DOS ашигладаг үйлдлийн систем", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (594, "Disk operating system", "Диск үйлдлийн систем", "", "Үйлдлийн систем жижиг хэсэг. Дискэн дээр хадгалагдсан програм болон файлуудад хандаж зохион байгуулах үйлдлийн системийн хэсэг.", "DOS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (595, "Dots per inch ", "Инч дэх цэгүүд", "", "Принтерийн нягтаршил нь DPI-аар хэмжигддэг. Цэг нт жижигхэн байх тусам ойрхон болж, зураг илүү тод болно.", "DPI", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (596, "Double click", "click-давхар товших ", "", "Хулгана дээр хурдан 2 товших.  Ямэр нэгэн зүйлийг сонгож нээдэг. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (597, "Double sideband", "2 хажуугийн зурвас", "", "Агуургийн модуляцын дээд, доод хажуугийн зурвасд хадгалагдан үлдсэн байдаг нь дамжуулалт зөөгчтэй холбоотой.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (598, "Double sideband", "", "Pertaining to a transmission or emissions where both the lower and upper sidebands resulting from amplitude modulation are preserved.", "", "DSB", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (599, "Double Spacing ", "Мөр хоорондын давхар зай", "", "Текст бүрийн мөр хоорондох давхар зай.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (600, "Down Time ", "Сул зогсолт(ашиглагдаагүй хугацаа)", "", "Компьютерийн систем хэрэглэгчид ашиглах боломжгүй болох  хугацаа. Энэ нь компьютер хадгалж байгаа эсвэл эвдэрсэн эсвэл үйлдлийн системийн алдаа болон холболтын алдаа гарсан тохиолдлууд юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (601, "Downlink", "", "See downlink.", "", "DL", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (602, "Downlink", "", "The transmission path from the base station down to the mobile station.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (603, "Down-link", "Буцах  шугам", "", "Дамжуулагч сансрын станц болон хүлээн авагч дэлхийн станцын хоорондох радио холбоос.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (604, "Down-link", "", "A radio link between a transmitting space station and a receiving earth station.", "", "", "Note 1 – The term is also used in terrestrial communications for a link between a transmitting base station and a receiving mobile station. Note 2 – The symbol ↓ is used as a subscript for letter symbols representing quantities associated with a down-link.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (605, "Download", "", "", "Нэгээс нөгөө компьютерт файл дамжуулах. Ерөнхийдөө үйлдлийг эхлүүлсэн хэрэглэгч файлыг хүлээж авах юм. Хэрэв хэрэглэгч ямар нэгэн юмыг илгээж байвал энэ нь upload болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (606, "Draft", "", "", "Draft mode-оор хэвлэх нь чанар муутай гардаг ч хурдан бас бага хор хэрэглэдэг.Бэлэн болоогүй загварыг хэвлэхэд тохиромжтой.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (607, "Draft", "", "", "Бичиг баримтын бэлэн болоогүй загвар ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (608, "Drag and drop", "", "", "Файлыг нэгээс нөгөө байрлал руу зөөх үйл явц буюу нарийвчилбал текст, зураг гэх мэт.  Зөөх файл дээрээ хулганы товчоо удаан дараад зөөх газраа аваачаад хулганыхаа товчийг тавина. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (609, "Drop down menu", "", "", "Харагдахуйц меню, гэхдээ зөвхөн сонгосон үед л гарч ирнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (610, "Drop shadow", "", "", "Объектэд тусгай график эффект оруулж 3-н хэмжээст мэт харагдах ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (611, "Drum ", "Хурд", "", "Лазер принтерийн хорыг цаасан дээр буулгах ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (612, "Dual band", "", "A term describing mobile phones that work on networks operating on different frequency bands. This is useful for mobile phone users who move between areas covered by different networks. For example GSM 900, GSM 1800. such as the 800 MHz digital band and the 1900 MHz digital PCS band.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (613, "Dual mode", "", "An industry term referring to a wireless device that can operate on either an analog or digital transmission network. However, multiple digital transmission systems exist, so dual-mode phone users must ensure that their dual-mode phone will operate on the digital transmission system used by their selected service provider.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (614, "Dubbing ", "", "", "Бичигдсэн бичлэг, дуу болон кинонд дуу оруулах жишээлбэл (яриа болон дуу) ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (615, "Ducting", "Сувагчлал", "", "Радио сувгийн агаар мандал нь дотроо радио долгион туйлшралын удардлагтай.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (616, "Ducting", "", "Guided propagation of radio waves inside a tropospheric radio-duct.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (617, "Dummy text", "Дууриамал текст", "", "Утгагүй текст нь вэб хуудсын загварын турш placeholder-г хэрэглэдэг. Текст оруулахын өмнө зохион байгуулалт ерөнхий төрхийг шалгадаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (618, "Dummy variable", "Дууриамал хувьсагч", "", "Хувьсагч нь програм дотроос гарч ирдэг гэвч аль нь ч хэрэглэгддэггүй. Програмчлалын хэл нь өгүүлбэр зүйн хувьсагч шаарддаг боловч бичигдэх мэдээлэл нь хэрэгтэй биш.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (619, "Duplex", "2 чиглэлийн ", "", "Өгөгдөл дамжуулах чиглэлийг илгээх. Магадгүй ижил цагт 1 зүгт 2 чиглэлтэй эсвэл бүтэн чиглэлтэй уулзах, цагт ганцхан 1 зүгт уулзах, хагас 2 чиглэлд  эсвэл ганцхан 1 зүгт  1 чиглэлд уулзах.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (620, "Duplex/full duplex", "", "Simultaneous two-way transmission, such as experienced in a phone conversation. In contrast, many speakerphones are half-duplex and will transmit in only one direction phone conversation. In contrast, many speakerphones are half-duplex and will transmit in only one direction - from the loudest noise - at a time.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (621, "Dynamic Channel Allocation", "", "An automatic process for assigning traffic channels in a frequency reuse wireless system. The base station continuously monitors the interference in all idle channels and makes an assignment using an algorithm that determines the channel that will produce the least amount of additional interference.", "", "DCA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (622, "Dynamic memory", "Динамик Санах Ой", "", "Санах ойны цахилгаан унтрах үед түүний агуулга алдагддаг. Жш нь: санах ойд санамсаргүй хандах.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (623, "Dynamic variable", "Динамик хувьсагч", "", "Динамик хувьсагч нь өөр өөр газар эзэмшиж байгаа өгөгдлийг олохын тулд заагчийг ашигладаг. Програмын хэмжээг хянаад өгөгдлийг хадгалдаг./Статик хувьсагч ялгаатай.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (624, "Earth station", "", "A station located either on the Earth’s surface or within the major portion of the Earth’s atmosphere and intended for communication:", "", "", "– with one or more space stations; or – with one or more stations of the same kind by means of one or more reflecting satellites or other objects in space.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (625, "Earth station ", "Газрын станц", "", "Энэхүү станц нь дэлхийн гадарга дээр болон дэлхийн агаар мандлын үндсэн хэсгийн аль нэгд байрладаг, харилцаа холбоонд зориулагдсан:  - Нэг эсвэл хэд хэдэн газрын станцууд   - Нэг буюу хэд хэдэн орон зайд хиймэл дагуул болон бусад обьектуудыг тусгах  ижил төрлийн  хэд хэдэн станц байна.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (626, "E-commerce", "Цахим худалдаа ", "", "Ийм жижиглэнгийн борлуулалт, хэвлэн нийтлэх зэрэг арилжааны ажиллуудад      интернэт                  ашигладаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (627, "Economic Area", "", "A geographic area over which a WCS operator is licensed to provide service. EAs are a group of counties in metropolitan areas having common financial, commercial and economic ties and were first used to license WCS service in the late '90s. EAs are about the size of a cellular MSA and cross state lines in some instances. EAs are used by the FCC to define areas of economic interest and are grouped into larger areas called REAGs.", "", "EA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (628, "Edit", "Засварлах", "", "Файлыг засварлах, өөрчлөх, шинэчлэх, түүний агуулгыг үстгах үйлдэл.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (629, "Edit", "Засварлах", "", "Мультимедиа, кино болон бусад Мультимедиа файл видео болон киног өөрчилдөг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (630, "Editing", "", "", "Файлын процессыг өөрчлөн засварлах, түүний агуулгыг үстгах", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (631, "Effect ", "Нөлөө", "", "График хэрэглэж болох онцгой нөлөө эргүүлж,тойруулж,буурдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (632, "Effective Isotropic Radiated Power", "", "A measure of the power in the main beam of an antenna relative to an isotropic radiator.", "", "EIRP", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (633, "Effective monopole radiated", "Хүчтэй  цацаргах чадал", "", "Антенн тухайн чиглэлд богино босоо антенн өөрийн гүйдэл харьцуулахад нийлүүлэх эрчим хүчний бүтээгдэхүүн.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (634, "Effective monopole radiated power", "", "The product of the power supplied to the antenna and its gain relative to a short vertical antenna in a given direction.", "", "e.m.r.p", "Note – The reference antenna, when fed with a power of 1 kW, is considered to radiate an e.m.r.p. of 1 kW in any direction in the perfectly conducting plane and produces a field strength of 300 mV/m at 1 km distance (equivalent to a c.m.f. of 300 V).");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (635, "Effective radiated power", "", "The product of the power supplied to the antenna and its gain relative to a half-wave dipole in a given direction.", "", "e.r.p", "Note – The reference antenna, when fed with a power of 1 kW, is considered to radiate an e.r.p. of 1 kW in any direction in the equatorial plane and produces a field strength of 222 mV/m at 1 km distance.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (636, "EIA Interim Standard 136 - NADC with Digital Control Channels", "", "The North American digital mobile telephony standard based on TDMA technology. It is the version of the TDMA specification resulting in a fully digital 2nd generation system and is backward compatible with analog AMPS. See also TDMA and D-AMPS.", "", "IS-136", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (637, "EIA Interim Standard 2000 (see cdma2000)", "", "A standard for current CDMA systems providing a migration path to 3G services.", "", "IS-2000", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (638, "EIA Interim Standard 95 (see cdmaOne)", "", "The original digital mobile telephony standard based on CDMA technology. See also CDMA.", "", "IS-95", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (639, "EIA Interim Standard for U.S. Digital Cellular", "", "Original TDMA digital standard. Implemented in 1992. This standard was the first to permit the use digital channels in AMPS systems. It used digital traffic channels but retained the use of analog control channels. This standard was replaced by the IS-136 digital standard in 1996.", "", "IS-54", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (640, "Eject", "Гаргах", "", "Шилжүүлэх(Зөөх) Жишээлбэл: Төхөөрөмжөөс дискийг гарга", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (641, "E-learning", "Цахим сургалт", "", "Цахим сургалт нь ICT-г хэрэглэх чөлөөтэй суралцахад дэмждэг. Онлайн болон алсын суралцах танхимыг үйл ажнллагааг сайжруулах нь хялбар програмууд, и-сургалт бүрдүүлж үйл ажнллагааг өргөн хүрээтэй байдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (642, "Electronic data interchange (EDI) ", "Өгөгдлийн электрон солилцох", "", "Компьютер систем уламжлалт үйл ажилгааны хооронд электрон системээр бизнес мэдээллийг солилцох(дамжуулах)ажиллагаа.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (643, "Electronic Industry Association", "", "A trade association and standards setting organization in the USA.", "", "EIA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (644, "Electronic mail ", "Цахилгаан шуудан ", "", "И-мэйл E-mail -тэй төстэй. Хэн нэгэн өөр нэгэн рүү тохирсон тоног төхөөрөмж болон E-mail хаягаар компьютер болон бусад төхөөрөмж рүү мессеж илгээх арга.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (645, "Electronic media ", "Цахилгаан хэвлэл мэдээллийн хэрэгсэл", "", "Тоон мэдээлэлд суурилсан харилцаа(холбоо) хэвлэх мэдээллийн хэрэгсэл.  Жишээлбэл: Нилээд их цаас хэвлэх, интэрнет, видео гэх мэт ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (646, "Electronic point-to-sale terminal ", "Цахим цэгийн борлуулалтын терминал", "", "Цахим цэгийн борлуулалтын терминал супермаркетын кассанд онцгой терминал хэрэглэдэг. Цахим цэгийн борлуулалтын терминал худалдан авагчид үйлдвэрлэх сүлжээнд мэдээллийн сангаас зардлыг авч нэгтгэсэн цахим хөрөнгийн шилжүүлэх, түүнчлэн бар код сканерт орж болно. Чиг үүрэг нь янз бүр байдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (647, "Electronic Serial Number", "", "A unique electronic identifier given to a mobile terminal. This number is used in verifying the identity of the mobile terminal.", "", "ESN", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (648, "Electronic whiteboard", "Цахим самбар", "", "Цахим самбарт холбож, зөвхөн компьютерийн дэлгэц агуулгыг харуулдаг, бас    хялбарчлахын тулд аль нэг тусгай үзэг хэрэглэж засварлаж самбарт агуулгыг    хөнгөвчлөхийн тулд хэрэглэгч хуруугаараа дэлгэцэнд хүрснээр холбогддог.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (649, "Element", "Элемент", "", "Дан өгөгдлийн хүснэгтэн мэдээлэл", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (650, "Elliptical polarization", "Богино туйлширал", "", "Огтойргуйн орон зайд радио долгионы цахилгаан орны хүчлэгийн вектор хэрхэн байрлсанаар радио долгионы туйлширал тодорхойлогдох бөгөөд  векторбайрлал нь туйлширлын чиглэлийг тодорхойлно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (651, "Elliptical polarization", "", "To be defined later.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (652, "Email ", "Захиа", "", "Хэн нэгэн өөр нэгэн рүү тохирсон тоног төхөөрөмж болон E-mail хаягаар компьютер болон бусад төхөөрөмж рүү мессеж илгээх арга.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (653, "Email account ", "Тайлбар", "", " Хүнд Email хэрэгтэй. Email тайлбар үүсэхийг интернэт үйлчилгээг   Буюу зохион байгуулагч компанид хүсэлт илгээнэ. Тухайн             тайлбарыг Email нөөцөд байрлуулах ба мэдээллийн системд орох хандах нэвтрэхийг хангаж өгдөг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (654, "Email notification ", "И-мэйл мэдэгдэл", "", "Тухайн mail-н онцгой үйл явдлыг мэдэгдэх мэдээллийг бий болгох, самбар дээр эмхтгэн шинээр постлоно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (655, "Email redirection", "Өөр хаягаас буцаж илгээх", "", "Нэг хаягаас илгээгээд өөр хаяг руу дамжуулдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (656, "Embed", "Суулгах", "", "HTML суулгахад тэмдэгтүүд нь <EMBED></EMBED> байрлуулсан эсвэл HTML баримт багтаагаад “EMBEDED” объект нь янз бүрийн боломж олгодог. Жишээлбэл: Кино суулгах байдлаар хаягуудыг ашиглах, вэб хуудас байрлуулж болно. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (657, "Emergency position-indicating radiobeacon station", "", "A station in the mobile service the emissions of which are intended to facilitate search and rescue operations.", "", "", "Note – The extension of this definition in the case of stations the emissions of which are intended to be relayed by satellite, needs further study.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (658, "Emission", "", "1. Radio-frequency radiation in the case where the source is a radio transmitter.  2. Radio waves or signals produced by a radio transmitting station. ", "", "", "Note 1 – For example, the energy from the local oscillator of a radio receiver if transferred to external space, is a radiation and not an emission. Note 2 – In radiocommunication, the French term “émission” applies only to intentional radiation.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (659, "Emoticon ", "Эмотикон", "", "Энгийн дүрс, ихэвчлэн тэмдэгтүүд нь хэд хэдэн нэгдэж бий болсон, мөн и-майл, мэссеж төрсөн сэтгэгдэл илэрхийлж байдаг. Эдгээр дүрсийг олон мэддэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (660, "Enable", "Зөвшөөрөх", "", "Ямар нэг ажил эхлэхийг зөвшөөрсөн дохио ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (661, "Encode", "Кодлох(Дугаарлах)", "", "Нууцлалтай адилхан. Өөрөөр хэлбэл: анхны нууц түлхүүргүйгээр(өгөгдөл                                тайлагдахгүйгээр) тухайн өгөгдөл  уншигдахгүй нууцлагдах буюу код тайлахыг хэлнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (662, "Encrypion ", "Шифрлэлт", "", "Мэдээлэлийг алгоритм (шифр) ашиглан түлхүүр гэгдэх тусгай мэдлэг ашиглахгүй бол уншигдах аргагүй болгох процесс. Уг процессийн дүнд шифрлэсэн мэдээлэл үүснэ. Шифрлэлтийг тусгай нууц мэдээллийг хадгалах, дамжуулахад ашигладаг. Шифрлэх ажилгааны гэдрэг процесс буюу шифрлэсэн мэдээлийг буцааж уншигдахуйц болгодог ажиллагааг шифр тайлалт (decryption) гэдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (663, "Encryption", "", "A cryptographic technique utilizing a digital key to scramble and hence 'lock' data in such a manner that it cannot be descrambled and decoded without the key.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (664, "Encryption key ", "Нууцлалтын түлхүүр ", "", "Нууцлалтын түлхүүр нь нууцлалтын үйл явцыг удирдахад хэрэглэгчээр сонгогдож байгаа үг эсвэл код юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (665, "End of file character ", "EOF тэмдэгт", "", "Тэмдэгт нь удирдлагын програмд файлийн төгсгөл гэх дохио хамгийн сүүлд бичэгдсэний дараа шууд файл руу үйлдлийн системээр бичддэг. Програмист нь файл дахь өгөгдлүүдийг боловсруулахад үүнийг ашигладаг ба боловсруулалт нь EOF тэмдэгт олдох хүртэл бичэгддэг. ", "EOF", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (666, "End tag ", "", "", "HTML – д веб хуудасанд хэрхэн форматлагдсаныг тодорхойлох кодын нэг хэсгийг tag  гэж нэрлэнэ. Tag нь хаалтан дотор байрладаг. Жишээ нь Нээх үед <> энэ тэмдэглэгээ, хаах үед </> тэмдэглэгээг ашиглана.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (667, "Enhanced Data for Global Evolution", "", "A technology that gives GSMA and TDMA similar capacity to handle services for the third generation of mobile telephony. EDGE was developed to enable the transmission of large amounts of data at a high speed, 384 kilobits per second. (It increases available time slots and data rates over existing wireless networks.)", "", "EDGE", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (668, "Enhanced Full Rate", "", "The second generation full rate speech codec used in GSM systems. This codec replaced the original RPE-LTP codec used in GSM systems. This codec employs ACELP technology.", "", "EFR", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (669, "Enhanced Specialized Mobile Radio", "", "The application of second generation wireless technology to the Specialized Mobile Radio Services.", "", "ESMR", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (670, "Enhanced TDMA", "", "A TDMA technique using Digital Speech Interpolation (DSI) and half rate coding to allow six calls to be carried in three time slots. The system does this by taking advantage of the 40% speech activity factor.", "", "E-TDMA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (671, "Enter", "Орох", "", "Компьютер команд явуулахад ашигладаг эсвэл word дээр догол мөр болон сонголт хийхэд ашиглана.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (672, "Enter ", "Тэмдэглэх", "", "Гараас өгөгдөл дамжуулахад ашиглана", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (673, "Entity ", "Бүтэц нэгж, бодит юм", "", "Өгөгдлийн санд өгөгдлийн сангийн хадгалж байгаа объектын нэрийг нэгж гэнэ. Жишээ нь: Үл хөдлөх хөрөнгө зуучлалын өгөгдлийн сангийн хувьд нэгж нь байшин, худалдан авагч байж болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (674, "Entity Relationship Diagram ", "Хамааралын диаграм", "", "Өгөгдлийн сан дахь хэд хэдэн нэгж хоорондын холбоог илэрхийлэх диаграм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (675, "Environment variable ", "Орчны хувьсагч", "", "Хувьсагч нь компьютерийн үйл ажилгааны орчинг тодорхойлно. Энгийн орчины хувьсагч нь үндсэн директор, файлын зам хайх команд, одоогийн байгаа цагийн бүс зэргийг тодорхойлно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (676, "Equalization", "", "Measures taken to reduce the distortion effects in a radio channel.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (677, "Equipment Identity Registe", "", "A database used by GSM and other second generation wireless systems used to identify the customer devices permitted to access the network. A device is usually placed in the EIR once its operation has been certified for the infrastructure in a laboratory or validation facility.", "", "EIR", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (678, "Equivalent (spot) noise temperature", "", "The amount by which at a given frequency the noise temperature of a one-port electrical network connected to the input of a given linear two-port electrical network would have to be increased, if the noise due to this two-port network was temporarily suppressed, in order to cause the noise power spectral density at the output frequency corresponding to input frequency, to be the same as that of the total noise of the one-port and two-port networks.", "", "", "Note 1 – This definition assumes that quantum phenomena are negligible. Note 2 – The equivalent spot noise temperature of a two-port network is dependent on the impedance of the one-port network connected to input.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (679, "Equivalent isotropically radiated power", "", "", "Антеннболон isotropic антенн (абсолютбуюу isotropic гүйдэл) харьцуулахад тухайн чиглэлд антенн нийлүүлсэн эрчим хүчний бүтээгдэхүүн.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (680, "Equivalent isotropically radiated power", "", "The product of the power supplied to the antenna and the antenna gain in a given direction relative to an isotropic antenna (absolute or isotropic gain).", "", "e.i.r.p.", "Note – The isotropic antenna, when fed with a power of 1 kW, is considered to provide an e.i.r.p. of 1 kW in all directions and to produce a field strength of 173 mV/m at 1 km distance.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (681, "Equivalent power flux-density", "", "Sum of the power flux-densities produced at a point on the Earth’s surface by all space stations within a non-geostationary-satellite system, taking into account the off-axis discrimination of a reference receiving antenna assumed to be pointing towards the geostationary-satellite orbit.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (682, "Ergonomic", "Эргономи", "", "Ажилын орчины судалгаа эргономи оффисын сандал ашиглахад тохиромжтой байдаг суухад эвтэйхэн эрүүл мэндэд ээлтэй байвал түүнийг хэлнэ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (683, "Ergonomic Keyboard ", "Эргономи гар", "", "Олон дахин давтагдсан ачаалалаас болж үүсэх гэмтэлийн (RSI) эрсдэлийг бууруулахад зориулсан тусгайлан загварчилсан гар. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (684, "Error ", "Алдаа", "", "Компютер юм уу хэрэглэгчийн гаргасан буруу үр дүн. Жишээ нь: програмд алдаа эсвэл буруу өгөгдөл оруулснаас болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (685, "Error correction", "", "The process of correcting errors in data transmitted over a radio channel using forward error correction (FEC) techniques.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (686, "Error distribution", "", "A description of how errors in a communications channel are distributed. Typical distributions are Gaussian (random) and Raleigh (bursty).", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (687, "Error message ", "Алдаа зурвас", "", "Ихэвчлэн алдаа нь шалтгааныг тайлбарлахдаа дэлгэцэн дээр гарч ирдэг. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (688, "Error probability", "", "A computation of the likelihood of an error involving the Probability Density Function (PDF).", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (689, "Error Trap ", "Алдаа", "", "Программ ажиллаж байх үед гарч буй алдааг шийддэг полграмын техник.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (690, "Error vector", "", "The error vector is the vector difference between a reference signal and a measured signal and is a complex quantity containing a magnitude and phase component.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (691, "Error Vector Magnitude", "", "EVM is a modulation quality metric widely used in digital RF communications systems. It is the root-mean-square (rms) value of the error vector over time at the instants of symbol clock transitions. Used properly, EVM and related measurements can pinpoint exactly the type of degradations present in a signal and can even help identify their sources", "", "EVM", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (692, "Esc key ", "Esc (Escape) товч", "", "Үйл ажиллагааг цуцлахад ашиглаж байгаа тусгай түлхүүр. Энэ нь программ хангамжаас хамаардаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (693, "Escape ", "Алдагдах", "", "Escape түлхүүр нь програмаас хамааралттай янз бүрийн эффект байна. Энэ нь ихэнхдээ үйлдэлийг цуцлахад ашиглагддаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (694, "Ethernet ", "Этернэт", "", "Этернэт нь дотоод сүлжээнд ашиглагддаг. Компьютерийн өөр өөр системд нэг зэрэг хэрэглэж болно. Дамжуулалын хурд нь 100мб .", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (695, "European Radio Message System", "", "A pan-European wide area paging network working in Europe, the Middle East and Asia.", "", "ERMES", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (696, "European Telecommunications Standard Institute", "", "The European standardization body for telecommunications.", "", "ETSI", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (697, "Evaluate ", "Дүгнэх", "", "Хувьсагчийн утга нь мэдэгдэж байгаа үед илэрхийлэлийн утагыг тооцоолох.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (698, "Event ", "Үйл явдал", "", "Үйлдлийн систем програм руу бичигдэж байгаа гадаад өөрчлөлт. Жишээ нь товчлуур  дарах мөн хулган дарах.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (699, "Executable File ", "Гүйцэтгэх файл", "", "Програм нь бүрэн ажиллаж дууссан гэдгийг харуулах хоёртын файл. Энэ нь компьютерийн ажилгаа болон гүйцэтгэл харуулах програм юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (700, "Execute", "Гүйцэтгэх", "", "Програмын гүйцэтгэлийг ажиллуулах явдал юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (701, "Exit ", "Гарах", "", "Дуудаж байгаа програмруу команд биелүүлж дуусахад салбар файйл хаагадахыг хэлнэ. Олон буцах заавартай байж болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (702, "Exocentric angle", "", "The angle formed by imaginary straight lines that join any two points with a specific point in space.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (703, "Expansion card ", "Өрөгтгөл карт", "", "Интерфасе боард процессорийн хүчин чадал бага үед нэмэлт слотод тохирч байхыг хэлнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (704, "Expansion slot ", "Өрөгтгөл слот", "", "Нэмэлт зүйл суулгах боломж олгодог. Жишээ нь: сүлжээний нэмэлт драйвер олон слот байвал компьютер олон зүйл нэмэх боломжтой.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (705, "Export", "Экспорт, оруулах", "", "Програс хангамжийн нэг хэсэгийг ашиглан өгөгдөлийн файл үүссэнээр програм хангамжийн өөр бусад хэсэгээр уншиж болохийг экспорт гэж нэхлэнэ. Импорт нь өөр програм хангамжаар үүсгэгдсэн файлыг зөвшөөрөхөд өшиглах үйл явц юм. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (706, "Extended Total Access Communications System", "", "The analog mobile phone network developed in the UK and available in Europe and Asia.", "", "ETACS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (707, "Extensible Hypertext Markup language ", "Гипертекст тэмдэглэгээний хэл", "", "HTML- н дараагийн үе XHTML болон XML – ийг багтаасныг хэлнэ.", "XHTML ", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (708, "Extensible Markup Language", "", "XML is a format for structured documents and data. It was developed by the World Wide Web Consortium (W3C). It is a meta-language, i.e. content is not directly encoded in XML but in a specific markup language defined using XML. It corresponds to the successor language for the current HTML. In contrast to HTML where tags are predefined, the XML user can freely extend a data format applying his or her own uniquely defined tags. Since the tag structure in the case of XML enables the computer to automatically analyze data content, building EC (electronic commerce) and EDI (electronic data interchange) systems is facilitated.", "", "XML", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (709, "Extensible Markup Language ", "", "", "Програм зохиож буй хэлний стандарт бүтэцийг тодорхойлно. Цахим хуудас ашигладаг хүмүүс XML – г хэрэглэдэг.", "XML", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (710, "Extract ", "Шахан гаргах", "", "Шахсан файлыг задалахыг хэлэнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (711, "Extranet", "", "The extension of a company's intranet out onto the Internet, e.g. to allow selected customers, suppliers and mobile workers to access the company's private data and applications via the World Wide Web. Generally an extranet implies real-time access through a firewall of some kind.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (712, "Extremely High Frequency", "", "The RF spectrum between 30 GHz and 300 GHz.", "", "EHF", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (713, "Eye diagram", "", "A superposition of segments of a received PAM signal displayed on an oscilloscope or similar instrument. The eye diagram is used to assess impairments in the radio channel.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (714, "Face to Face ", "Нүүр тулах", "", "Өөд өөдөөсөө харж харилцахыг хэлнэ. Жишээ нь : Утасаар ярих, и-мэйл илгээх нь Face to face биш юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (715, "Facilitator", "Хялбарчлах ,зохицуулагч                               ", "", "Хэрэглэгч нь мэйлээ идэвхитэй ажилууллахын тулд мэйлийн жагсаалт эсвэл зарлалын самбарыг хянадаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (716, "Fade", "Намдалт, намдах, сулрах, замхралт, унтралт, дууны түвшин тохируулах, дүрсийг алгуур өөрчлөх", "", "Пресентейшний дүрс бичлэгийн хуудас өгөгдлийн хуудас хооронд аажуу дараалалд оруулж эсвэл сүүлийн хуудсанд  шилжүүлнэ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (717, "Fading", "", "The variation in signal strength from it normal value. Fading is normally negative and can be either fast or slow. It is normally characterized by the distribution of fades, Gaussian, Rician, or Rayleigh.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (718, "False ", "Худал, буруу, алдаа мадагтай, хуурмаг, гажуу, хиймэл, дууриамал, зохиомол, найдваргүй", "", "Буулийн алгебр нь үнэн ба худал нь ихэвчлэн хоёртын тооллын 1 болон 0 гэсэн утгаар илэрхийлэгддэг", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (719, "Fast Associated Control Channel", "", "The channel derived by preempting information in a traffic channel. It is used to send handoff and similar messages.", "", "FACCH", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (720, "Fast fading", "", "The short term component associated with multipath propagation. It is influenced by the speed of the mobile terminal and the transmission bandwidth of the signal.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (721, "Fast forward", "Хурдан урагшлах", "", "Мультимедиа файлын дамжуулал нь хэвийн хурдаас илүү хурдан дамжуулана.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (722, "Fast packet switching", "", "An emerging, packet-orientated, digital technology that differs from traditional packet switching in a number of ways. The most obvious is that it transmits all data in a single packet format whether the information is video, voice or data. Fast packet switching uses short, fixed length packets (cells) and - via hardware switching - is capable of speeds between 100,000 and 1,000,000 packets/second.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (723, "Favorites", "Дэмждэг, урьтал болгодог", "", "Таны вэб хуудас үзүүлэгч программ дотор вэб хуудас эсвэл вэб байршлын  холбоосыг хадгалах арга. Bookmark нь Neetscape-аар ашиглагддаг нэр томъёо бөгөөд ихэнхдээ Internet Explorer-ийг ашиглагдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (724, "Fax", "Факс", "", "Богино дамжуулал ба телефон утасны шугам ашиглаж график болон текст оруулж болох ба мөн баримт бичгийн хуулбарыг илгээнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (725, "Fax modem", "Факс модем", "", "Факс модем нь шууд факс илгээх нь телефон утасны шугамд холбогдсон компьютерт олгодог ба модем нь тусгай төрлийн байна. Энэ нь цаасан дээр өгөгдлийг гаргахгүй бөгөөд факс хүлээн авахад  оператор түүнийг боловсруулах болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (726, "Feasibility study", "Техник эдийн засгийн үндэслэл", "", "Асуудлыг урьдчилсан судлах боломжтой шийдвэр гаргахад ашигладаг бөгөөд энэ нь ямар ч байдлаар хийж болно. Энэ системийн дүн шинжилгээ хийх нь их энгийн түвшинд хийгддэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (727, "Federal Communications Commission", "", "Regulatory body governing communications technologies in the US. established by the Communications Act of 1934, as amended, and regulates interstate communications (wire, radio, telephone, telegraph and telecommunications) originating in the United States.", "", "FCC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (728, "Feed", "Хөтлөгч, тэжээл, тэжээх, өгөх, ажиллуулах, өдөөх схем                                                                                                                                                    ", "", "Хэвлэлтэд ашиглагддаг нэр томъёог цаас хэвлэгч руу оруулах үед ашиглагддаг", "", "");
