INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (730, "Feeder link", "Салаа холбоос", "", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (731, "Feeder link", "", "A radio link from an earth station at a given location to a space station, or vice versa, conveying information for a space radiocommunication service other than for the fixed-satellite service. The given location may be at a specified point, or at any fixed point within specified areas.", "", "", "Note – Examples of feeder links: – an up-link for a broadcasting satellite; – a down-link for a data collection or Earth exploration satellite; – an up-link and down-link between a coast earth station and a satellite in the maritime mobile-satellite service.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (732, "Fetch-execute cycle", "Нөхөж-биелүүлэх цикл", "", "Төв боловсруулах хяналтын нэгж, дарааллаар нь тус бүр хичээлыг сүлжээнээс татаж нөхдөг декод болон компьютерийн бусад хэсэгт дохио илгээж ажиллуулж өмнө зохицуулдаг Энэ нь нөхөж-биелүүлэх цикл гэж нэрлэдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (733, "Fiber optic cable", "Шилэн кабель", "", "Модуляцлан гэрэл  ашиглан өгөгдлийг хурдан дамжуулах боломжийг олгодог маш нарийн шилэн хэлхээ.Энэ нь нэг буюу хэд хэдэн мэдээллийн дохио явуулах чадвартай. шилэн кабелийн хөндлөнгийн шуугиан бага, аюулгүй өгөгдөл дамжуулах боломжийг олгодог бөгөөд, металл утаснас ялгаатай нь зэвэрдэг зүйл биш юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (734, "Field", "Талбай, талбар, салбар, муж хэсэг, төрөл, салбар, хүрээ", "", "Талбарын нэг тодорхой зүйлийг эзэмшиж байгаа мэдээ баримтын хэсэг. Жишээ нь: номын сангийн номын файл ном тус бүрийн хувьд нэг баримтжуулсан байх болно, мөн мэдээ баримтад ном тус бүрийх нь нэр, үнэ, хуудасны тоо.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (735, "Field name", "Талбарын нэр", "", "Талбарыг илэрхийлсэн нэр,  гарчиг нь тухайн талбарын нэр байна тэхээр өгөгдлийн талбар нь гарчгаараа илэрхийлсэн байна . Ноён Хадагтай гэх мэт.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (736, "Field type ", "Талбайн төрөл", "", "Тухайн талбарын төрөлд зохион байгуулагдсан өгөгдлийн төрөл, жишээ нь: номер, үсэг, мэдээлэл", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (737, "Field width ", "Талбайн өргөн", "", "Зөвшөөрөгдөх тоон тэмдэгтийн хамгийн их утга", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (738, "Fifth-generation computer", "Тав дахь үеийн компьютер", "", "Компьютерийн системийг төлөвлөх, хэрэглэх нь  өөр арга зам нь алсын хараа.Энэ нь шинэ компьютерийн архитектур болон програм хангамжийн төрлийн аль аль нь орно, энэ үзэгдлийн зарим нь хүрч байна.Энэ нь хүмүүстэй харилцаж чадна виртуал бодит байдал, шинжээч систем, байгалийн хэлний интерфэйсүүд дээр төвлөрч байна.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (739, "File", "Файл , баримт цуглуулга , архив", "", "Өгөгдлийн  цуглуулга компьютерт хадгалж, нэг хэсгийг боловсруулдаг", "", "");
