INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (741, "File ( or search )", "Хайх", "", "Мэдээлэл дэх онцгой үг ба үсгийг хайж олох төхөөрөмж. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (742, "File allocation table (FAT) ", "Файл хуваарилах хүснэгт", "", "Файлууд арын нөөцөд хадгалагдаж байгаа бол энэ талаарх дэлгэрэнгүй мэдээлэл өөрт нь байдаг бөгөөд энэ нь файл хуваарилах хүснэгтэд хадгалагдаж байгаа файл нь мөн нөөцө дээр хадгалагдаж байдаг", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (743, "File attribute ", "файлын шинж чанар", "", "Энэ нь өөрчилж болно  жишээ нь файл уруу өгч болно шинж чанар, файл, бичих нь өөрчилж болохгүй учраас зөвхөн унших, эсвэл уншиж болно далд. бусад далд шинж чанарууд нь, систем болон архив зэрэг орно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (744, "File format ", "Файлын формат", "", "Мэдээлэл файлд хадгалагдаж байгаа нь маш олон Арга зам хэлбэр байдаг.   текст боловсруулагч удирдлагын  хүснэгттэй хэл өөр хэлбэрээр Өгөгдлийн хадгалдаг", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (745, "File handling ", "Файлын боловсруулалт", "", "Нээх, тэдгээрийг хаах, жишээ нь, ямар нэг байдлаар файлуудыг удирдах тушаалууд  програм нь ерөнхий нэр томъёо.", "", "");
