INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (748, "File name extension 1 ", "Файлын нэрийн өргөтгөл", "", "файлын нэр нь ихэвчлэн файлын төрлийг илэрхийлсэн нь бүрэн зогсоох хойш өргөтгөх, олон тооны, эсвэл тэмдэгтүүдтэй байна,жишээ нь  EXE энэ excel гэсэн үг, xls нь хүснэгттэй ажилладаг файл юм,doc энэ нь үг боловсруулсан файл юм .", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (749, "File name extension 2 ", "Файлын нэрийн өргөтгөл", "", "Файлын нэр, тэд файлын төрлийг заана дээр нь өргөтгөлтэй байдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (750, "File operations ", "Файлын агуулах зүйл", "", "Зарим нэг арга, жишээлбэл нээх файл удирдахыг, тэдгээрийг хаах хэл дахь тушаалууд гэдэг програм нь ерөнхий нэр томъёо", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (751, "File path ", "Файлын зам", "", "Энэ файлын нэр, гадна диск дээр удирдах хэрэгсэл (хавтас) дамжуулан энэ замыг нь зөвхөн жагсаан, файлын байршлыг өгдөг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (752, "File server ", "Файл сервер", "", "Файл сервер сүлжээн дээр хэрэглэгчдэд зориулсан төв диск хадгалах болно. файл нь серверийн програм хангамж бүрийн хэрэглэгчдийг тодорхойлно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (753, "File transfer protocol (FTP) ", "Файл дамжуулах протокол", "", "Компьютер хоорондын дамжуулж буй файлын хуулбар мессеж илгээхийг хэрэглэгчид зөвшөөрөх. Файл дамжуулах протокол нь компьютер хоорондын мессежүүд болон бүлгүүд дэх (мөн блок гэж нэрэлдэг) файлын хуулбарыг илгээх явцыг хянах бөгөөд алдаа зааж буй өгөгдлийг мөн шалгадаг. Интернет дээр файлуудыг байрлуулах боломжтой болгохын тулд нэг төхөөрөмжөөс нөгөө рүү ийнхүү дамжуулж байгаа юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (754, "File type ", "Файлын төрөл ", "", "Файлд хадгалагдах болно. Жишээ нь: Гүйцэтгэж болохуйц програм, өгөгдөл тодорхой аппликашинд зориулж бүтэцтэй болгоно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (755, "Fill colour", "Өнгөөр будах ", "", "Сонгосон өнгөөрөө график дэх дүрсийг будах.", "", "");
