INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (757, "Find", "Олох", "", "Дискэнд байгаа болон файл дах зарим мэдээлэлийг олох. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (758, "Find and replace", "Хайх, солих", "", "Мэдээлэл дэх тодорхой текстийг олох, нэг нэрийг өөрөөр солих.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (759, "Finite Impulse Response", "", "A technique used to characterize electrical circuits and networks in the time domain.", "", "FIR", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (760, "Firewall", "Галт хана ", "", "Компьютерийн системд гадны хэрэглэгчид зөвшөөрөлгүй хандалт хийхээс сэргийлсэн програм. Гадны хэрэглэгч рүү явуулсан ба ирсэн мэдээллийг хязгаарладаг. Жишээ нь: мэдээллийг тодорхой төрлүүдийг блоклон компьютероос зөвхөн зөвшөөрөгдсөн хандалт эсвэл хэрэглэгчийн нэмэлт тодорхойлолтыг шаарддаг. Ихэвчлэн бүрэн итгэмжлэгдсэн сервер хэрэглэнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (761, "First in first out (FIFO 1)", "", "", "Устгасан зүйл ба нэмсэн зүйлсийн жагсаалт. Энэ нь ирсэн мэдээлэлтэй хамаатай.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (762, "First-generation computer", "Анхны үеийн компьютер ", "", "Компьютерын анхны төрөл. Тэд хавхлага, мөнгөн ус, удаашруулах шугам, электростатик ба хязгаарлалттай санах ойд ашиглагддаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (763, "Flame ", "", "", "Ихэвчлэн и-мэйл листэнд байдаг, группийн бусад гишүүд рүү халдана.", "", "");
