INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (766, "Flat file database", "Мэдээллийн сан ", "", "Ганц файлыг хадгална. Мэдээлэл мөр нь бичиг баримт, багана нь талбай бүрт байрлана. Энэ зөвхөн маш амархан мэдээллийг зөвшөөрч, 2 хэмжээст хүснэгтийг авч үздэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (767, "Flat screen", "Хавтгай дэлгэц", "", "Дэлгэц маш нимгэн CRT дэлгэцнээс илүү өөр технологийг ашигладаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (768, "Float type", "Float төрөл ", "", "Хадгалах float дугаарт ашиглагдана.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (769, "Floating- point numbers ", "Хөдөлгөөн цэгэн тоонууд ", "", "Аравтын бутархай эсвэл хосолсон чанартай бүхэл тоонууд.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (770, "Floppy disk", "Уян диск", "", "Мэдээллийг хадгалхад зориулагдсан салгадаг соронзон диск ихэнхдээ нэг комьпютерээс нөгөөд жижиг файлууд зөөхөд ашиглагддаг. Нэг удаад 5,25-8 инч хэлбэртэй байх боломжтой. Өнөөдөр уян диск нь 3.5 инчийн хатуу хайрцагтай зөвхөн 1,44 мегабатын багтаамжтай нь худалдаанд гарч байна.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (771, "Flowchart ", "", "", "Програм болон системийн загварт үйлдэл хийх, хадгалах гэх мэт үйл явцын бүдүүвчилсэн дүрслэл бөгөөд эдгээр алхмууд нь үйл явцыг бүрэн гүйцэд болгоход ашигладаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (772, "Folder", "Хавтас  буюу фолдер ", "", "Файлуудыг эмх цэгцтэй байлгахын тулд нээдэг бөгөөд нэг төрлийн холбоотой файлууд нэг фолдер дотор хийдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (773, "Font", "Фонт ", "", "Мэдээллийг бичихдээ ашиглах тэмдэгтүүд болон хэлбэр дүрсний тохиргоо. Times New Roman, Arial гэх мэт маш олон төрлийн фонтууд байдаг бөгөөд зарим нь маш нарийн хийцтэй гоёмсог, зарим нь маш энгийн байх жишээтэй.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (774, "Footer", "Хөл бичих", "", "Бичвэрийн хуудас бүрийн доор байрлуулсан бичвэр.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (775, "Foreground", "Нүүрний цонх", "", "Гар доорх хэрэглээний апликешн бөгөөд хэрэглэгч хэрэглэхэд амар хялбар байдлаар байршуулсан цонх. Комьпютерийн идэвхитэй цонх юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (776, "Form", "Маягт ", "", "Вэб загвар, өгөгдлийн сангууд болон бусад электроникийн хэрэгсэлд форм нь хэрэглэгч мэдээлэлд нэвтрэхийг зөвшөөрөх тавцангийн нэг хэлбэр юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (777, "Formal parameter ", "Формал параметр ", "", "Функц болон үйл явцад ашиглагддаг дуудлагын програмд мэдээллийг холбох үүргийг гүйцэтгэдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (778, "Formal specification ", "Формал /албан ёсоны / зааварчилгаа ", "", "Системийн загварт формал зааварчилгааны хүрээ нь системийн тусгай хэсгүүдийн бүтэц.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (779, "Format", "Формат ", "", "Тектийн харагдах байдлын өөрчлөх. Жишээ нь: Bold, italic", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (780, "Format", "Формат ", "", "Үйл ажиллагааны системийн үед болон үйл ажиллагааны систем нь диск дээр өөр байдлаар мэдээллийг хадгалах гэх мэт шинж тэмдэг илрэх үед дискийг форматлах шаардлагатай болдог. Дискийг форматлахын тулд дискийн зохион байгуулах дэс дараалалд оруулах замуудыг ашигладаг. Тэдгээр зам бүр нь тэнцүү урттай блокуудаас бүрддэг /мөн сектор гэж дууддаг/. Харин сектор бүр нь замуудын жижиг хаяг бүхий хэсгүүдээс бүрдэх ба тэдгээр жижиг хэсгүүд нь дискийг уншсан, бичсин мэдээллүүдийн багтаасан хамгийн жижиг хэсэг юм. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (781, "Forward Error Correction", "", "An encoding technique that allows a limited number of errors in digital stream to be corrected based on knowledge of the encoding scheme used.", "", "FEC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (782, "Forward link", "", "See downlink.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (783, "Frame Alignment Word", "", "A unique digital word used by codecs to allow them to resynchronize to the framing structure in the event of errors.", "", "FAW", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (784, "Frame Erasure/Error Rate", "", "A measure of the number of frames of data that contained errors and could not be processed. FER is usually expressed as a percentage or exponent.", "", "FER", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (785, "Framing", "", "A technique used in digital communications systems for organizing the transmitted data into regular patterns so that the various logical channels in the data stream can be detected and processed.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (786, "Free space basic transmission loss", "", "The transmission loss that would occur if the antennas were replaced by isotropic antennas located in a perfectly dielectric, homogeneous, isotropic and unlimited environment, the distance between the antennas being retained.", "", "", "Note – If the distance d between the antennas is much greater than the wavelength λ, the free space attenuation in decibels will be: ⎟ ⎠ ⎞ ⎜ ⎝ ⎛ λ π = d Lbf 4 20 lg dB");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (787, "Free-space propagation", "", "Propagation of an electromagnetic wave in a homogeneous ideal dielectric medium which may be considered of infinite extent in all directions.", "", "", "Note – For propagation in free space, the magnitude of each vector of the electromagnetic field in any given direction from the source beyond a suitable distance determined by the size of the source and the wavelength is proportional to the reciprocal of the distance from the source.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (788, "Free-space-propagation", "Чөлөөт орон зайн тархалт ", "", "Цахилгаан соронзон долгионы тархалт нь нэгэн төрлийн тусгаарлагч дунд хязгааргүй, бүх чиглэлд хийсвэр түвшинтэй  тусгагдсан байж магадгүй.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (789, "Frequency Correction Channel", "", "A logical channel in GSM systems used to transmit a frequency correction data burst of all 'zeros'. The resulting frequency shift seen by the mobile is then used for frequency correction.", "", "FCCH", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (790, "Frequency diversity", "", "The simultaneous use of multiple frequencies to transmit of information. This is a technique used to overcome the effects of multipath fading, since the wavelength for different frequencies result in different and uncorrelated fading characteristics.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (791, "Frequency diversity reception", "", "Diversity reception in which several radio channels are used with appropriate frequency separations.", "", "", "Note – If the channels are situated in different frequency bands, the frequency diversity is said to be “cross-band diversity”.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (792, "Frequency Division Duplex", "", "Radio technology using a paired spectrum. Used in cellular communication systems such as GSM.", "", "FDD", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (793, "Frequency Division Multiple Access", "", "Method of allowing multiple users to share the radio frequency spectrum by assigning each active user an individual frequency channel. In this practice, users are dynamically allocated a group of frequencies so that the apparent availability is greater than the number of channels.", "", "FDMA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (794, "Frequency Hopped Multiple Access", "", "A set of frequency hopping communicators operating as a system to provide communications services. All communicators traditionally use the same set of carrier frequencies and coordinate their hopping sequences to minimize interference in the network.", "", "FHMA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (795, "Frequency Hopped Spread Spectrum", "", "A spectrum spreading technique using an RF carrier hopped across a large number of RF channels using a random or pseudorandom code to determine the sequence of channels used.", "", "FHSS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (796, "Frequency Hopping", "", "A periodic changing of frequency or frequency set associated with transmission. A sequence of modulated pulses having a pseudorandom selection of carrier frequencies.", "", "FH", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (797, "Frequency Modulation", "", "A form of angle modulation in which the instantaneous frequency of a sine-wave carrier is caused to depart from the carrier frequency by an amount proportional to the instantaneous value of the modulating wave.", "", "FM", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (798, "Frequency reuse", "", "A technique of reusing frequencies and channels within a communications system to improve capacity and spectral efficiency. Frequency reuse generally utilizes regular reuse patterns.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (799, "Frequency re-use satellite network", "", "A satellite network in which the satellite utilizes the same frequency band more than once, by means of antenna polarization discrimination, or by multiple antenna beams, or both.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (800, "Frequency selective fading", "", "A type of signal fading occurring over a small group of frequencies caused by a strong multipath component at those frequencies.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (801, "Frequency Shift Keying", "", "A form of modulation using multiple carrier frequencies to carry the digital information. The most common is the two frequency FSK system using the two frequencies to carry the binary ones and zeros.", "", "FSK", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (802, "Frequency standard", "", "A generator, the output of which is used as a frequency reference.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (803, "Frequency tolerance", "", "The maximum permissible departure by the centre frequency of the frequency band occupied by an emission from the assigned frequency or, by the characteristic frequency of an emission from the reference frequency.", "", "", "Note – The frequency tolerance is expressed in parts in 106 or in hertz.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (804, "Full carrier", "", "Pertaining to a transmission or emission with amplitude modulation where, by convention, the power of the sinusoidal carrier component is no more than 6 dB below the peak envelope power. ", "", "", "Note 1 – Double-sideband amplitude-modulated emissions normally comprise a full carrier with a power level exactly 6 dB below the peak envelope power at 100% modulation. Note 2 – In single-sideband full-carrier emissions, a carrier at a power level of 6 dB below the peak envelope power is emitted, to enable the use of a receiver designed for double-sideband full-carrier operation.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (805, "Full carrier ", "зөөгч дохио", "", "дулааны урсгал, синуслэг зөөгч бүрэлдэхүүн хэсгийн эрчим хүчний ямар ч илүү 6dB-эс өндөр хүчдэл доогуур, далайцын модуляцнь дамжуулах эсвэл цацрагахтайхолбоотой. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (806, "full rate", "", "The term commonly applied to voice codecs in a communications system. Most frame formats are designed to accommodate full and half-rate channels, with the intention of implementing half-rate coding as the technology permits to double the capacity of the system. The full-rate codec uses all of the time-slots available.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (807, "Gateway GPRS Support Node", "", "A gateway from a cellular network to an IP network.", "", "GGSN", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (808, "Gaussian channel", "", "An RF communications channel having the properties of wide-band uniform noise spectral density resulting in a random distribution of errors in the channel.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (809, "Gaussian Minimum Shift Keying", "", "A modulation technique involving Gaussian filtering of the input data prior to its application to the phase modulator. This results in a narrow occupied spectrum and better adjacent channel interference performance.", "", "GMSK", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (810, "General Packet Radio Service", "", "A packet-linked technology that enables high-speed (115 kilobit per second) wireless Internet and other data communications over a GSM network. It is considered an efficient use of limited bandwidth and is particularly suited for sending and receiving small bursts of data.", "", "GPRS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (811, "Geocentric angle", "", "The angle formed by imaginary straight lines that join any two points with the centre of the Earth.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (812, "Geostationary satellite", "", "A stationary satellite having the Earth as its primary body.", "", "", "Note – A geostationary satellite remains approximately fixed relative to the Earth");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (813, "Geostationary-satellite orbit", "", "The unique orbit of all geostationary satellites.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (814, "Geosynchronous satellite", "", "A synchronous Earth satellite.", "", "", "Note – The sidereal period of rotation of the Earth is about 23 hours 56 minutes.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (815, "Gigabyte", "Гигабайт ", "", "1024 мегабайт  хэмжээтэй дискний зай эсвэл компьютерийн санах ойн хэмжээ. Товчилсон хэлбэр нь Gb.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (816, "Gigahertz", "", "A frequency measurement which equals one billion hertz.", "", "GHz", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (817, "Global change", "", "", "Баримт бичиг эсвэл файлын үр дагаварыг нийтэд нь өөрчлөх. Жишээ нь: Presentatin-д байгаа slide master-ийг өөрчлөх. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (818, "Global Positioning System", "", "A worldwide radio-navigation system that was developed by the US. Department of Defense. In addition to military purposes it is widely used in marine and terrestrial navigation (for example car navigation systems).", "", "GPS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (819, "Global System for Mobile Communication", "", "Originally developed as a pan-European standard for digital mobile telephony, GSM has become the world's most widely used mobile system. It is used on the 900 MHz and 1800 MHz frequencies in Europe, Asia and Australia, and the MHz 1900 frequency in North America and Latin America.", "", "GSM", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (820, "Global variable", "", "", "Global variable нь: программын хаана ч хэрэглэгдэнэ. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (821, "Go ", "", "", "Ихэнхи  Apple –ийн компьютерт байдаг  цэсийн сонголт. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (822, "Grade of Service", "", "A measure of the success a subscriber is expected to have in accessing a network to complete a call. The grade of service is usually expressed as percentage of calls attempted by the subscriber during the busy-hour that are blocked due to insufficient network resources.", "", "GOS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (823, "Grammar checker", "Алдаа шалгагч ", "", "Алдаа  шалгагч нь баримт бичиг дэх дүрмийн алдааг илрүүлдэг бөгөөд word зэрэг программд байдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (824, "Graph", "График ", "", "Бүлэг тоонуудыг илэрхийлэхээр хийгдсэн зургийн хэлбэр. Энгийн жагсаалт тоонуудыг ойлгоход хэцүү үед график нь тэднийг хэрэглэгчидэд ойлгоход тусалдаг. Алдартай графикийн төлбөрүүд:", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (825, "Graphical user interface(GUI) ", "Хэрэглэгчийн график интерфейс", "", "Microsoft Windows график хэрэглэгчийн харагдах байдлын хамгийн энгийн жишээ ба системийн цонхнууд болон  Menu, icon-ууд нь хэрэглэгчийг компьютертай харилцахад амар болгож өгдөг. GUI гарж ирэхээс өмнө ихэнхи хэрэглэгчийн харагдах байдал голдуу бичмэл хэлбэртэй байсан.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (826, "Graphics ", "", "", " Гэрэл зурагийг график зураг болгон шинээр  өөрчлөх боломжийг олгодог програм хангамж.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (827, "Graphics interchange format", "", "", "Графикт ашиглагддаг файлын формат", "GIF ", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (828, "Graphics plotter ", "", "", "Инженер болон архитектoрууд ихээр ашигладаг. Зураг  төслийг дизайнлан бий болгоход зориулагдсан төхөөрөмж.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (829, "Graphics tablet ", "", "", "Компьютерлуу зургийг оруулахад хэрэглэгддэг төхөөрөмж. Пад эсвэл таблэт дээр хэрэглэгч зүүгээр зурж эсвэл зургийг хуулж оруулдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (830, "Grid ", "Тор ,сүлжээ ", "", "Төхөөрөмж эсвэл word process-ийн хүснэгтэд өгөгдлийн мөр баганан хүснэгт болгон оруулахыг grid гэнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (831, "Gridline", "", "", "Төхөөрөмж болон word processing хүснэгтэд gridline гэдэг нь: мөр болон баганыг хязгаарлаж буй зураасууд юм. Ихэвчлэн gridline- ийг харагдах болон харагдахгүй байлгаж болдог. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (832, "Ground wave", "Газрын долгион ", "", "Дэлхийнагаар мандалын радио долгион нь гол төлөв диффракцын ойролцоо газар дээрх төлөв байдалыг ихэнхдээ тодорхойлдог.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (833, "Ground wave", "", "A radio wave basically determined by the properties of the ground which propagates in the troposphere and which is mainly due to diffraction around the Earth.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (834, "Groupware", "", "", "Хүмүүс  өөр өөр байрлалд байгаа хэдий ч хамт ажиллах боломжыг олгодог программ.  Groupware гэдэг үйлчилгээ нь  e-mail  дамжуулах өгөгдөл хооронд дамжуулах, электрон уулзалт хийх боломжийг олгодог. Electronic meeting  Гэдэг нь бүх л хүмүүс өөрсдийнхөө мэдээллийг бусдад үзүүлэн өөр өөр үйл ажиллагаануудийг явуулж болдог.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (835, "Guard band", "", "A set of frequencies or band-width used to prevent adjacent systems from interfering with each other. Guard bands are typically used between different types of systems at the edges of the frequency allocations.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (836, "Hacker", "Компьютерийн  програмд зөвшөөрөлгүй нэвтрэгч ", "", "Хэн нэгэн компьютерийн систем рүү зөвшөөрөлгүй хандах оролдлого хийхийг хэлнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (837, "Hacking ", "", "", "Зөвшөөрөлгүйгээр нэвтрэх эрхийг олж авахыг оролдохыг хэлнэ. Зөвшөөрөлгүй компьютерийг ашиглан компьютерт хадгалагдсан өгөгдлийн сан болон ерөнхий програмуудад зөвшөөрөлгүй нэвтрэхийг хэлнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (838, "Half duplex", "", "", "Өгөгдөл дамжуулалтын чиглэлийг илэрхийлэх. Duplex  болон full duplex  гэж 2 нэрлэгдэнэ. Эдгээр нь нэг цаг хугацаанд бүх чиглэлд явуулах боломжтой. Хагас  duplex нь тухайн цаг хугацаанд ганцхан чиглэлд дамжуулдаг. Зөвхөн нэг чиглэлд дамжуулахыг  simplex гэнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (839, "Half rate", "", "The term commonly applied to voice codecs in a communications system. Most frame formats are designed to accommodate full and half-rate channels, with the intention of implementing half-rate coding as the technology permits to double system capacity. The halfrate codec uses only half of the time-slots in the frame.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (840, "Hamming code", "", "A well known simple class of block codes capable of detecting up to two errors and correcting one. Although not particularly powerful, they are one of the 'perfect' codes in that its standard array has all of the error patterns that can exist for single errors.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (841, "Hand disk ", "Хатуу диск ", "", "Компьютерт байгаа хэвийн бус хөдөлгөөнгүй суурин диск. Хатуу диск уян дискийг бодвол илүү их хэмжээг хадгалдаг бөгөөд илүү өндөр хурдтай.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (842, "Hand-held scanner", "Гар сканнер", "", "Энэ сканнер нь гэрлийн туяа ашиглаж зургийг илрүүлж түүнийгээ хуулбарлан компьютерт хадгалдаг төхөөрөмж. Hand-held  сканнерийг ашиглаж байгаа үед бичиг баримтны дээгүүр хөдөлгөх ёстой.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (843, "Hand-off", "", "The process of transferring a call in progress from the current base station to another without interruption as the user moves out of range of the current base station.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (844, "Hand-over", "", "The passing of a call signal from one base station to the next as the user moves out of range or the network software re-routes the call.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (845, "Handshake", "", "", "Компьютер болон принтер лүү өгөгдлийг дамжуулах, өгөгдлийг хүлээн авах болон  илгээдэг холболтын систем юм. Дамжуулал эхлэхээс өмнө дамжуулах болон хүлээн авах тал бэлэн гэдгийг мэдэгдэж буй аргуудыг handshake гэнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (846, "Handwriting recognition", "", "", "Гар бичвэрээр дүгнэх нь компьютерийн процессорт орох  үед компьютерийн санд аль хэдийн үр дүн нь харицуулагдсан байдаг. Энгийн систем нь характеристикийг салган хэрэглэгч рүү чиглэсэн байдаг. Хэдийгээр зарим программ хангамж нь системийн тэжээлийн үйл  явцад хэрэглэгддэг ч бичихийг зөвшөөрдөг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (847, "Hang", "", "", "Hang гэдэг нь:  Компьютерийн систем санамсаргүй байдлаар  ажиллахаа больж өгөгдлүүдэд  хариу үйлдэл  өгөхгүй байхыг хэлнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (848, "Hard copy ", "Гараар хуулбарлах ", "", "Компьютерийн эх үүсвэрээс цаасан дээр хэвлэгдсэн мэдээлэл. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (849, "Hard drive ", "", "", "Энэ байгууламж нь удирдлагын механизм болон бичих болон уншихад диск хооронд эргэх механизм хийдэг. Hard drive  нь хатуу дисктэй хамтарсан ихэвчлэн хөдөлгөөнгүй байдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (850, "Hard hand-off", "", "A term used in CDMA systems to describe a hand-off involving a frequency change. The hard hand-off is a break before make hand-off just like in other wireless systems and must be used where the current and hand-off candidate base stations do not use the same RF channel. See also soft hand-off.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (851, "Hard wired ", "", "", "Өөрчлөх боломжгүй хэлхээн дэх тогтмол болон бүрэн хэлхээний дизайныг  удирдан  ажиллах үйл ажиллагаа.  Иймэрхүү үйл ажиллагаа нь асаахад шууд ажиллах боломжтой болдог бөгөөд хэрэглэгчээр удирдуулдаггүй. Ингэснээр төхөөрөмж ассаны дараа эхний шатандаа байдгийг батлаж өгч байна. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (852, "Hardware", "Техник хангамж ", "", "Техник хангамж нь принтер болон гар залуур, удирдах төхөөрөмж, төв процессорын төхөөрөмж гэх мэт тоног төхөөрөмжүүд нь компьютерийн системд хэрэглэгдэнэ. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (853, "Harmonic emission", "", "Spurious emissions at frequencies which are whole multiples of those contained in the band occupied by an emission.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (854, "Hash", "", "", "Нэг болон олон өгөгдлийн төрлүүдээс тухайн тооны үнэ(өртөгийг) тооцоолох.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (855, "Hash table", "", "", "Hash table нь хүснэгт бүтцийн өгөгдлийн арга юм. Өгөгдөл нь хүснэгтэнд хадгалагдаж байдаг ба математик тооцоо болон мэдээллийг нэг талбар дээр ашиглан байршилыг бий болгодог.  Үүний ачаар мэдээлэлд шууд хандаж болдог.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (856, "Hata model", "", "Common name used for the Okamura-Hata model used to predict signal strength levels in land-mobile systems", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (857, "Head crash", "", "", "Head crash гэдэг нь:  Унших болон бичих толгойнууд нь дискний гадаргууд хүрэхийг хэлнэ. Үүнээс болж механизмд маш их хэмжээний хохирол үүсдэг бөгөөд өгөгдлийн алдагдалд оруулдаг. Хувийн компьютеруудад ийм зүйл тохиолдохоос зайлс хийхийн тулд: Компьютерийг нааш цааш хөдөлгөх үед унших болон бичих толгойнууд нь автоматаар аюулгүй байрлалд ордог. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (858, "Header", "Толгой ", "", "Баримт бичгийн хуудас бүрийн дээд хэсэгт гарч ирэх текст. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (859, "Height Above Average Terrain", "", "A measure of an antenna's height above average terrain. This value is used by the FCC in determining compliance with height limitations and transmitting powers for high sites.", "", "HAAT", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (860, "Hertz", "", "A radio frequency measurement (one hertz = one cycle per second).", "", "Hz", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (861, "High altitude platform station", "", "A station located on an object at an altitude of 20 to 50 km and at a specified, nominal, fixed point relative to the Earth.", "", "HAPS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (862, "High altitude platform station ", "Далайн түвшнээс дээш  өндөрлөг платформ станц", "", "Дэлхийн тогтмол цэг болох 20-50 км өндөрт орших обьект бүхий станц.", "HAPS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (863, "High Speed Circuit Switched Data", "", "A circuit-linked technology for higher transmission speeds - up to 57 kilobits per second - primarily in GSM systems.", "", "HSCSD", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (864, "Home Location Register", "", "The functional unit responsible for managing mobile subscribers. Two types of information reside in the HLR: subscriber information and part of the mobile information that allow incoming calls to be routed to the mobile subscriber. The HLR stores the IMSI, MS ISDN number, VLR address, and subscriber data on supplementary services", "", "HLR", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (865, "Hop", "", "A propagation path between two points on the surface of the Earth, comprising one or more ionospheric reflections but without intermediate reflection by the ground.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (866, "Horizontal directivity pattern", "Хөндлөн чиглүүлэлтын диаграмм", "", "Антенны чиглүүлэлтийн диаграмм нь хөндлөн хэвтээ гадаргууд оршино.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (867, "Horizontal directivity pattern", "", "An antenna directivity diagram in the horizontal plane.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (868, "Horizontal scrolling ", "Хэвтээ текст ", "", "Дэлгэцэн дээрх хөдөлгөөнт текст ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (869, "Host ", "Толгой компьютер ", "", "Ихэнхи хүмүүс интернетийн үйлчилгээг  ISP (интернетийн үйлчилгээ үзүүлдэг компани) компаниас авдаг. Хэрэглэгчийн холбогдсон толгой компьютерүүдийг эдгээр компаниуд нийлүүлж байдаг.Толгой компьютер нь холболт  өгөгдлийн сан цахилгаан шуудан вэб хуудас мөн хэрэглэгчийн файлыг зохицуулдаг. Уг толгой компьетэр нь интернет ба хэрэглэгчтэй холбогдсон ба нэг хэрэглэгчийг нөгөөтэй интернетээр холбож чадна.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (870, "Hot key", "Залгаастай түлхүүр ", "", "Түлхүүр функц нэг бол товчны хослол, үйлдлийг маш хурдасгана жишээ нь цэс рүү орох өөр програм ачааллуулах гэх мэт. Шинэ үйлдэл эхлэхэд цэсний хэсгүүд шаардлагагүй юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (871, "Hot link", "Халуун холбоос ", "", "График эсвэл хэсэг текст , дарж ороход холбогдсон хуудас нээгддэг . нэг бол вэбсайт эсвэл сайт . ихэнхидээ холбоос цахим холбоос гэдгээр нь мэднэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (872, "Hot spot", "Хэт халах", "", "HMTL-д хэсэг графикийг өсгөхөд ашигладаг  дарахад интернетийн өөр хавтастай холбоно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (873, "Hot spot", "Халуун цэг", "", "Утасгүй орчинд нийтийн кафе галт тэрэгний буудал онгоцний буудал гэх мэт газар хэрэглэгчид утасгүйгээр интернетийг нөүтбүүк эсвэл бусад төхөөрөмжтэй үнэтэй үнэгүйгээр хэрэглэдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (874, "Hover", "Хөвөлзөх,хэлбэлзэх", "", "Хулганы заагчыг дэлгэцэн дээрх дүрсэн дээр байрлуулах үйлдэл. Ихэвчлэн вэбсайт ашиглаж байхад хэрэглэнэ,  шинэ цэс нээхэд ашигладаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (875, "Hub", "Концентратор,төвлөрүүлэгч, удирдах станц, хавтанг залгах үүр", "", "Нийтийг сүлжээгээр хангахад ашиглана. Компьютер эсвэл жижиг төрлийн техник хангамж байдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (876, "Human computer interface", "Хүн компьютерийн холбоо", "", "Хүн ба ком-н харилцан холбоо. Хүн ба ба ком-н системийн хоорондох холбоог тодорхойлсон нэр томъёо. Бусад нэр томъёо : машин хүний харилцан холболт , хэрглэгчийн холболт , хэрэглэгчийн орчин. Эдгээр нэр томъёоны ихэнхи нь хүн болон ком-н хоорондох харилцан холболтыг аль болох амраар хийхийг зорьсон систем байдаг. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (877, "Hybrid Phase Shift Keying", "", "The spreading technique used in the reverse link of 3G systems to reduce the peak-to-average ratio of the signal by reducing zero crossings and 0 degree phase transitions. Also known as Orthogonal Complex Quadrature Phase Shift Keying (OCQPSK). For more information see Agilent application note 'HPSK Spreading for 3G'.", "", "HPSK", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (878, "Hyper text mark up language ", "Веб сайт хийхэд ашигладаг хэл  ", "", "Тэмдэгт хэл ба мултимедиа файлд зориулж хөгжүүлсэн  WWW гэх мэт.", "HTML", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (879, "Hyperlink", "Дээгүүр холбоос ", "", "Ихэвчлэн холбоос гэдгээр мэднэ. График эсвэл хэсэг текст , дарж ороход холбогдсон хуудас нээгддэг . нэг бол вэбсайт эсвэл сайт", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (880, "Hypertext  transfer protocol ", "Гипертекст дамжуулах протокол", "", "мултимедиа файл дамжуулах хүлээн авах интер нет хуудасын протокол . эдгээр вэб хуудсууд нь HTML-с бүрдэнэ.", "HTTP", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (881, "Icon", "Жижигрүүлсэн дүрс пиктограмм, икон, дүрс зураг ", "", "Тэмдэг нь дэлгэцэн дээрх зураг эсвэл тэмдэгт юм дарахад ганц үйлдэл хийнэ .  жишээ нь  шинэ программ нээнэ. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (882, "Icremental backup", "Өсөн нэмэгдэх нөөц", "", "Сүүлийн өөрчлөлт хийхийн өмнөх хуулбар , бүрэн нөөцлөсөн эсвэл өмнөх нөөцлөсөнөө нэмсэн.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (883, "Identifier", "Ялгуур,танигч, заагч ", "", "Прогаммистийн сонгосон програмтай хавсаргасан обьект эсвэл нэр хаяг. Обьект нь утга эсвэл функц үйл ажиллагаа өгөгдлийн төрөл эсвэл өөр программыг тодорхойлсон элементүүд байж болох юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (884, "IF statement ", "Баримт батлах хуудас,мэдэгдэл,тунхаглал", "", "Хэрвээ өгөгдсөн нөхцөл үнэн бол ганц дан комманд хэсэг коммандыг зөвшөөрөх тушаал.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (885, "Image", "Дүрс, дүрслэмж ", "", "Ком-р боловсруулсан зураг эсвэл график.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (886, "Image compression", "Дүрсийн нягтруулга ", "", "Зургийг шахсанаар хэмжээ нь багасаж дамжуулахад илүү хурдан болно. Энэ нь тоглож байгаа бичлэг тасалдах эсвэл бичигдсэн видео хуурцаг тасалдах гол чухал хэсэг болдог. Зургийг өндөр хурдаар дамжуулах нь гуйвуулж байдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (887, "Image manipulation", "Зураг өөрчлөх", "", "Зураг аваад өөр нэг график программ ашиглаж өөрчлөх", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (888, "Image map", "Дүрс хуваарилах", "", "HTML-д далд холбоостой график. Курсор нэг холбоос дээр байхад , курсорын гаднах  өөрчлөгдөнө.Зургийн өөр хэсэгт дарахад зураг өөр хэсэг рүү интернетээр аваачна.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (889, "Immediate access store", "Шууд холболт ", "", "Санах ой бол   ком-н системийн  өгөгдөл болон зааварчилгааг төв процессорт мөн төв процессорт өгөгдөл хийх хэсэг юм. Ком-н санах ой нь маш их тооны дугаарласан үүрнээс тогтоно. Үүр бүр нь давтагдашгүй хаягтай төв процессор үүнийг хаягаар нь танина.Эдгээр үүрүүдийг шууд төв процессороос дугаарлаж болох ба үүнийг үндсэн санах ой гэнэ.,шуурхай санах ой эсвэл анхдагч санах ой. Шуурхай санах ой нь ком тэжээлээс салахад идэвхигүй болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (890, "Implementation", "Хэрэгжүүлэлт ", "", "Ком-н системийн ажиллагааны бүтцийн процесс нь системийн дизайны хэрэгжүүлэлт.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (891, "Import", "Гаднаас авах", "", "Өөр программ хангамжаас өгөгдөл унших", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (892, "Improved Mobile Telephone Service", "", "A further development of the Mobile Telephone Service (MTS) introduced by the Bell System. This version of mobile service add channels and features, such as automatic dialing, and was the forerunner to the AMPS system.", "", "IMTS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (893, "Inbox", "Ирсэн", "", "Майл ирэхэд зориулагдсан сан.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (894, "Inclination", "", "(Rec. S.673) The angle between the plane of the orbit of a satellite and the principal reference plane.", "", "", "Note – By convention, the inclination of a direct orbit of a satellite is an acute angle and the inclination of a retrograde orbit is an obtuse angle.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (895, "Incompatible", "Үл нийцсэн, нийцэхгүй, таарахгүй ", "", "Нэг зүйл нь ажиллахаа байх жишээ нь программ хангамж нь бусадтайгаа ажиллахгүй байх. WINDOWS дээр ажиллаж байгаа программ MAC дээр ажиллахгүй байх, учир нь 2 систем хоорондоо таардаггүй. Энэ нь үргэлж техник хангамжид болдог.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (896, "Inconsistent data", "Нийцэхгүй мэдээ", "", "Тохирохгүй өгөгдөл нь хайх процесс нь буруу болсон хэрэв хоёр холбоотой өгөгдөл буруу холбоосоор холбогдсон үед жишээ нь хэрвээ хүн.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (897, "Increment", "Өсөлт, ихсэлт, нэмэгдэлт", "", "Нэг нэгжээр нэмэх", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (898, "Indent", "Баруун тийш догол мөр ", "", "Текстийн байрлах зай баруун зүүнээс авах зай. Үгтэй ажиллаж байхад ихэвчлэн ашиглана танилцуулаг гэх мэт ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (899, "Index page", "Индекс хуудас", "", "Вэб хуудас хуудасны индекс нь эхний хуудас байдаг. Анх өгөгдсөнөөрөө уншина ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (900, "Indirect distribution", "", "Use of a satellite link of the fixed-satellite service to relay broadcasting programmes from one or more points of origin to various earth stations for further distribution to the terrestrial broadcasting stations (possibly including other signals necessary for their operation).", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (901, "Individual reception", "", "The reception of emissions from a space station in the broadcasting-satellite service by simple domestic installations and in particular those possessing small antenna.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (902, "Infection", "Халдвар", "", "Вирус өөрийгөө компьютерийн системруу хуулахад тохиолддог ихэнхдээ хард диск руу  ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (903, "Infix notation", "Тэмдэглэгээ оруулах", "", "Тэмдэглэгээ оруулах нь оперотороос опероторын хооронд байршуулна. А-с Б ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (904, "Information", "Мэдээлэл", "", "Мэдээлэл гэдэг нь хайлтын үйл явцын үр дүн, боловсруулсан зохион байгуулсан өгөгдөл гэх мэт утга агуулсан өгөгдөл", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (905, "Information and communication technology ", "Мэдээлэл, харилцаа холбооны технологи", "", "Ерөнхийдөө ком-н болон холбооны технологийн хэрэглээний нэр томъёо.", "ICT", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (906, "Information and learning technology ", "Мэдээлэл, сургалтын технологи", "", "Мэдээлэл болон холбооны технологийн сурах болон заах технологи. ", "ILT", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (907, "Information manager", "Мэдээллийн менежер", "", "өгөгдөл боловсруулах процесст , мэдээллийг зохион байгуулагч нь ком –н системийг зохицуулагчтай хэрхэн хэн холбох вэ гэдгийг хэлж байгаа юм. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (908, "Information processing", "Мэдээлэл боловсруулах", "", "Зохион байгуулалт , мэдээллийн тасалдуулга мөн арга зам. Эдгээр үйл ажиллагаа нь бүх ком хэрэглэгчдэд чухал, ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (909, "Information retrevial", "Мэдээлэл олж авах", "", "Хэрэгтэй мэдээллийг их багтаамжаас өгөгдлийн баазд шахахыг хэлнэ. Үргэлж хүснэгт болон төрлөөр нь ялгаж соортлодог. Их хэмжээтэй өгөгдлийг хайхад амар үр дүн гардаг болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (910, "Information superhighway", "", "", "Интернетийн холболтын хурд нь илүү өндөр.  Ингэж нэрлэсний учир нь их хэжээтэй өгөгдлийг хэрэглэгчдийн хооронд маш хурдан холбож өгдөгт байгаам.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (911, "Information technology", "Мэдээлэлийн технологи ", "", "Ерөнхийдөө ком-уудыг бүрхэж мөн хооронд холбодог", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (912, "Infra red communication", "Хэт улаан туяаны холболт", "", "Хэт улаан туяаны холболт нь телевизийн удирдлагатай адилхан зарчимтай системтэй юм. Робот төхөөрөмж болон мөн алсын гар удирдахад ашиглана. Хүлээн авагч болон дамжуулагчын хоорондох байршил үл хамааран  чиглэлгүй дамжуулна.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (913, "Initialise", "Эхлүүлэх", "", "Анхны утгыг өгнө.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (914, "Ink cartridge", "Бэхний бортого", "", "Бэхний бортого нь бэхт принтер болон бэхээр принтлэхэд ашиглагдана.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (915, "Inkjet printer", "Бэхэн хэвлэгч", "", "Жижиг бөмбөгүүдийг хурдан хатаах замаар хэвлэдэг хэвлэгч. Бэх нь жижиг саванд байна бэхний бортого эсвэл хэвлэгч бортогог гэнэ. Хэвлэгдсэн дүрс нь бортогоноос гарсан бэхээр цаасан дээр нүхлэн цэг болгон будсан байна. Зураг болон текст хэвлэж яадна. Нэг өнгийн хэвлэгч нь саарал өнгөний сүүдэр болон харыг диаграммаар нь хэвлэдэг. Өнгөт принтер нь дөрвөн төрлийн бортоготой  шар хар хөх , хар , цагаан , фусин .", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (916, "In-Phase", "", "For PSK modulation, the reference phase channel. See also Q.", "", "I", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (917, "Input", "Оролт ", "", "Компьютерт өгөгдөл оруулах үйл явц.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (918, "Input device", "Оролтын төхөөрөмж", "", "Өгөгдөл оруулах ком-н систем гар , хулгана , мэдрэгчтэй дэлгэц , скайннер , эсвэл микрофон.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (919, "Insert", "Оруулах", "", "Шинэ өгөгдөл оруулах өмнөх өгүүлбэр дээрээ нэмж.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (920, "Insert disk ", "Оруулах диск ", "", "CD гээ CD эсвэл DVD уншигч руу хийх явц.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (921, "Install", "Суулгах", "", "Өөрийнхөө системд шинэ программ техник хангамж таниулж өгөх.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (922, "Installation", "Суулгалт,тавилт,төхөөрөмж", "", "шинэ программ техник хангамж таниулж өгөх процесс.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (923, "Installation disk", "Суулгалт диск", "", "Дискээр таниулах үйл явц , хэрэглээний прогррам эсвэл шинэ техникийн драйвер.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (924, "Integrated Services Digital Network.", "", "A technology offering switched and fixed high speed transmission of voice, data and video through the existing telephone infrastructure. The service is based on 1 or more 64 kbps digital channels and does not use traditional modems.", "", "ISDN", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (925, "Intellectual Property Rights", "", "Also known as patents, these are the rights of an inventor or assignee to develop and commercialize an invention and license it, usually for a fee, to other manufacturers.", "", "IPR", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (926, "Intelligent Network", "", "Often referred to as the Advanced Intelligent Network, this is a network of equipment, software and protocols used to implement features on the network and support switching and control functions.", "", "IN", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (927, "Interfering source", "", "An emission, radiation, or induction which is determined to be a cause of interference in a radiocommunication system.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (928, "Interleaved", "", "In a given set of radio channels, this term refers to the insertion of additional channels between the main channels (or each RF channel and its adjacent channels), the characteristic frequencies of the additional channels being different from those of the main channels by a specified value, generally a significant portion (e.g. one half) of the nominal channel spacing.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (929, "Interleaving", "", "The process of spreading a block of data over a wider time frame by placing data bits from other data blocks in between the original data bits in the block. Interleaving is frequently done in digital system to spread the data so that bits from the same block are not contiguous and bit errors are randomized to the point FEC is more effective. Systems using this technique are more likely to withstand Rayleigh and other bursty fading and interference phenomenon.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (930, "Intermediate Frequency", "", "The operating frequency in superheterodyne receivers where amplification, filtering and level control prior to demodulation is accomplished.", "", "IF", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (931, "intermodulation products (of a transmitting station)", "", "Each spectral component produced by intermodulation at one of the combination frequencies: f = pf1 + qf2 + rf3 . . . where p, q, r are positive, negative or nil integers and where f1, f2, . . . are the frequencies of the various oscillations existing in a transmitting station, such as the carrier frequencies of the different transmitters, the sub-carrier or local oscillation frequencies, the frequencies of sidebands due to modulation, etc., where the sum | p | + | q | + | r | + . . . is the order of an individual intermodulation product.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (932, "International Atomic Time", "", "The time scale established by the Bureau international des poids et mesures (BIPM) on the basis of data from atomic clocks operating in several establishments conforming to the definition of the second, the unit of time of the International System of Units (SI).", "", "TAI", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (933, "International Mobile Station Equipment Identity", "", "An identification number assigned to GSM mobile stations that uniquely identifies each one. It is a 15 digit serial number that contains a type approval code, final assembly code and serial number.", "", "IMEI", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (934, "International Mobile Station Identity", "", "A unique 15 digit number assigned to a mobile station at the time of service subscription. It contains a mobile country code, a mobile network code, mobile subscriber identification number, and a national mobile subscriber identity.", "", "IMSI", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (935, "International Mobile Telecommunication 2000", "", "A term used by the International Telecommunication Union, a United Nations agency, to describe the third generation mobile telephony due to be ready in 2000. Can also be applied to mobile telephone standards that meet a number of requirements in terms of transmission speed and other factors.", "", "IMT-2000", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (936, "International Telecommunications Union", "", "A United Nations agency that deals with telecommunications issues.", "", "ITU", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (937, "Internet capability", "", "Functionality in a wireless network enabling access to the Internet for web page viewing and e-mail purposes.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (938, "Internet Messaging Access Protocol", "", "A remote mailbox access protocol. It enables efficient operation such as downloading only essential data by first acquisitioning the e-mail header prior to actual e-mail download. This feature makes the protocol well suited to remote environments.", "", "IMAP4", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (939, "Internet Mode", "", "A wireless service launched in Japan in spring 1999 by NTT DoCoMo. The service is accessed by a wireless packet network (PDC-P) and the contents are described in a subset of the HTML language.", "", "i-mode", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (940, "Internet Protocol", "", "A set of instructions defining how information is handled as it travels between systems across the Internet.", "", "IP", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (941, "Internet Protocol version 6.", "", "The latest IP version. Address exhaustion is prevented by means of a long address field, thereby enabling further Internet expansion. In addition, security and mobility are built into the protocol. Currently utilized IP addresses are almost all IPv4, and with the present rate of Internet growth this type of address will be exhausted by 2010. IPv6 on the other hand enables 10 to the 29th power more available addresses than the previous IPv4.", "", "IPv6", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (942, "Inter-network connection protocol for connecting systems based on both analog and digital US standards.", "", "Inter-network connection protocol for connecting systems based on both analog and digital US standards.", "", "IS-41", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (943, "Inter-satellite link", "", "A radio link between a transmitting space station and a receiving space station without an intermediate earth station.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (944, "Inter-Symbol Interference", "", "An interference effect where energy from prior symbols in a bit stream is present in later symbols. ISI is normally caused by filtering of the data streams.", "", "ISI", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (945, "Interworking Function", "", "A technique for interfacing data between a wireless system and the telephone network. It usually involves the use of modems or data terminal adapters to convert the data transmitted over the air interface and mobile network to a format that can be recognized and carried by the public telecommunications network.", "", "IWF", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (946, "Ionosphere", "", "That part of the upper atmosphere characterized by the presence of ions and free electrons mainly arising from photo-ionization, the electron density being sufficient to produce significant modification of the propagation of radio waves in certain frequency bands.", "", "", "Note – The Earth’s ionosphere extends approximately from a height of 50 km to a height of 2 000 km");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (947, "Ionospheric propagation", "", "Radio propagation involving the ionosphere.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (948, "Ionospheric reflection (propagation by) ", "", "Ionospheric propagation at a sufficiently low frequency that, for given conditions, transionospheric propagation is not possible; the radio wave is then subject to progressive refraction which, when considered from a sufficiently large distance, may be considered as equivalent to reflection from a hypothetical surface", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (949, "Ionospheric scatter propagation", "", "Ionospheric propagation involving scatter from irregularities in the electron density in the ionosphere.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (950, "Ionospheric wave", "", "A radio wave returned to the Earth by ionospheric reflection.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (951, "kiloHertz", "", "A radio frequency measurement (one kilohertz = one thousand cycles per second).", "", "kHz", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (952, "Land mobile station", "", "A mobile station in the land mobile service capable of surface movement within the geographical limits of a country or continent.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (953, "Land station", "", "A station in the mobile service not intended to be used while in motion.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (954, "Land station ", "Газрын станц", "", "Тодорхой цэгт бус, хөдөлгөөнт үед ашиглах зориулалттай гар утасны үйлчилгээт станц.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (955, "Least Significant Bit", "", "In a binary coding scheme, the bit having the least numerical value. Analogous to the units position in a decimal number.", "", "LSB", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (956, "Lee's model", "", "A slope-intercept propagation prediction model developed at Bell Laboratories and popularized by William Lee. The model assumes an initial condition at a short distance from a base station and uses that as one end of a slope intercept model to predict path loss between a base station and a mobile unit.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (957, "Left-hand polarization, counter-clockwise polarization", "", "An elliptical polarization for which the electric flux-density vector observed in any fixed plane not containing the direction of propagation, whilst looking in this direction, rotates with time in a left-hand or counter-clockwise direction.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (958, "Left-hand-polarization counter-clock wise polarization", "Зүүн гарын дагуу туйлшрах, цагын зүүний эсрэг туйлшрах ", "", "Тогтмол туйлшралын хөдөлгөөний чиглэл нь вектор,богинхон нигтралтай тархалтын түвшин багтаамжгүй цахилгаанжсан.Энэ чиглэл нь хархад хоорондоо зүүн гарын дагуу цагын зүүний эсрэг чиглэлтэй  .", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (959, "Line of sight", "", "A description of an unobstructed radio path or link between the transmitting and receiving antennas of a communications system.", "", "LOS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (960, "Linear power amplifier", "", "The final amplification stage in a multicarrier transmitter that has been designed and optimized to produce a linear response. By operating in the linear mode, the amplifier reduces the non-linear effects that produce intermodulation products and side-lobe spectra that cause adjacent channel interference.", "", "LPA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (961, "Linear Predictive Coding", "", "A speech encoding scheme that uses periodic pulses to excite a filter, similar to the way human voice is produced. The code is predictive in that it uses knowledge of past data (represented as vectors) to predict future values in a feed forward manner.", "", "LPC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (962, "Line-of-sight propagation", "", "Propagation between two points for which the direct ray is sufficiently clear of obstacles for diffraction to be of negligible effect.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (963, "Line-of-sight-propagation", "Тархалтын шугам", "", "Тархалтын цацрагын чиглэл нь хорондоо 2 цэгээс хангалттайгаар чөлөөтэй диффракц үүсгэдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (964, "Link", "", "The radio connection between a transmitter and a receiver.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (965, "Link budget", "", "A calculation involving the gain and loss factors associated with the antennas, transmitters, transmission lines and propagation environment used to determine the maximum distance at which a transmitter and receiver can successfully operate.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (966, "Local Area Network", "", "A small data network covering a limited area, such as within a building or group of buildings.", "", "LAN", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (967, "Local Multipoint Distribution System", "", "The name given to point-to-multipoint systems operating at 29 GHz and carrying high speed digital traffic. These systems are usually laid out like cellular systems and are currently being used by entrepreneurs to provide competitive services to the local telephone companies.", "", "LMDS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (968, "Location Area Identity", "", "Information carried in the SIM of GSM handsets that identify the subscriber's home area. This is used for billing and sub-net operation purposes.", "", "LAI", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (969, "Location registration", "", "One of several computer databases used to maintain location and other information on mobile subscribers. See HLR and VLR.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (970, "Logical channel", "", "A communications channel derived from a physical channel. A physical channel, i.e. RF channel, typically carries a data stream that contains several logical channels. These usually include multiple control and traffic channels.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (971, "Loss of signal", "", "A description of an unobstructed radio path or link between the transmitting and receiving antennas of a communications system.", "", "LOS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (972, "Loss relative to free space", "", "The difference, between the basic transmission loss and the free-space basic transmission loss, expressed in decibels.", "", "", "Note 1 – The loss relative to free space may be expressed by: Lm = Lb – Lbf dB (5) Note 2 – Loss relative to free space may be divided into losses of different types, such as: – absorption loss for example by ionospheric, atmospheric gases or hydrometeors; – diffraction loss as for ground waves; – effective reflection or scattering loss, as in the ionospheric case including the results of any focusing or defocusing due to curvature of a reflecting layer; – polarization coupling loss, which can arise from any polarization mismatch between the antennas for the particular ray path considered; – aperture to medium coupling loss or antenna gain degradation, which may be due to substantial scattering phenomena on the path; – losses due to phase interference between the direct ray and rays reflected from the ground, other obstacles or atmospheric layers.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (973, "Low Noise Amplifier", "", "A receive preamplifier having very low internal noise characteristics placed very near the antenna of a receiver to capture the C/N before it can be further degraded by noise in the receiving system.", "", "LNA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (974, "lowest useful frequency", "", "The lowest frequency that would permit acceptable performance of a radio circuit by signal propagation via the ionosphere between given terminals below the ionosphere at a given time under specified working conditions.", "", "LUF", "Note – See Notes 1 and 2 of term G30 “operational MUF”.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (975, "Macro cell", "", "A large cell in a wireless system capable of covering a large physical area. Macrocells are used in rural areas and other areas where subscriber or traffic densities are low.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (976, "Major Trading Area", "", "A geographic area over which a PCS operator is licensed to provide service. MTAs are a group of BTAs having common financial, commercial and economic ties and were first used to license PCS service in the middle '90s. MTAs can be many times larger a cellular MSA and can cross multiple state lines in some instances. MTAs are used by the Rand-McNally corporation to summarize economic data. MTAs are one of the largest licensing areas used by the FCC.", "", "MTA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (977, "Matched filter", "", "The receiver filter with impulse response equal to the timereversed, complex conjugate impulse response of the combined transmitter filter-channel impulse response.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (978, "Mean Opinion Score", "", "A statistical rating and scoring technique used to rate the performance of telephone connections by users.", "", "MOS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (979, "Mean power", "Дундаж чадал", "", "Дундаж чадал нөөцөнд антены нэвтрүүлэх шугамаар хангалттай цаг нам давтамж өндөр давтамжийн уртын харьцуулахад үүссэн нормаль үйлдэлийн горимууд.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (980, "Mean power", "", "The average power supplied to the antenna transmission line by a transmitter during an interval of time sufficiently long compared with the lowest frequency encountered in the modulation taken under normal operating conditions.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (981, "Medium Access Control", "", "The protocols that arbitrate access between all nodes of a wireless LAN.", "", "MAC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (982, "Mega Chips per Second", "", "A measure of the number of bits (chips) per second in the spreading sequence of direct sequence spreading code", "", "Mcps", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (983, "Megahertz", "", "A unit of frequency equal to one million hertz or cycles per second. Wireless communications occur in the 800 MHz, 900 MHz and 1900 MHz bands.", "", "MHz", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (984, "Metropolitan Statistical Area", "", "A geographic area over which a cellular operator is licensed to provide service. MSAs are groups of counties in metropolitan areas having common financial, commercial and economic ties and were first used to license cellular service in the early '80s. MSAs cross state lines in some instances. MSAs were first used by the Dept. of Commerce to collect economic data.", "", "MSA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (985, "Micro cell", "", "A base station with a very small coverage area designed to provide service in areas having a very high density of mobile subscribers. Microcells are traditionally used in convention centers, airports and similar areas.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (986, "Minimum Shift Keying", "", "A modulation technique using sinusoidal shaped input data pulses to drive the phase modulator. This results in a linear phase change over conventional QPSK, resulting in lower side lobes and less adjacent channel interference performance.", "", "MSK", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (987, "Minimum usable field-strength, [minimum usable power flux-density]", "", "Minimum value of the field-strength [minimum value of the power flux-density] necessary to permit a desired reception quality, under specified receiving conditions, in the presence of natural and man-made noise, but in the absence of interference from other transmitters.", "", "", "Note 1 – The desired quality is determined in particular by the protection ratio against noise, and for fluctuating noise, by the percentage of time during which this protection ratio must be ensured. Note 2 – The receiving conditions include, inter alia: – the type of transmission, and frequency band used; – the receiving equipment characteristics (antenna gain, receiver characteristics, siting, etc.); – receiver operating conditions, particularly the geographical zone, the time and the season. Note 3 – Where there is no ambiguity, the term “minimum field-strength” [“minimum power flux-density”] may be used. Note 4 – The term “minimum usable field-strength” corresponds to the term “minimum fieldstrength to be protected” which appears in many ITU texts.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (988, "Mobile Application Part", "", "A protocol using the lower level layers of the SS7 protocol stack (TCAP, SCCP and MTP) for communication between the various registers and other MSCs.", "", "MAP", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (989, "Mobile Assisted Handoff", "", "A handoff technique involving feedback from the mobile station as part of the handoff process. The feedback is usually in the form of signal level and quality measurements on the downlink and signal level measurements from neighbor cells.", "", "MAHO", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (990, "Mobile Identification Number", "", "A unique identification number given to a mobile unit. In most cases, this number is the telephone number of the handset. In the case of analog cellular, the MIN is used to route the call. In most second generation system, the system assigns temporary numbers to the handset to route calls as a security precaution. See also TMSI.", "", "MIN", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (991, "Mobile Media Mode", "", "The WWW:MMM logo is a marketing innovation comprising a unifying industry-wide marketing symbol representing leading edge web-based products and services.", "", "MMM", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (992, "Mobile phone network", "", "A network of cells. Each cell is served by a radio base station from where calls are forwarded to and received from your mobile phone by wireless radio signals.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (993, "Mobile service", "Гар утасны сүлжээ", "", "Гар утас болон газрын станцын хооронд буюу хөдөлгөөнт станцууд хоорондын  Радио холбооны үйлчилгээ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (994, "Mobile station", "Хөдөлгөөнт станц", "", "Гар утасны хөдөлгөөнт үйлчилгээг  тодорхойгүй цэгт ажиллуулах болон зогсоох зорилготой.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (995, "Mobile station", "", "A station in the mobile service intended to be used while in motion or during halts at unspecified points.", "", "", "Note 1 – Mobile service; Service mobile; Servicio móvil (CV 1003) (RR. 1.24). A radiocommunication service between mobile and land stations, or between mobile stations. (CV) Note 2 – The definitions of those categories of stations in mobile services, which are most useful for Radiocommunication Study Group 8 work are given in Appendix A to this Recommendation");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (996, "Mobile Station", "", "The term used to describe the customer terminal in a wireless network.", "", "MS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (997, "Mobile Switching Center", "", "The location providing the mobile switching function in a second generation network wireless network. The MSC switches all calls between the mobile and the PSTN and other mobiles.", "", "MSC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (998, "Mobile Telephone Switching Office", "", "The location providing the mobile switching function in a first generation wireless network. The MTSO switched all calls between the mobile and the PSTN and other mobiles.", "", "MTSO", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (999, "Modification", "Хаягийн өөрчлөлт ", "", "Хаяг нь тооны ухаан учир зүй өгүүлбэр зүйн үйлдэл гүйцэтгэдэг Cf;-үр дүнтэй хаяг жагсаалтай хаяг уялдаатай  хаяг ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1000, "Modulation", "", "The process of coding and decoding information for transmission. For example, a voice conversation is coded into binary bits (digital information), transmitted and then decoded at the receiving end.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1001, "Most Significant Bit", "", "In a binary coding scheme, the bit having the greatest numerical value. Analogous to the left-most numeric position in a decimal number.", "", "MSB", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1002, "Moving Picture Experts Group", "", "The group that has defined the standards for compressed video transmission. Can also refer to the file format itself.", "", "MPEG", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1003, "Moving Picture Experts Group Compression Standard Version 4.", "", "MPEG4 is a technology for compressing voice, video and related control data and is one of the MPEG (Moving Picture Experts Group) international standards. It is currently a focus of attention due to the fact that it enables high speed and stable video transmission even in heretofore difficult environments such as mobile communication. Incorporation of this leading edge technology will imbue 3G terminals with a rich multimedia capability.", "", "MPEG4", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1004, "Multi-Carrier Code Division Multiple Access", "", "Typically, this means the combination of three IS-95 carriers to form one wideband carrier. It is an evolution of IS-95 for third generation systems. Also called cdma2000. The current nomenclature is temporary, with a formal name for this technology to be determined under 3GPP2.", "", "MC-CDMA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1005, "Multimedia Messaging Service", "", "MMS is a new standard that is being defined for use in advanced wireless terminals. The service concept is derived from Short Message Service and allows for nonreal-time transmission of various kinds of multimedia contents like images, audio, video clips, etc. As a further evolution of the current text mail, for example, electronic postcards, audio/video clips, etc. can be sent.", "", "MMS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1006, "Multipath", "", "A propagation phenomenon characterized by the arrival of multiple versions of the same signal from different locations shifted in time due to having taken different transmission paths of varying lengths.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1007, "Multipath propagation", "Олон сувгийн тархалт ", "", "Тархалтын зам нь нэгэн зэрэг хүлээн авч дамжуулах ба цэгээс цэгийн хооронд тусгай тархалтын хэмжээтэй.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1008, "Multipath propagation", "", "Propagation between a transmission point and a reception point over a number of separate propagation paths simultaneously.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1009, "Multiple access", "", "The process of allowing multiple radio links or users to address the same radio channel on a coordinated basis. Typical multiple access technologies include FDMA, TDMA, CDMA, and FHMA.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1010, "Multi-pulse excited", "", "A multi-pulse process for determining the position and amplitude of sample pulses in a speech codec.", "", "MPE", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1011, "Multi-satellite link", "", "A radio link between a transmitting earth station and a receiving earth station through two or more satellites, without any intermediate earth station. A multi-satellite link comprises one up-link, one or more satellite-to-satellite links and one down-link.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1012, "Narrowband Advanced Mobile Phone System", "", "Combines the AMPS transmission standard with digital signaling information to effectively triple the capacity of AMPS while adding basic messaging functionality.", "", "N-AMPS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1013, "Necessary bandwidth", "", "For a given class of emission, the width of the frequency band which is just sufficient to ensure the transmission of information at the rate and with the quality required under specified conditions.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1014, "Nested codes", "", "A type of concatenated block code where the layers (inner and outer) are amalgamated in such a way that burst errors not able to be corrected by the inner code are sufficiently spread over enough blocks as to be corrected by the outer layer.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1015, "Network Management Center", "", "An operations center used to manage network resources such as the MSC, location registers and base stations.", "", "NMC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1016, "Network Switching Subsystem", "", "That portion of a GSM network that manages the connections and communications within the network. The BSS and OSS complete the major components of the network.", "", "NSS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1017, "Noise", "", "Any variable physical phenomenon apparently not conveying information and which may be superimposed on, or combined with, a wanted signal.", "", "", "Note – The term “radio-frequency noise” is defined in this Recommendation.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1018, "Noise ", "Шуугиан", "", "Аливаа хувьсагч нь физик үзэгдэл бололтой мэдээлэл дамжуулах биш, аль ууссан, эсвэл хүссэн дохио, хослуулж байж болох юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1019, "noise figure", "", "A figure of merit for receivers and preamplifiers representing the amount of excess noise added to the signal by the amplifier or receiving system itself. The lower the noise figure, the less excess noise is added to the signal.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1020, "Non Return to Zero", "", "A type of data stream where successive data pulses 'ones' are continuous over several clock cycles without returning to the 'zero' state between successive 'ones'.", "", "NRZ", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1021, "Nordic Mobile Telephony", "", "The common Nordic standard for analog mobile telephony as established by the telecommunications administrations in Sweden, Norway, Finland and Denmark in the early 1980s. NMT systems have also been installed in some European countries, including parts of Russia, and in the Middle East and Asia. NMT is operated in 450 MHz and 900 MHz bands.", "", "NMT", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1022, "Nordic Mobile Telephony - 450", "", "An early cellular system developed and operated in northern Europe utilizing the 450 MHz band.", "", "NMT-450", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1023, "North American Digital Cellular", "", "See IS-136.", "", "NADC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1024, "Number Assignment Module", "", "The programmable module in an AMPS analog phone used to contain the MIN, ESN, home system ID and other information.", "", "NAM", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1025, "Nyquist filter", "", "An ideal low pass filter with a cutoff frequency equal to the sampling rate. This technique is used to convert PAM pulses to an analog signal in D/A converters.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1026, "Nyquist rate", "", "The minimum sampling rate proposed by Nyquist for converting a band limited waveform to digital pulses. The rate must be at least twice the highest frequency of interest in the waveform being sampled.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1027, "Occupied band", "", "The frequency band such that, below the lower and above the upper frequency limits, the mean powers emitted are each equal to a specified percentage β/2 of the total mean power of a given emission. Unless otherwise specified by the ITU-R, for the appropriate class of emission, the value of β/2 should be taken as 0.5%.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1028, "Occupied bandwidth", "", "The width of a frequency band such that, below the lower and above the upper frequency limits, the mean powers emitted are each equal to a specified percentage β/2 of the total mean power of a given emission. Unless otherwise specified by the ITU-R for the appropriate class of emission, the value of β/2 should be taken as 0.5%.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1029, "Offset", "", "In a given set of radio channels, this term refers to a change of the characteristic frequency of a radio-frequency channel in relation to its nominal frequency, by a specified value which is generally small compared to the channel spacing.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1030, "Offset Quadrature Phase Shift Keying", "", "A type of QPSK modulation that offsets the bit streams on the I and Q channels by a half bit. This reduces amplitude fluctuations and helps improve spectral efficiency.", "", "OQPSK", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1031, "Okamura model", "", "A propagation prediction model for land-mobile communications developed by Yoshi Okamuar et al. in the late '60s.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1032, "Open System Interconnect", "", "A reference model that describes a layered structure for modeling the interconnection and exchange of information between users in a communications system. The 7 layers are: the physical layer, the link layer, the network layer, the transport layer, the session layer, the presentation layer and the application layer.", "", "OSI", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1033, "Operational MUF", "", "The highest frequency that would permit acceptable performance of a radio circuit by signal propagation via the ionosphere between given terminals below the ionosphere at a given time under specified working conditions.", "", "", "Note 1 – Acceptable performance may for example be quoted in terms of maximum error ratio or required signal/noise ratio. Note 2 – Specified working conditions may include such factors as antenna types, transmitter power, class of emission and required information rate.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1034, "Operations & Maintenance Center", "", "A location used to operate and maintain a wireless network.", "", "OMC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1035, "Operations, Administration, Maintenance & Provisioning Center", "", "An operations center used to operate, administer, maintain and provision the network.", "", "OAM&P", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1036, "Operator", "", "Company that operates a telephone network, for example AT&T, Vodaphone and BT.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1037, "orbit", "", "1. The path, relative to a specified frame of reference, described by the centre mass of a satellite or other object in space, subjected solely to forces of natural origin, mainly the force of gravity. 2. By extension, the path described by the centre of mass of a body in space subjected to forces of natural origin and occasional low-energy corrective forces exerted by a propulsive device in order to achieve and maintain a desired path.", "", "", "Note – In the Radio Regulations, the above two definitions are combined in the following form (RR No. 1.184): “The path, relative to a specified frame of reference, described by the centre of mass of a satellite or other object in space subjected primarily to natural forces, mainly the force of gravity.”");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1038, "Order of diversity", "", "The number of different radio signals used for diversity reception. For two signals, reception is said to be “double diversity”, and so on.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1039, "Orthogonal co-channel", "", "Refers to the use of the same RF channel by two emissions with orthogonal polarizations, for the transmission of two independent signals.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1040, "Orthogonal Complex Quadrature Phase Shift Keying", "", "Also known as HPSK. See HPSK.", "", "OCQPSK", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1041, "Orthogonal Frequency Division Multiplex", "", "A modulation technique that transmits blocks of symbols in parallel by employing a large number of orthogonal subcarriers. The data is divided into blocks and sent in parallel on separate sub-carriers. By doing this, the symbol period can be increased and the effects of delay spread are reduced.", "", "OFDM", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1042, "Orthogonal Variable Spreading Function", "", "A set of spreading codes derived from tree structured set of orthogonal codes and are used to channelize the IMT2000/ULTRA system.", "", "OVSF", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1043, "Out-of-band emission", "", "Emission on a frequency or frequencies immediately outside the necessary bandwidth which results from the modulation process, but excluding spurious emissions.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1044, "Output Radio Frequency Spectrum", "", "A measurement for GSM signals that tests for interference in the adjacent frequency channels (ARFCNs) and results from two effects: modulation within the bursts and the power that ramps up and down, or switching transients. ORFS is a critical GSM transmitter measurement.", "", "ORFS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1045, "Packet", "", "A piece of data transmitted over a packet-switching network such as the Internet. A packet includes not just data but also address information about its origination and destination.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1046, "Packet Assembler/Disassembler", "", "The part of a packet transmission system that segments the transmit data into packets and returns the receive data to longer messages.", "", "PAD", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1047, "Packet radio", "", "A radio system that operates by sending data in packets.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1048, "Packet Reservation Multiple Access", "", "PRMA is a packet-based TDMA concept where the users contend for the time slots. In situations where the system is not near capacity, a user can reserve a time slot for future use", "", "PRMA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1049, "Packet switching", "", "A method of switching data in a network where individual packets of a set size and format are accepted by the network and delivered to their destinations. The sequence of the packets is maintained and the destination established by the exchange of control information (also contained in the packets) between the sending terminal and the network before the transmission starts. The network is open to all users, all the time, with packets from the various nodes being interleaved throughout the network. The packets can be sent in any order, as the control information sent at the beginning of the transmission ensures they are interpreted in the correct order at the receiving end. Because each packet carries its own control instructions, it can use any route to reach its destination.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1050, "Pad", "", "See attenuator.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1051, "Paging", "", "Single direction radio service for alerting subscribers and delivering messages.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1052, "Paging Channel", "", "A logical channel in GSM, cdma2000, and W-CDMA systems used to send messages to mobile station. Used primarily to notify the mobile that it has an incoming call.", "", "PCH", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1053, "Palm Query Applications", "", "An Internet clipping application developed from HTML code and run on Palm PDAs. The application is designed to streamline the flow to the PDA to minimize the number of kilobytes sent and ultimately paid for.", "", "PQA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1054, "Partial response signalling", "", "A signalling technique in which a controlled amount of intersymbol interference is introduced at the transmitter to shape the transmitted spectrum.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1055, "Passive sensor", "", "A measuring instrument in the Earth exploration-satellite service or in the space research service by means of which information is obtained by reception of electromagnetic waves of natural origin.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1056, "Path loss", "", "The amount of loss introduced by the propagation environment between a transmitter and receiver.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1057, "Peak envelope power", "", "The average power supplied to the antenna transmission line by a transmitter during one radio frequency cycle at the crest of the modulation envelope taken under normal operating conditions.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1058, "Peak envelope power ", "", "", "Дундаж чадал үйлдэлийн горим дотор антен нэвтрүүлэх шугамаар дамжуулах туршид давтамж ХИ утгандаа модуляцлагдсан бүрхүүлийг хэлнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1059, "Peak power", "", "The maximum instantaneous power radiated by a pulsed or bursted transmitter. It is the power radiated while the transmitter is keyed or operated.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1060, "Period", "", "The time elapsing between two consecutive passages of a satellite through a characteristic point on its orbit.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1061, "Personal Access Communications System", "", "A low mobility low power wireless system designed for residential use.", "", "PACS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1062, "Personal Communications Industry Association", "", "A leading international trade association representing the personal communications services (PCS) industry. Its primary objective is to advance regulatory policies, legislation, and technical standards in this industry.", "", "PCIA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1063, "Personal Communications Network", "", "A standard for digital mobile phone transmissions operating at a frequency of 1800 MHz (also referred to as GSM 1800). It is used in Europe and Asia Pacific.", "", "PCN", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1064, "Personal Communications Services", "", "Generally, a marketing term used to describe a wide variety of two-way digital wireless service offerings in North America operating at 1900 MHz. PCS services include next generation wireless phone and communication services, wireless local loop, inexpensive walk-around communications service with lightweight, low-powered handsets, in-building cordless voice services for business, in-building wireless LAN service for business, enhanced paging service as well as wireless services integrated with wired networks. A Personal Communications System refers to the hardware and software that provide communications services.", "", "PCS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1065, "Personal Communications System", "", "The infrastructure used to provide personal communications services.", "", "PCS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1066, "Personal Data Appliance/Assistant", "", "Also known as Personal Digital Appliance.", "", "PDA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1067, "Personal Handy Phone", "", "The mobile hand-set used with the Japanese Personal Handy Phone system.", "", "PHP", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1068, "Personal Handy Phone System", "", "A digitalized evolution of the earlier analog cordless phone concept which enables outdoor use as well. PHS incorporates a unique Japanese standard which melds the advantages of the European DECT and CT2. The system operates in the 1.9 GHz band", "", "PHS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1069, "Personal Identification Number", "", "A code used for all GSM-based phones to establish authorization for access to certain functions or information. The PIN code is delivered together with your subscription.", "", "PIN", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1070, "Personal or Pacific Digital Cellular", "", "A Japanese standard for digital mobile telephony in the 800 MHz and 1500 MHz bands. To avoid the previous problem of lack of compatibility between the differing types of earlier analog mobile phones in Japan (i.e. NTT type and US developed TACS type), digital mobile phones have been standardized under PDC. In the case of the PDC standard, primarily six channel TDMA (Time Division Multiple Access) technology is applied. PDC, however, is a standard unique to Japan which renders such phone units incompatible with devices which adopt the more worldwide prevalent GSM standard. Nevertheless, digitalization under the standard enables ever smaller and lighter mobile phones which in turn has spurred market expansion. As a result, over 93% of all mobile phones in Japan are now digital.", "", "PDC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1071, "Phase jitter", "", "The amount of uncertainty introduced in digital demodulation caused by imperfections in the clock recovery timing.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1072, "Phase Locked Loop", "", "PLL is a major component in the frequency synthesizer scheme. This device provides a wide, flexible range of internal frequency dividers which allow the designer the ability to create a synthesizer to match design requirements.", "", "PLL", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1073, "Phase Shift Keying", "", "A broad classification of modulation techniques where the information to be transmitted is contained in the phase of the carrier wave.", "", "PSK", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1074, "Physical channel", "", "The actual radio channel that carries the various logical and traffic channels in a wireless system.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1075, "Pico cell", "", "Very small cell in a mobile network for boosting capacity within buildings.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1076, "Pilot code", "", "This is a logical channel in a CDMA system characterized by an unmodulated direct sequence spread-spectrum signal continuously monitored by each base station. It allows the mobile stations to acquire the timing of the forward channel, serves as a phase reference for demodulation, and allows the mobile to search out the best (strongest) base stations for acquisition and hand-off.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1077, "Pilot pollution", "", "A type of co-channel interference in CDMA systems caused when the pilot code from a distant cell or base station is powerful enough to create an interference problem.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1078, "Polarization", "Туйлшрал", "", "Огторгуйн орон зайд радио долгионы цахилгаан орны хүчлэгийн вэктор  хэрхэн байрласнаас радио долгионы туйлшрал тодорхойлогдох бөгөөд векторын байрлал нь туйлшралын чиглэлийг тодорхойлно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1079, "Polarization", "", "To be defined later.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1080, "Polarization diversity", "", "A diversity technique where antennas of different polarizations, I.e. horizontal and vertical, are used to provide diversity reception. The antennas take advantage of the multipath propagation characteristics to receive separate uncorrelated signals.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1081, "Post Office Code Special Advisory Group", "", "An international group that develops and sets standards for the paging industry.", "", "POCSAG", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1082, "Power Amplifier", "", "A device for taking a low or intermediate-level signal and significantly boosting its power level. A power amplifier is usually the final stage of amplification in a transmitter.", "", "PA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1083, "Power control", "", "A technique for managing the transmit power in base stations and mobiles to a minimum level needed for proper performance. Downlink power control applies to base stations and uplink power control to mobiles. Power control is used in nearly all wireless systems to manage interference, and in the case of mobiles, to extend battery life.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1084, "Power spectral density", "", "Power normalized to 1 Hz. Knowing the power spectral density and system bandwidth, the total power can be calculated.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1085, "Power Spectral Density", "", "See power spectral density", "", "PSD", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1086, "Precipitation-scatter propagation", "Тархалт эрчимтээ буурах ", "", "Агаар мандалын тархалт нь ихэвчилэн борооны усны хэмжүүрээс шууд шалтгаална .", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1087, "Precipitation-scatter propagation", "", "Tropospheric propagation due to scattering caused by hydrometeors, mainly rain.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1088, "Print", "Хэвлэх ", "", "Хэвлэмэл материал гаргаж авахын тулд принтер рүү өгөгдөл илгээнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1089, "Print buffer ", "Хэвлэх буфер", "", "Принтерийн хэвлэхэд бэлэн болсон өгөгдлийг компьютерийн санах ойн хаанаас хандаж хэвлэхийг заадаг хэсэг юм. Буфер луу өгөгдөл илгээж чадах боловч програмыг идэвхжүүлж, үйлдлийг үргэлжлүүлэн тэмдэгт үсэг хэвлэхийг хүлээдэг. Буфер бол ердийн үдирдлагатай үйлдлийн систем юм. Энэ үйлдлийн систем нь дохиог тасалдуулах замаар бэлэн болсон дараагийн мэдээлэлийг хэвлэх ба принтер нь компьютерээс удаан ажилладаг. Энэ үйлдлийн систем нь мэдээллийг буферт авч, принтер лүү мэдээлэл илгээдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1090, "Print cartridge ", "Картридж", "", "Картридж нь хороор хангахад ашиглагддаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1091, "Print job ", "Хэвлэх үйл ажиллагаа ", "", "Хэвлэх үйл ажиллагааг хийснээр төрөл бүрийн өгөгдлийг хэвлэх боломжтой.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1092, "Print preview ", "Хэвлэхээс өмнөх байдлаар харах ", "", "Принтер лүү хэвлэх комманд илгээхийн өмнө баримт хэрхэн харагдаж байгааг харж болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1093, "Print queue", "Хэвлэх дараалал", "", "Хэвлэгдэхээр хүлээгдсэн ажлын жагсаалт нь хэвлэгдэх дарааллын дагуу түр хадгалагдсан байдаг. Ер нь бол энэ нь хэвлэгдэхээр хүлээгдсэн файлын жагсаалт юм. Файл бүр нь гол мэдээллийг агуулах бөгөөд түр хадгалагдсан мэдээллийг принтер лүү мэдээлдэг. Ажлын баримт бичиг  эсвэл арилжааны баримт бичгийн томоохон багц байж болох юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1094, "Print spooler", "Хэвлэх дамар, Хэвлэгчийн түр хадгалагч ", "", "Програмын нөөцөөс бэлэн болсон ямар нэг өгөгдлийг хэвлэнэ.  Хэвлэх үйл ажиллагааг хийснээр төрөл бүрийн өгөгдлийг хэвлэх боломжтой. Програм хэвлэх үйл ажиллагааг бүрэн хүлээж авсны дараа хэвлэгчийн түр хадгалагч нь принтер лүү мэдээлэл илгээдэг. Төрөл бүрийн програмууд эсвэл терминал дээр хадгалагдсан файлыг принтер лүү өгөгдөл илгээх нь тохиромжтой байдаг учраас хэвлэгчийн түр хадгалагч  уруу өгөгдөл илгээж болдог. Хэвлэгчийн түр хадгалагч нь  үйлдлийн системийн eрдийн нэг хэсэг юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1095, "Printed circuit board (PCB) ", "Хавтан дээр хэлхээ хэвлэх", "", "PCB нь цахилгаан хэлхээг агуулсан хавтан юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1096, "Printer", "Хэвлэх төхөөрөмж, хэвлэгч ", "", "Цаасан дээр график эсвэл үсэг тэмдэгтүүдыг гаргах гаралтын төхөөрөмж юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1097, "Printer font", "Хэвлэгчийн фонт ", "", "Хэвлэгчийн фонтууд нь принтерт өөрт нь байдаг нөөц юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1098, "Printer resolution ", "Принтерийн нягтрал ", "", "Хэвлэгдсэн дүрсийн чанарын хэмжих үзүүлэлт юм. Принтерийн нягтрал нь нэг инч дэх цэгүүдээр буюу dpi-аар хэмждэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1099, "Printout", "Компьютерээс хэвлэн гаргасан материал ", "", "Цаасан дээр компьютерын мэдээллийг хэвлэнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1100, "Private Automatic Branch Exchange", "", "A customer premise telephone switching system capable of interfacing to a telephone central office with trunk groups and routing calls based on a 3 or 4 digit telephone extension number.", "", "PABX", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1101, "Private Branch Exchange", "", "An exchange system used in companies and organizations to handle internal and external calls.", "", "PBX", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1102, "Private Mobile Radio", "", "Generally for use within a defined user group such as the emergency services or by the employees of a mining project.", "", "PMR", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1103, "Probability Density Function", "", "The probability that X lies between two values, x1 and x2.", "", "PDF", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1104, "Procedural language ", "Процедур хэл ", "", "Дэд програм нь бүтцийн хувьд процедур (procedure) эсвэл функц (function) гэсэн хэлбэртэй байна. Функц, процедур болгон өөрийн гэсэн оролт, гаралт буюу эхлэл, төгсгөлтэй байна. Тэдгээр нь хоорондоо оролт болон гаралтанд илгээгдсэн өгөгдлүүдээр холбогдоно. Түүнээс гадна функц хэлбэрийн дэд програм нь утгатай (хариутай) байна. Харин процедур төрлийн дэд програмд ийм утга гэж байхгүй.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1105, "Procedure", "Процедур", "", "Процедурт тодорхойлолт эсвэл заагч өгөгдсөн байдаг.Бусад дэд програмын зааварт дараагийн заагчыг ашиглана. Процедур үзүүлэлт нь хөгжүүлэгдсэн ассемблер хэлийг зөвшөөрөх хэдийч процедур нь өндөр түвшний хэлтэй холбогдсон байдаг. Процедур нь програмын өгөгдлийг хүлээж авах ба зарим тохиолдолд бэлэн байгаа програмын үр дүнг удирдана. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1106, "Process", "Процесс, үйл явц", "", "Компьютерийн системийн мэдээллийг боловсруулах үйлдлүүдийг нэг бүрчлэн шалгах үүрэг гүйцэтгэдэг. Жишээ нь бичиглэл (бичлэг) засах, файлыг ангилах, мэдээлэл оруулах эсвэл тайлан хэвлэх зэрэг үйлдлүүд багтана.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1107, "Process", "Процесс, үйл явц ", "", "Компьютер ихэвчлэн 1 болон олон үйлдлийг нэгэн зэрэг ачаалдаг. Төв процессорын тусламжтай олон үйлдлийг нэгэн зэрэг идэвхтэй байлгаж болдог. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1108, "Processing gain", "", "The amount of gain, in dB, provided by the spreading code in a CDMA system, usually the ratio of the spreading rate to the information rate.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1109, "Processor speed", "Процессорын хурд ", "", "Төв процессорын (CPU) хурд юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1110, "Program", "Програм, хөтөлбөр ", "", "Компьютерийн гүйцэтгэх чадах прорамуудын нэгдэл юм. Дээд түвшний болон ассемблер хэлээр програм бичигдсэн байдаг. Машины кодоос хөрвүүлэгч програм руу хөрвүүлэх дараалалаар програмыг гүйцэтгэдэг. Програм:Компьютер болон бусад машины үйл ажиллагааг удирдах зааварчилгаа нь програм хангамж дээр дэс дараатай кодлогдсон байдаг. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1111, "Program code", "Програмын код", "", "Компьютер буюу бусад машины үйл ажиллагааг удирдах, програм хангамжийн дараалсан код юм", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1112, "Programmer", "Програмист", "", "Компьютерийн програмыг бичих эсвал засдаг хүн", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1113, "Programming", "Програмчлал", "", "Програмын хөгжүүлэлт ба загварчлалын үйл явц", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1114, "Programming language", "Програмчлалын хэл", "", "Машинаар болон компьютерээр тооцоолол хийх зориулалтай зохиомол хэл. Программчлалын хэл нь компьютерийн програм бичихэд зориулагдсан хийсвэр тэмдэгтийн систем юм. Програмчлалын хэл нь компьютер програмын гадаад төрх , ажилгааг бүрдүүлдэг толь зүйн , өгүүлбэр зүйн болон утга зүйн дүрмийг тодорхойлж өгдөг. //Дугарчулуун багшийн номноос", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1115, "Progressive download", "Татах үйл явц", "", "Төрөл бүрийн файл дуу видио татаж эхлэхэд тодорхой бага өгөгдлийн нийлбэрүүд хүлээж авснаас хойш, бүрэн татаж дуусхаас өмнө, файл тоглуулж эхлэхийн өмнөхийг хэлнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1116, "Prompt ", "Яаралтай сануулга", "", "Систем дотор өгөгдөл өгөхөд ямар нэгэн тэмдэгт эсвэл мэдээг хэрэглэгчийн дэлгэцэнд үзүүлнэ. Заримдаа промт нь дуу байдлаар үзэгддэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1117, "Propagation", "", "The process an electromagnetic wave undergoes as it is radiated from the antenna and spreads out across the physical terrain. See also propagation channel.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1118, "Propagation channel", "", "The physical medium electromagnetic wave propagation between the transmit and receive antennas, and includes everything that influences the propagation between the two antennas.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1119, "Proprietary", "Өмч, эзэмшил", "", "Тодорхой байгууллагын худалдааны зориулалтаар үйлдвэрлэсэн цор ганц бүтээгдэхүүн эвсэл технологийг өмч гэж хэлнэ. Зарим бүтээгдэхүүн нь ижил төстэй компаний эзэмшил бүтээгдэхүүнүүдтэй харьцангуй нэг үйл ажиллагаатай байж болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1120, "Protection", "Хамгаалт", "", "Ямар нэгэн юмыг зориудаар эсвэл санаандгүй байдлаар хандах, өөрчлөхөөс сэргийлдэг. Жишээ нь: хамгаалагдсан уян дискэнд файл нэмж бичиж чадахгүй. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1121, "Protection margin", "", "The difference between the signal-to-interference ratio and the protection ratio, these ratios being expressed in logarithmic form.", "", "", "Note 1 – Generally, care is taken to ensure that the difference between the ratios is positive to ensure reliability of communication. Note 2 – Various Recommendations contain definitions for specific applications (e.g. Recommendation ITU-R BO.566).");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1122, "Protection ratio", "", "The minimum value of the signal-to-interference ratio required to obtain a specified reception quality under specified conditions and at a specified point.", "", "", "Note 1 – Various ITU-R Recommendations contain definitions for specific applications. The minimum value is usually laid down in these Recommendations and in other international agreements.  Note 2 – The specified conditions comprise inter alia: – the nature and characteristics of the wanted signal; – the nature and characteristics of the radio-frequency disturbance or the noise and interfering signals; – the receiver and antenna characteristics; – the propagation conditions. Note 3 – A distinction is made for example between: – the radio-frequency (RF) protection ratio; – the video frequency (VF) protection ratio; – the audio-frequency (AF) protection ratio. ");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1123, "Protocol", "протокол", "", "Харилцаа холбоонд төхөөрөмжүүд хооронд өгөгдлийн тохирох дамжууллыг хийх үйл явцыг батлахад ашиглагддаг дүрэмүүдийн стандарт багцыг протокол гэж нэрлэнэ.  Өгөгдлийн формат тодорхойлох бас эхлэх дохионууд, удирдах бас төгөсгөлийн хүлээж авахад протокол нь илэрнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1124, "Prototype", "Туршилтийн загвар", "", "Програмын энгийн хувилбар, систем хэрхэн ажиллахыг үзүүлхийг, загварийн үйл явцийг туршилтийн загв аргэнэ. Туршилтын загвар нь хэрэглэгчидэд ажилж байгаа, програмийн харагдах байдал тэгэхдээ өгөгдлийг боловсруулж болохгүй.Туршилтийн загвар нь хэрэглэгчид шүүмжлэх болон загварь системд өөрчлөлт оруулж болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1125, "Proxy server", "Прокси сервер ", "", "Өөр серверүүдээс эх сурвалж хайж байгаа клиентийн хүсэлтэнт завсрын зуучийн үүрэг гүйцэтгэдэг сервер. Прокси сервер нь компьютер системийн эсвэл хавсрага програм байж болно . Клиент эхлээд прокси серверт холбогдож  өөр серверт байгаа ямар нэг эх үүсгэвэр хүснэ, Прокси сервеорт уг заагдсан серверт холбогдож эх үүсгэвэрийг хүлээн авах эсвэл кэшнээсээ буцаана // Дугарчулуун багшийн номноос", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1126, "Pseudo code", "Хуурамч код ", "", "Програм эсвэл системийн загварыг тодорхойлох арга. Энэ програмын хэлний хатуу дүрмээс гадуур програмын үндсэн хэлтэй адил төстэй түлхүүр үгүүдийг ашигладаг ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1127, "Pseudo-Noise", "", "Historically, the Ministry of Post, Telecommunications and Telegraph. Now a term to describe the incumbent, dominant operator in a country, many of which are being or have been privatized.", "", "PN", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1128, "Pseudo-Noise Complex Quadrature Phase Shift Keying", "", "The spreading technique that uses basic complex scrambling and PN signals for Is and Qs . For more information see Agilent application note 'HPSK Spreading for 3G'.", "", "PNCQPSK", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1129, "Pseudo-Random Binary Sequence", "", "A digital signal having framing information and using pseudo-noise in the individual traffic channels. Commonly used to performance test PCM systems.", "", "PRBS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1130, "Public domain software ", "Нийтийн эзэмшлийн програм хангамж", "", "Нийтийн эзэмшлийн програм хангамж нь зохиогчийн эрхийг хамаарахгүйгээр програм хангамжийг чөлөөтэй хэрэглэх", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1131, "Public Land-Mobile Network", "", "A European term used to describe the GSM system.", "", "PLMN", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1132, "Public Switched Telephone Network", "", "Standard domestic and commercial phone service.", "", "PSTN", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1133, "Public switched telephone network ", "", "", "Үндсэн аналог суурин утасны  сүлжээ нь тоон системээр солигдож  байна.", "PSTN ", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1134, "Pull ", "Таталт", "", "Таталт нь багцаас ямар нэгэн зүйлийг татахын нэр томъёо .", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1135, "Pull down menu ", "Буцааж татах цэс", "", "Буцааж татах цэс  нь  цэсийн bar –д хүсэлт гарч ирэхэд сонгогдсон гарчиг юм доод цэсийн гарчиг хурдан  үзэгдэх нэрүүдийг байнга авдаг ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1136, "Pulse amplitude modulation", "", "A technique for encoding the samples of an analog waveform as part of the PCM process. Also used to display the amplitude of QAM signals in an eye diagram.", "", "PAM", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1137, "Pulse Code Modulation", "", "The most predominant type of digital modulation in use today. PCM performs an analog to digital conversion of the speech waveform through a sampling process and encodes and transmits the samples in a serial bit stream as 8-bit digital words.", "", "PCM", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1138, "Punctured code", "", "A technique used in convolutional decoders that allows a limited number of coded bits to be deleted to greatly simplify processing in the codec. This is extremely useful with long codes.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1139, "Push ", "Түлхэх", "", "Түлхэх нь  багцад ямар нэгэн зүйлииг нэмэх нэр томъёо ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1140, "Push ", "Түлхэх", "", "Хэрэглэгчийн  Browser руу мэдээлэл дамжуудсан үед хэрэглэгчээс  илүү олон тооны хүсэлт ирдэг", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1141, "Quadrature Amplitude Modulation", "", "A type of modulation where the signalling information is carried in the phase and amplitude of the modulated carrier wave.", "", "QAM", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1142, "Quadrature Phase Shift Keying", "", "A type of phase modulation using 2 pairs of distinct carrier phases, in quadrature, to signal ones and zeros.", "", "QPSK", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1143, "Quadrature-Phase", "", "The quadrature phase channel in a phase shift keyed system having more that 2 phase states.", "", "Q", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1144, "Quality of service", "", "A measure of the quality of the signal transmitted over the RF channel. In some systems, the QoS measurement is used to dynamically adjust operational parameters such as transmitter power levels and coding rates.", "", "QoS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1145, "Quantizing", "", "The process of assigning values to waveform samples by comparing the samples to discrete steps.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1146, "Quarantine ", "Тусгаарлалт", "", "Вирусны байрласан байгаа байрлалыг устгахаасаа өмнө идэвхгүй болгодог", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1147, "Quarter CIF (1/4 CIF). A video image format which employs 176 horizontal pixels and 144 vertical lines.", "", "Although resolution is courser than CIF, QCIF consumes less memory while still achieving an acceptable level of clarity on small displays such as those incorporated in mobile phones.", "", "QCIF", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1148, "Query ", "Хариулт", "", "Өгөгдлийн сан дотрох өгөгдлийн асуултанд хариулах. Хариулт бол үнэн эсвэл худал гэсэн хариулт байгуулдаг. Асуултын хариултанд компюьтер бичлэг бүрд шалгаад хариултанд үнэн өгөгдөл харуулна.Хариултын үр дүнд бүх өгөгдлийн жигсаалтанд нийцэх хариулт өгнө. Хариултын өгөгдлийн сан эсвэл өгөгдлийн сангаас ирсэн хариултанд , өгөгдлийг идэвхжүүлэх эсвэл өгөгдөлийг хайх ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1149, "Query language", "Лавлагааны хэл", "", "Өгөгдлийн бааз мэдээллийн системд лавлагааны асуулт үүсгэхэд хэрэглэдэг компьютерийн хэл.Лавлагааны хэл нь өгөгдлийн баазтай харьцах ажиллагааг хялбарчилж өгнө.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1150, "Query ", "Query хэл", "", "Query хэлний нэг төрөл, өөрөөр хэлбэл өгөгдлийн санг зохион байгуулах зориулалт бүхий хялбаршуулсан програмчлалын хэл.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1151, "Queue", "Дэс дараалал", "", "Ямар нэг шинэ багцыг нэмэхдээ багцын төгсгөл дээр нэмэх зарчимтай жагсаалтын төрөл юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1152, "Quit", "Гарах,хаах, дуусгах", "", "Программаас гарах", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1153, "Radar beacon", "", "A transmitter-receiver associated with a fixed navigational mark which, when triggered by a radar, automatically returns a distinctive signal which can appear on the display of the triggering radar, providing range, bearing and identification information.", "", "Racon", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1154, "Radio", "", "Pertaining to the use of radio waves.", "", "", "Note – In French and in Spanish “radio” is always a prefix.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1155, "Radio ", "Радио  ", "Pertaining to the use of radio waves.     ", "Радио долгионыг ашиглахтай хамааралтай.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1156, "Radio (frequency) noise", "", "A time-varying electromagnetic phenomenon having components in the radio-frequency range, apparently not conveying information and which may be superimposed on, or combined with, a wanted signal.", "", "", "Note 1 – In certain cases a radio-frequency noise may convey information on some characteristics of its source, for example its nature and location. Note 2 – An aggregate of signals may appear as radio-frequency noise, when they are not separately identifiable.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1157, "Radio Access Network", "", "The ground-based infrastructure required for delivery of third-generation (3G) wireless communications services, including high-speed mobile access to the Internet. The RAN must be able to manage a wide range of tasks for each 3G user, including access, roaming, transparent connection to the public switched telephone network and the Internet, and Quality of Service (QoS) management for data and Web connections.", "", "RAN", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1158, "Radio button  ", "", "", "Уг товчлуурыг дарахад сонголт хийгддэг цэс. Зөвхөн нэг л сонголт хийгдэнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1159, "Radio Configuration", "", "RC defines the physical channel configuration of cdma2000 (IS-2000) signals. Each RC specifies a set of data rates based on either 9.6 or 14.4 kbps. RC1 is the backwards-compatible mode of cdmaOne for 9.6 kbps voice traffic. It includes 9.6, 4.8, 2.4, 1.2 kbps data rates and operates at Spread Rate 1 (SR1). RC3 is a cdma2000 specific configuration based on 9.6 kbps that also supports 4.8, 2.7, and 1.5 kbps for voice, while supporting data at 19.2, 38.4, 76.8, and 153.6 kbps. RC3 also operates at SR1. For more information see Agilent application note 'Performing cdma2000 Measurements Today'.", "", "RC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1160, "Radio frequency", "", "Electromagnetic waves in the frequency range of 30 kHz to 300 GHz.", "", "RF", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1161, "Radio horizon", "Радио далайц ", "", "Дэлхийн газрын гадаргуу нь цэгээс цэгүүдийг шүргэх төдий радио долгионуудын цацаргуудиг чиглүүлхэд оршино.  ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1162, "Radio horizon", "", "The locus of points at which the direct rays from a point source of radio waves are tangential to the surface of the Earth.", "", "", "Note – As a general rule, the radio and geometric horizons are different because of atmospheric refraction.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1163, "radio link", "Радио шугам", "", "Радио долгионы аргаар заагдсан хоёр цэгийн хооронд тогтоосон онцлог бүхий харилцаа холбооны хэрэгсэл.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1164, "Radio link", "", "A telecommunication facility of specified characteristics between two points provided by means of radio waves.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1165, "Radio link", "", "The equipment and transmission path (propagation channel) used to carry on communications. It includes the transmitting system, the propagation channel and receiving system.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1166, "Radio Network Controller under the UMTS system.", "", "A complex network element of the RAN that connects to and co-ordinates as many as 150 base stations in WCDMA systems. It is involved in managing activities such as hand-over of active calls between base stations.", "", "RNC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1167, "Radio port", "", "The T1P1 PCS architecture model equivalent to the BTS.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1168, "Radio propagation", "", "The science associated with the description of electromagnetic waves at radio frequencies as they radiate from a transmitting antenna.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1169, "Radio relay system ", "Радио релей систем", "", "30 MHz давтамж дээр үйл ажиллагаа явуулж байгаа заасан тогтмол цэгүүдийн хооронд Радио холбоо систем нь  трофосфер тархалт ашиглаж байгаа болон ихэвчлэн нэг буюу түүнээс дээш дунд шатны станц орно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1170, "Radio waves, hertzain , waves ", "Радио долгионууд , герцийн, долгионууд", "", "3000 GHz-ээс бага давтамжтайгаар огторгуйд тарха үзэгдлийг радио долгион гэнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1171, "Radio waves, hertzian waves", "", "An electromagnetic wave propagated in space without artificial guide and having by convention a frequency lower than 3 000 GHz.", "", "", "Note – The electromagnetic waves having frequencies around 3 000 GHz may be regarded either as radio waves or optical waves.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1172, "Radiocommunication", "Радио холбоо", "", "Радио долгион ашиглан  Харилцаа холбоо тогтоох", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1173, "Radiocommunication", "", "Telecommunication by means of radio waves.", "", "", "Note – The definition of the term “telecommunication” is included in Appendix 2 of Recommendation ITU-R V.662 dealing with general terms.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1174, "Radio-frequency disturbance", "", "Any electromagnetic phenomenon having components in the radio-frequency range, which may degrade the performance of a device, equipment or system, or affect adversely living or inert matter.", "", "", "Note – A radio-frequency disturbance may be a radio-frequency noise, an unwanted signal or a change in the propagation medium itself.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1175, "Radio-frequency interference", "", "Degradation of the reception of a wanted signal caused by a radio-frequency disturbance.", "", "RFI", "Note 1 – Often man-made noise is not included in interference. Note 2 – Various levels of interference are defined for administrative purposes in the Radio Regulations viz. permissible interference (RR No. 1.167), accepted interference (RR No. 1.168) and harmful interference (RR No. 1.169). The first term describes a level of interference which in the given conditions involves degradation of reception quality to an extent considered insignificant, but which must be taken into account in the planning of systems. The level of permissible interference is usually laid down in ITU-R Recommendations and/or other international agreements. The second term describes a higher level of interference involving a moderate degradation of reception quality which in given conditions is deemed to be acceptable by the administrations concerned. The third term describes a level of interference which “seriously degrades, obstructs, or repeatedly interrupts a radiocommunication service”. Note 3 – The English words “interference” and “disturbance” are often used indiscriminately; the expression “radio-frequency interference” is also commonly applied to a radio-frequency disturbance or to an unwanted signal.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1176, "Radio-frequency radiation", "", "1. The phenomenon by which energy in the form of electromagnetic waves, in the radiofrequency range, emanates from a source into space. 2. Energy transferred through space in the form of electromagnetic waves in the radiofrequency range.", "", "", "Note – By extension the term “radio-frequency radiation” sometimes also covers induction phenomena.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1177, "Radio-relay system", "", "Radiocommunication system between specified fixed points operating at frequencies above about 30 MHz which uses tropospheric propagation and which normally includes one or more intermediate stations.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1178, "Rake receiver", "", "A radio receiver having multiple 'fingers' and utilizing off-sets of a common spreading code to receive and combine several multipath (time delayed) signals, in effect using 'time diversity' to overcome deep fades.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1179, "Random", "Санамсаргүй, тохиолдлоор", "", "Санаандгүй сонгогдсон ямар нэгэн зүйл.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1180, "Random access", "", "A technique for radio access to a network where an access message is not coordinated or administered by the network and can collide with other attempts by others to access the network over the same channel.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1181, "Random Access Channel", "", "The channel used by mobiles in GSM and W-CDMA systems to attempt to gain access to the system when first attaching to it.", "", "RACH", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1182, "Random access memory ", "Шуурхай санах ой", "", "Шуурхай санах ой гэдэг нь компьютер ажиллаж байх үед ашиглаж буй санах ой юм. Компьютерийн суурин дискэнд өгөгдөл хадгалах явцад тухайн өгөгдөл RAM-д түр хадгалагдана.", "RAM", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1183, "Range check", "Ранжи чек", "", "Гаднаас дохио болон өгөгдөл оруулах үед ашиглагддаг үнэлгээний шалгалтын нэг төрөл.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1184, "Raster graphics", "Растер график", "", "Компьютерийн дэлгэц дээр дүрс харуулдаг төхөөрөмжийн тусламжтай харагддаг пиксель хэмээх өнгөт дөрвөлжин цэгүүдийн торолсон бүтэцтэй нийлбэрээс дүрс үүсгэдэг технологи.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1185, "Raw data", "Боловсруулаагүй өгөгдөл", "", "Ямар нэг үнэлгээ эсвэл үйл ажиллагаа эхлэхийн өмнө компьютерт оруулах өгөгдөл", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1186, "Ray path", "Цацрагын чиглэл ", "", "Тархалтын чиглэл нь цэгээс цэг тус бүрийг шүргэх төдий чиглэлтэй юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1187, "Ray path", "", "At each point, the path tangential to the direction of propagation of energy at this point.", "", "", "Note 1 – The concept of ray is the basis of the geometrical optics which, when applicable, permits the substitution of simple relationships for Maxwell's equations. Note 2 – In some cases, several paths may exist between two points. Note 3 – In an isotropic medium, the ray path is a trajectory orthogonal to the wavefronts and the term 'ray' is often defined as this trajectory. In an anisotropic medium, the trajectories orthogonal to the wave fronts do not always coincide with physical paths between a source and a receiving point and should not be called rays.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1188, "Ray path transmission loss", "", "The transmission loss for a particular ray propagation path, equal to the basic transmission loss minus the transmitting and receiving antenna gains in the ray path directions.", "", "", "Note – The ray path transmission loss may be expressed by: Lt = Lb – Gt – Gr dB (4) where Gt and Gr are the plane-wave directive gains of the transmitting and receiving antennas for the directions of propagation and polarization considered.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1189, "Rayleigh channel", "", "A communications channel having a fading envelope in the form of the Rayleigh Probability Density Function.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1190, "Rayleigh fading", "", "A type of signal fading caused by independent multipath signals having a Rayleigh PDF.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1191, "Read", "Унших", "", "Уг файл нь  өгөгдөл доторхийг уншина.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1192, "Read only memory", "Зөвхөн уншиж болох файл", "", "Хэрэглэгч нээж үзэж болох хэдий ч устгаж, өөрчлөлт хийж болохгүй файл.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1193, "Read/write head", "Унших болон бичих толгой", "", "Диск болон кассетыг унших бичих зориулалттай диск ба кассетны бүрэлдэхүүн  хэсгүүдийн нэг", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1194, "Read-only memory ", "Тогтмол санах ой", "", "Тогтмол санах ой бөгөөд ихэнхдээ компьютерийн эх хавтан дээр суулгагдсан тусгай зориулалтын микросхем байдаг ба дахин бичигдэх боломжгүй санах ой юм.Сүлжээний карт болон видео картууд тогтмол санах ойн микросхемийг агуулсан байдаг.", "ROM", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1195, "Ready", "Бэлдэх, Бэлэн байх", "", "Өгөгдөл хүлээн авахад бэлэн байх. Жишээ нь: Принтер залгаатай ба чөлөөтэй үед", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1196, "Real  type", "Бодит Төрөл", "", "Бутархай тоог агуулсан тоонууд.  Жишээ нь: 5.76, 34.835 гэх мэт. Float гэж нэрлэдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1197, "Real numbers", "Бодит тоо", "", "Бутархай тоог агуулсан тоонууд. Жишээ нь: 5.76, 34.98 гэх мэт.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1198, "Real Time ", "Бодит Хугацаа", "", "Систем нь гадны ямар нэгэн үйл ажиллагаанд түргэн шуурхай хариу өгөх, цаг хугацааны алдагдалгүй байх  жишээ нь: Агаарын хяналтанд хэрэглэнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1199, "Reboot ", "Дахин Ачааллах", "", "Компьютерийг дахин ачааллах систем. Компьютер гацах үед ашиглана. Бүх параметрүүд анхны байрлалдаа орж,өгөгдөл болон утгууд алдагдагдвал хадгалагдахгүй.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1200, "Receive diversity", "", "The process of providing two independent receiving systems and spatially separated antennas to overcome fading effects on the radio signal.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1201, "Received Signal Strength Indication", "", "An indication of the average signal strength at the input of a receiver produced by measurement circuitry in the receiver. Such a measurement does not normally include antenna gain or transmission system losses.", "", "RSSI", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1202, "Receiver", "", "Arrangement of active components such as the LNA, mixer and IF amplifier together with passive components such as the image filter and IF filter. Taken together they perform the task of recovering the modulation from a known RF signal while rejecting unwanted signals. The portion of the communication system that includes a detector and signal processing electronics to convert electrical signals (electric waves) to audio or data signals. It provides reception and, if necessary, demodulation of electronic signals.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1203, "Record", "Бичлэг, Бүртгэл", "", "Мультимедиа программ хангамж нь дуу болон дүрс бичлэгийг тоон хэлбэртэй файл болгон хадгална.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1204, "Record format ", "Бичлэгийн формат", "", "Бичлэгийн формат нь тодорхой бичлэгт агуулагдах нөхцлүүдийг тодорхойлон заасан байдаг ба эдгээрт нь талбарууд, жишээ, талбарын төрөл, талбарын урт, талбарын нэр зэрэг орно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1205, "Record number ", "Бичлэгийн дугаар", "", "Бичлэгийн файлд байрлал тогтоохын тулд давтагдашгүй дугаарыг ашигладаг. Анх бичлэгийн физик байрлал ба өгөгдлийн байрлалыг энэ аргаар тогтоодог байсан. Санамсаргүй файлууд болон файлын бүтцэд бичлэгийн дугаарыг ашигладаггүй.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1206, "Record ", "Бичлэг", "", "Бичлэг нь дэс дараалсан өгөгдлийн файл юм. Бичлэг нь нэгж, талбар болон ялгаатай өгөгдлийн төрлүүдийн цуглуулга бөгөөд эдгээрийг хамааруулсан объектоор бичлэгийг илэрхийлдэг. Жишээ нь: Бүх ажилчдын цалингийн мэдээлэл өгөгдлийн санд байдаг. Тухайн ажилчны бичлэг буюу талбарт түүний нэр, иргэний үнэмлэхний дугаар, төрсөн өдөр, хөдөлмөрийн хөлсний хэмжээ гэх мэт багтана. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1207, "Recover", "Сэргээх", "", "Эх файлыг эргүүлэн сэргээх, жишээ нь ямар нэг эвдэрсэн файл байж болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1208, "Recursion", "Рекурс (програмчлалын арга)", "", "Програмд өөрөө өөрийгөө дууддаг дэд програм юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1209, "Recycle bin", "хогийн сав", "", "Үйлдлийн системээс устгагдсан файлууд хогийн саванд ордог. Тэндээсээ сэргээгдэж болно, үгүй бол хогийн савнаас устгаж болдог.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1210, "Red, green, blue", "", "", "Улаан,ногоон,цэнхэр. График орчинд вэб загварчлал ба компьютерийн дэлгэцийг загварчлахад бүх өнгийг энэ 3 өнгөнөөс гаргаж авдаг.", "RGB", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1211, "Redirect", "Өөр хаягаар буцааж илгээх", "", "Буруу хүн рүү цахим шуудан илгээвэл, түүнийгээ зөв хүн рүү өөр хаягаар буцааж илгээдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1212, "Redo ", "Дахин хийх", "", "Ямар нэг үйлдлийн дахин хийнэ гэдэг нь дуусаагүй үйлдлийг дахин хийхийг хэлнэ.Энэолон програмд нийтлэг байдаг функц юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1213, "Reduced carrier", "Багасах зөөгч дохио", "", "дулааны урсгал,синуслэгзөөгчбүрэлдэхүүнхүчюмдалайцынмодуляцбүхийдамжуулах, эсвэлдулааны ялгаралтай холбоотойоргилхүчдэлдоор 6-аас дээшдббуурсанхэдий ч энэньшинээрзохионdemodulationашиглажболохиймтүвшиндбайна.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1214, "Reduced carrier", "", "Pertaining to a transmission or emission with amplitude modulation where the power of the sinusoidal carrier component is, by convention, reduced by more than 6 dB below the peak envelope power but remains at such a level that it can be reconstituted and used for demodulation.", "", "", "Note 1 – The level of the reduced carrier is normally between 6 dB and 32 dB and preferably between 16 dB and 26 dB below the peak envelope power of the emission. Note 2 – The reduced carrier may also be used to achieve automatic frequency control and/or gain control at the receiver.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1215, "Reduced carrier ", "Багасах зөөгч дохио", "", "Конвенцоор синуслэг зөөгч бүрэлдэхүүн хүч юм далайцын модуляц бүхий дамжуулах, эсвэл дэгдэлт холбоотой оргил дугтуй хүч доогуур 6dB илүү буурсан хэдий ч энэ нь шинээр зохион demodulation ашиглаж болохийм түвшинд байна.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1216, "Reduced instruction set computer ", "", "", "Цомхон бөгөөд энгийн команд илүү хурдан гүйцэтгэгдэнэ гэсэн зарчимд тулгуурладаг төв процессорын хийц төлөвлөлтийн арга юм. Архитектурын хувьд энгийн байх нь процессорын өртгийг хямдруулаад зогсохгүй, хэмийн давтамжийг өсгөн, командын гүйцэтгэлийг хэд хэдэн гүйцэтгэх блокуудад зэрэгцээ хуваарилах боломжтой болгодог байна. ", "RISC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1217, "Redundancy ", "Нөөц төлөв", "", "Компьютер системд нөөц төлөв гэдэг ижил үүрэг гүйцэтгэдэг функцүүдээс бүрдэх нөөц бүрэлдэхүүн байдаг, тиймээс бүрэлдэхүүн гэмтэхэд нөөц (backup) удирдлагыг гартаа авч систем үргэлжилэн ажилладаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1218, "Reed Solomon code", "", "A particular implementation of the BCH block (cyclic) coder capable of correcting double errors.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1219, "Reference frequency", "", "A frequency having a fixed and specified position in respect to the assigned frequency.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1220, "Reference usable field-strength, [reference usable power flux-density]", "", "The agreed value of the usable field-strength [the agreed value of the usable power flux-density] that can serve as a reference or basis for frequency planning.", "", "", "Note 1 – Depending on the receiving conditions and the quality required, there may be several reference usable field-strength [reference usable power flux-density] values for the same service. Note 2 – Where there is no ambiguity, the term “reference field-strength” [“reference power flux-density”] may be used.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1221, "Reflecting satellite", "", "A satellite intended to reflect radiocommunication signals.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1222, "Reflection", "", "A process that occurs when a propagating electromagnetic wave impinges upon a obstruction whose dimensions are very large when compared to the wavelength. Reflections from the surface of the earth, and from buildings or walls produce reflected waves which may interfere, constructively of destructively at the receiver.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1223, "Refresh rate", "Шинэчлэх давтамж", "", "Шинэчлэх давтамж нь дэлгэцэнд дүрсийг секунд бүрт давтах тоо хэмжээгээр тодорхойлогдоно. Шинэчлэх давтамж нь бага бол хэлбэлзэх хандлагатай болдог.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1224, "Region", "", "A geographic area over which a WCS operator is licensed to provide service. REAGs are a group of economic areas (EAs) and were first used to license WCS service in the late '90s. REAGs are very large, with 6 REAGs covering the entire continental United States.", "", "REAG", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1225, "Registration", "", "This is the process by which a mobile station informs the immediate service provider of its presence in the network and its desire to receive service.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1226, "Regular Pulse Excited-Long Term Prediction", "", "A type of speech coding using regularly spaced pulses in an excitation frame and a long term predictor to model the fine structure (pitch).", "", "RPE-LTP", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1227, "Reject(a mailing list posting)-", "Татгалзах, няцаах, хүлээж авахгүй байх", "", "Пост нь зохицуулагчаар татгалзах үед хэрэглэгчийн хаягийн жагсаалт болон мэдээллийн самбарт харагдах боломжгүй.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1228, "Relational database", "Харьцуулалттай өгөгдлийн бааз", "", "Харьцуулалттай өгөгдлийн баазийн хүснэгтүүдэд нэг давхцсан /адилхан/ талбар байх ёстой. Энэ давхцагч талбар нь нэг хүснэгтийн нөгөө хүснэгттэй холбох холбоос байх болно. Тэгэхээр relationship(холбогдол) гэдэг нь нэг хүснэгтийн бичлэгийг бичлэгтэй холбох холбоос юм. Үүний ачаар тус тусдаа хүснэгтүүд хоорондоо харилцан уялдаа холбоотой болж цогц өгөгдлийн санг үүсгэх юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1229, "Relational operator", "Харьцуулалтын оператор", "", "Харьцуулалтын операторууд нь өгөгдөл ба бүтээгдэхүүнийг харьцуулж үнэн эсвэл худал гэсэн хариулт өгдөг. IF, WHILE эсвэл REPEAT UNTIL буюу давталтуудыг ашиглан програмыг удирдаж чаддаг. Жишээ нь: include= and < гэх зэрэг харьцуулах операторууд байдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1230, "Relative reference", "Харьцангуй лавалгаа", "", "Харьцангуй лавалгаа гэдэг нь томъёог хүснэгтийн нэг үүрнээс өөр өөр үүрэнд хуулбарлахыг хэлнэ. Ер нь томъёог өөр үүрэнд зөөхдөө үнэмлэхүй  ба харьцангуй аргын аль нэгийг ашиглах бөгөөд харьцангуй лавалгаань өөр орчинд харьцангуй өөрчлөгддөг ба үнэмлэхүй лавалгаа нь  өөр орчинд үл өөрчлөгдөдөг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1231, "Release", "", "", "Програм хангамжид release нь олон нийт ашиглах боломжтой хийгдсэн програм хангамжийн шинэчилсэн хувилбар юм. Гаралтууд ер нь бүхэл тоо харуулдаг, ба гаралтын дугаар нь хувилбарын хэд дэх хувилбар болохыг аравтын тоогоор илэрхийлдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1232, "Remote", "Алсын зайн удирдлага", "", "Суурин биш юмыг илэрхийлэх, жишээлбэл телефон утасны шугамаар алсын зайн компьютерт холбогдох.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1233, "Remote access", "Алсын зайн хандалт", "", "Алсын зайн хандалт бол холбооны шугамаар газарзүйн байрлалын хувьд алслагдмал компьютер системийг ашиглах.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1234, "Remote sensing satellite", "", "A satellite whose purpose is remote observation by reception of electromagnetic waves using active or passive sensors (these two types of sensors are defined in this Recommendation, numbers H31 and H32).", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1235, "Remote testing", "Алсын зайн шалгалт", "", "Зохион бүтээгчийн суурин компютерээс өөр компьютер ашиглан шалгах.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1236, "Rename", "", "", "Файлд өөр нэр өгөх", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1237, "Repeater", "", "Receives radio signals from the base station. They are then amplified and re-transmitted to areas where radio shadow occurs. Repeaters also work in the opposite direction, i.e. receiving radio signals from mobile telephones, then amplifying and re-transmitting them to the base station.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1238, "Research in Advanced Communications Equipment", "", "An ETSI research project that has subsequently been replaced by ACTS.", "", "RACE", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1239, "Reuse factor", "", "Also known as frequency reuse factor, is the number of distinct frequency sets used per cluster of cells.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1240, "Reverse link", "", "See uplink.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1241, "Rewind", "Дахин эргүүлэх", "", "video файлыг эхнээс нь эхлүүлнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1242, "Rich Site Summary or Really Simple Syndication", "", "", "Эрэлтэй site-ийн хураангуй эсвэл маш энгийн мэдээлэл гэсэн утгатай. RSS-ээр аливаа site дагах юм бол тэр site шинээр мэдээ нэмэх бас өөрчлөлтийг чамд мэдэгдэнэ.", "RSS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1243, "Rician channel", "", "A transmission channel that may have a line-of-sight component and several scattered of multipath components. This fading characteristic exhibits a Rician PDF.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1244, "Rician fading", "", "A type of signal fading having a characteristic similar to the Rician PDF.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1245, "Right click", "Баруун товч", "", "Энэ бол хулганы баруун талын товч юм. Дэлгэн дээрх жижиг зурган дээр хулганы баруун талын товчийг даран файлын нэмэлт мэдээлэл авч болно. Жишээ нь: Жижиг жагсаалт бүхий меню юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1246, "Right-hand polarization, clockwise polarization", "", "An elliptical polarization for which the electric flux-density vector observed in any fixed plane not containing the direction of propagation, whilst looking in this direction, rotates with time in a right-hand or clockwise direction.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1247, "Right-hand-polarization clock wise polarization", "Баруун гарын дагуу туйлшрах, цагын зүүний дагуу туйлшрах ", "", "Тогтмол туйлшралын хөдөлгөөний чиглэл нь вектор,богинхон нигтралтай тархалтын төршин багтаамжгуй цахилгаанжсан .энэ чиглэл нь хархад хоорондоо баруун гарын дагуу,цагын зүүний дагуу чиглэлтээ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1248, "Ring network", "Бөгжин сүлжээ", "", "Орон нутаг хоорондын сүлжээнд хэрэглэдэг.Бөгж холболт нь төхөөрөмж бүрийг хооронд нь цуваагаар тойрог хэлбэртэй холбон мэдээллийг дамжуулахыг хэлнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1249, "Roaming", "", "Within your home network, this means that your mobile phone automatically sets up communication procedures with different radio base stations when on the move. International roaming means that you can use networks other than your own when traveling abroad.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1250, "Robot", "", "", "Хүний хийж байгаа үйлдлийг хийж чаддаг бөгөөд хүний удирлагаар болон бие даан  ажиллах чадвартай автомат төхөөрөмж юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1251, "Rogue value", "Хуурамч утга", "", "Хуурамч утга нь тодорхой утгатай, энгийн мэдээлэл тооцоолоход хэрэглэдэггүй энийг мэдээлийн жагсаалтын төгсгөлд бичиж өгдөг. Энэ утга хүртэл програм ажиллаад зогсдог. Дугаарлахад янз бүрээр цаанаасаа дугаарлаж өгдөг. Хуурамч утга үргэлж тоон утга агуулдаг. Энэ нь мөн онцгой утгыг илэрхийлдэг ба өгөгдлийн зарим нэг тохиолдолд, жишээ нь мэдээлэлийг устгах гэх мэт онгой нөхцлийг тэмдэглэдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1252, "Rolleover", "", "", "Хулганы сумыг түүн дээр хөдөлгөснөөр зургийн харагдах байдлыг өөрчилдөг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1253, "Rotate", "Эргүүлэх", "", "График орчинд , зургийг нар зөв эсвэл нар буруу эргүүлнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1254, "Rounding", "Ойролцоо", "", "Тооны ойролцоо утга буюу бутархай тоог ойролцоо бүхэл утганд шилжүүлдэг. Жишээ нь 1,36 ойролцоогоор 1,4 болгоно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1255, "Router", "Цацагч", "", "router нь сүлжээний маш нарийн холбох төхөөрөмж юм. Зөв замаар зөв байрлал руу өгөгдлийг дамжуулахын тулд компьютерийн хаягийг хамт дамжуулдаг. Ерөнхийдөө том сүлжээнд ашиглах ба интернетэд орон нутгийн сүлжээг холбож өгдөг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1256, "Router", "", "A data switch that handles connections between different networks. A router identifies the addresses on data passing through the switch, determines which route the transmission should take and collects data in so-called packets which are then sent to their destinations.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1257, "Routing", "", "The forwarding of data packets in packet-switched networks, to the intended address", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1258, "Row", "Эгнээ", "", "Хүснэгт болон маягтын хэвтээ нүднүүд юм. Маягт дээр эгнээ бүрийг дугаарласан байдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1259, "Ruler", "Шугам", "", "Ихэнхдээ бичиг хэргийн програм, төсөл зургийн програм гэх мэт зүйлд ашигладаг. Нарийн холболттой хүснэгтүүд, төсөл зургийн нарийвчлалтай зурахад ашигладаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1260, "Run", "Ажиллуулах", "", "Энэ коммандыг хийснээр програм ажиллаж эсвэл ажиллахад бэлдэж эхэлнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1261, "Run length coding", "", "A type of video coding used in H.261 and H.263 codecs", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1262, "Run time", "Ажиллах хугацаа", "", "Заасан хугацааны турш програм ажиллана.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1263, "Runtime error", "Ажиллах явцын алдаа", "", "Программ ажилаж байх үед гарах алдаанууд.Санах ойн дутагдал эсвэл байхгүй өгөгдөл нь програм дээр дахин ажилах боломжгүй болж программын логикт эсвэл үр дүнд алдаа гархыг хэлнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1264, "Rural Service Are", "", "A geographic area over which a cellular operator is licensed to provide service. RSAs are a group of rural counties having common financial, commercial and economic ties and were used to license cellular service Rural areas in the latter '80s. RSAs cross state lines in some instances and were developed during a public rule making process at the FCC in 1987 and 1988..", "", "RSA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1265, "sampling", "", "The first process performed in the conversion of analog waveforms to a digital format. It converts a continuoustime signal into a discrete-time signal or sequence of numbers.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1266, "Satellite", "", "A body which revolves around another body of preponderant mass and which has a motion primarily and permanently determined by the force of attraction of that other body", "", "", "Note – A body so defined which revolves around the Sun is called a planet or planetoid.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1267, "Satellite link", "", "A radio link between a transmitting earth station and a receiving earth station through one satellite. A satellite link comprises one up-link and one down-link.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1268, "Satellite link ", "Хиймэл дагуулын шугам", "", "Нэг хиймэл дагуулаар дамжуулан дэлхий дамжуулах станц болон хүлээн авах газар станцын хоорондох радио холбоос. Сансрын холбоос нь нэг буцах шугам , илгээх шугамнаас бүрдэнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1269, "Satellite network", "", "A satellite system or a part of a satellite system, consisting of only one satellite and the cooperating earth stations.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1270, "Satellite phone", "", "A type of wireless mobile telecommunications system using satellites as base stations. Such systems have the ability of providing service to the oceans and other remote areas of the globe.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1271, "Satellite system", "", "A space system using one or more artificial satellites.", "", "", "Note – If the primary body of the satellite or satellites of a specific system is not the Earth, it should be identified");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1272, "Save", "Хадгалах", "", "Хадагалхыг хүссэн файлыг хадгалах орчин луу  илгээдэг. Ж/нь disc эсвэл CD", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1273, "Save-as", "", "", "Үсгийн фонтны хэлбэр дүрсийг өөрчлөлтгүй томруулж жижигрүүлдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1274, "Scan", "", "", "Бичиг баримтыг компьютерт оруулан ашиглахын тулд скайнер ашиглан шинжих (scan) үйлдлийг хийж тоон буюу дижиталь зураг болгодог.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1275, "Scattering", "", "A phenomenon that occurs when the medium through which a radio wave travels consists of objects with dimensions small compared to the wavelength and diffuses the wave as it propagates through it.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1276, "Search routine ", "Тогтмол хайлт", "", "Хэсэг программын кодыг хэрэгжүүлсэн хайлт", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1277, "Second adjacent channel", "", "In a given set of radio channels, the RF channel whose characteristic frequency is situated next above that of the upper adjacent channel or next below that of the lower adjacent channel.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1278, "Secondary key", "Хоёрдогч түлхүүр", "", "Гол нь дээд амжилт тогтооход ашигладаг бичлэгийн талбар.Ихэнх өгөгдлийн файлууд өвөрмөц, дээд амжилт тогтооход ашиглаж байгаа гол түлхүүр болно. Өгөгдлийн файл дахь бичлэгүүд нь дараалан хандаж,  бүртгэл гол салбарт хандаж болно. Өгөгдлийн файл нь файлыг боломж олгох хоёрдогч түлхүүр, өөр дарааллаар хандаж болох юм. Гол нь файлаа ялгах төрлийн түлхүүр болгон дээд амжилт олоход ашиглаж болно. Нэгээс олон салбарт бүрдсэн нэгдсэн түлхүүр, файлыг нь ангилахын тулд ашиглаж болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1279, "Second-generation computer", "Хоёр дахь үеийн компьютер", "", "Транзистор нь үндсэн бүрэлдэхүүн хэсэг болох хаалтыг солих үед Хоёр дахь үеийн компьютер ирсэн байна. Тэд улмаар илүү найдвартай болж, бага эрчим хүч хэрэглэж байна. Тэд хямд боловч ихэнх нь бизнест хол, мэдээж хувь хүмүүс зориулсан байна.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1280, "Section break", "Хэсэг бүлэг өөрчлөлт", "", "Үг үсэг боловсруулалт нь хэсэг бүлэг бичиг баримтыг хуваах, олон форматтайгаар байрлал өөрчлөх.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1281, "Sector", "Заагч", "", "Диск дээрх замын хаалт дугаар болон ижил зайнд хуваагдсан уулзах загвар юм. Диск нь өгөгдлийг унших, болон бичих байгууламж.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1282, "Sector", "", "A physical coverage area associated with a base station having its own antennas, radio ports and control channels. The concept of sectors was developed to improve cochannel interference in cellular systems and most wireless systems use three sector cells.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1283, "Secure Socket Layer ", "", "", "2 компьютерийн хоорондхолбожинтернэтийн орчинд хувиргалт бий болгох.Гэвч элекроник харилцаа үүссэнээр сэрверээр дамжуулж байна.", "SSL", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1284, "Security", "Нууцлал, хамгаалал", "", "Хамгаалалт нь гол төлөв лавлах хамгаалалтын өгөгдөл байна. Энэ хандалт нь нууц үг хязгаарлах эсвэл харгалзах файлыг аюулгүй болгох бас баталгаа өгөх ердийн нөөц болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1285, "Seek time", "", "", "Дискний зам дээрх толгой файлыг (уншихболонбичих ,хуулах) үйлдлийг SEEK TIME гэнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1286, "Segment", "Сегмент", "", "Жижигпрограм буюусегментнь хэд хэдэнтомоохонпрограмхуваажбайна.Сегмент тус тусад ньажиллуулахбүрэнпрограм юм.Хэсгийнньтом програмпрограмыг бүгдийг ньхадгалаххангалттайсанах ойнькомпьютер дээражиллуулахболомжийг олгодог.Хэд хэдэн томоохонхөтөлбөрүүдийн нөөцийгхамгийн сайнашиглахын тулд, хэсэгт, нэгэн зэрэгажиллуулах боломжтой байдагболохоорхэд хэдэнхөтөлбөрсегмент, багцалсанболно.Эдгээр аргаөргөнулмаасорчин үеийнкомпьютерхарьцангуй ихсанах ойгодоохэрэглэжбайна.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1287, "Select", "", "", "Тухайн текстийг хулганы заагчаар тодруулж зөөх болон арилгах үйлдэл.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1288, "Select", "", "", "Хулганы зүүн товчийг дарсанаар файл болон фолдерийг дэлгэцний заагч сум ашиглан сольж болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1289, "Select", "", "", "SQL ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1290, "Select all", "Бүгдийг сонгох", "", "Хэрэглэгчийнбүх зүйлийгсонгохболомжийг олгодог.Хулганаэсвэлгарынтовчашиглан үйлдэл гүйцэтгэнэ.Үйлдлийгявуулахөмнө, өөрөөр хэлбэл устгах, засах,  фонт өөрчлөх.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1291, "Selective Transmit Diversity", "", "A transmit diversity technique using multiple base stations to originate the signal and provide spatial diversity on the downlink. In STD, the transmitter selection is based on a QoS measurement made at the mobile station. See also transmit diversity, TDTD and TSTD.", "", "STD", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1292, "Self extracting archive", "", "", "Файлууд бас байдагньхуулжтатаж аваххэмжээг багасгахзорилгоорнэгболгоннэгтгэжбайгааүед энэ ньхэрэгжихтусдаахандаххөтөлбөрийншаардлагагүйгээр, файлуудыгялгаж,өөрөөзадалжархивлана.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1293, "Sent mail", "Майл илгээх", "", "Sent mail нь хэзээ ямар газраас илгээгдсэн нь эмайл програм дотор хадгалагдсан байна.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1294, "Sequence", "Дэс дараалал", "", "Ямар нэгэн зүйл гаралтандаа зөөх дараалал юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1295, "Sequential access", "Дараалсан хандалт", "", "Дэс дараалан хандах зүйлс нь  файлын логик эхлэх  утга  нь, нэг удаа нэг унших зориулалтай байна.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1296, "Sequential file", "Дараалсан файл", "", "Дарааллын дагуу физик өгөгдөл цуваагаар  дамжихийг хэлнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1297, "Serial access", "Дараалсан /// Цуваа хандалт", "", "Цуваахандалт нь хаана уншижбайгааг,файлын физик эхлэх утга хадгалах дарааалалтай байна.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1298, "Serial data transmission", "Цуваа өгөгдөл дамжуулах", "", "Цувааөгөгдөл дамжуулахадижилөгөгдөлшугамын дагуударааланньтэмдэгтийннэгбитилгээдэг.Энэ ньцуваанэвтрүүлгүүд, зөвхөн хоёрутас(өгөгдлийн шугам,газар, буцах мөр) шаардагдана.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1299, "Serial file", "", "", "Сериал файл гэдэг нь дараалсан өгөгдлүүдийн нэгдэл юм. Хэрвээ тухайн нэг бичлэгийг сэргээх шаардлага гарвал бүх бичлэгийг  нь унших хэрэгтэй. Хэрвээ бичлэг болохгүй байвал устгагдсан файлын төгсгөл хүртэл унших боломжтой. Устгагдсан бичлэг нь интервалд бүр мөсөн устах тул дахин ашиглах боломжгүй мөн бүрэн сэргээх боломжгүй юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1300, "Serial line internet protocol ", "Цуваа шугамын IP", "", "Зөвхөн телефоны шугам ашиглан хувийн  компьютертай хэрэглэгчдэд интэрнэтийн үйлчилгээг ашиглах боломж олгодог интернэт протокол. Гэвч TCP/IP протоколыг бүрэн ашигладаг.Тухайн хэрэглэгчийн хувьд бүрэн интернэтийн хандалттай байх ёстой.", "SLIP", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1301, "Server", "", "", "Зөвшөөрөлтэй сүлжээн дэх бусад дурын компьютеруудад ашиглагдах нөөцийг хангах сүлжээний компьютер.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1302, "Server side", "", "", "Сүлжээн дэх сервер дээр болж байгаа үйл явдлыг чиглүүлэхийг хэлнэ.Хэрэглэгчийн машин биш.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1303, "Server side includes ", "", "", "SSI скрипт ньвеб хуудас дотор агуулагдсан байна.Скрипт нь веб хуудсыг хэрэглэгчийн машин руу илгээхээс өмнө сервер дээр хийгддэг.Жишээлбэл: Веб хуудасны дотор гаднаас файл оруулах.  ", "SSI", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1304, "Service arc", "", "The arc of the geostationary satellite orbit within which the space station could provide the required service (the required service depends upon the system characteristics and user requirements) to all of its associated earth stations in the service area.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1305, "Service area", "", "Area associated with a station for a given service and a specified frequency under specified technical conditions where radiocommunications may be established with existing or projected stations and within which the protection afforded by a frequency assignment or allotment plan or by any other agreement must be respected.", "", "", "Note 1 – Several separate service areas involving both reception and/or transmission, may be
associated with the same station.
Note 2 – The technical conditions include the following: characteristics of the equipment used
both at the transmitting and receiving stations, how it is installed, quality of transmission desired
and operating conditions");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1306, "Service area", "", "The specified area over which the operator of a wireless communications network or system provides services.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1307, "Service pack", "", "", "Service pack нь програм хангамж дээр гарч буй асуудал байвал түүнийг засварлах програмууд болон  өгөгдлийн файлуудын нэгдлийг хэлнэ.Энэ нь анх гаргасан програм хангамжийн хэсгийг дарж суулгасан байна.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1308, "Service Provider", "", "A company that provides services and subscriptions to telephone, mobile phone and Internet users.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1309, "Set", "Тохируулга", "", "Ямар нэг зүйлийг үнэн зөв хийхийн тулд шаардлагатай тохиргоог зөв хийх.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1310, "Settings", "Тохиргоо", "", "Системийн статус болон сонгогдсон шинж чанарыг тайлбарлах ерөнхий нэр томъёо юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1311, "Set-up Audio Tone", "", "An audio tone in the 6 kHz range added to the downlink or forward channel in analog cellular systems. The mobile detects and returns the tone. The SAT tone is used to determine channel continuity, and only one SAT tone is usually assigned to a base station or sector.", "", "SAT", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1312, "Shadow fading", "", "A phenomenon that occurs when a mobile moves behind an obstruction and experiences a significant reduction in signal power.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1313, "Shared Secret Data", "", "Part of an encryption process supporting authentication of mobile phones. It uses an encryption key installed in the phone at the time of activation and known to the system through an entry in the HLR, that protects signalling and identity information. It can also be used to establish a voice privacy key.", "", "SSD", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1314, "Shareware", "", "", "᠋᠌᠌᠌᠌Програм хангамжын туршилтын хугацаанд түүнийг үнэгүй ашиглах (гол төлөв түгээлт) олгох тусгай зөвшөөрөл юм. Хэрэв хэрэглэгч цаашид үргэлжлүүлэн ашиглах хүсэлтэй бол төлбөр төлөх ёстой. Ингэснээр сайжруулсан хувилбар болон гарын авлагыг авах боломжтой.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1315, "Sheet", "Хуудас", "", "Хүснэгттэй ажиллах програм хангамжын ажлын талбар.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1316, "Sheet feeder", "Хуудас нийлүүлэгч", "", "Хэвлэгчийн хувьд: хуудас тайран нийлүүлгч (мөн хуудас нийлүүлэгч гэж нэрлэдэг) хуудсыг тус тусд нь автоматаар хэвлэх боломжийг олгодог.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1317, "Shift Key", "Шилжих товч", "", "Шилжих товчийг удаан дарж өөр нэг тэмдэгттэй цуг ашиглах үед олон боломжууд гарч ирдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1318, "Ship station", "", "A mobile station in the maritime mobile service located on board a vessel which is not permanently moored, other than a survival craft station.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1319, "Short message service", "Захидлын үйлчилгээ", "", "Гар утаснаасаа захидал илгээх боломж", "SMS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1320, "Short Messaging Service (Service Management System)", "", "A store and forward message service available on most second generation digital systems that allows short messages (up to 140 octets) to be sent to the mobile and displayed on a small screen. The control and signalling channels are normally used to deliver these messages.", "", "SMS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1321, "Shortcut", "", "", "Ямар нэг зүйлийг хурдан хийх аргыг хэлнэ. Жишээ нь: байрлалыг товчлох (дүрсээр мэт харагддаг) тухайн програмыг хурдан аргаар олж ажиллуулах.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1322, "Shrink-to-fit", "Багасгаж тохируулах", "", "Тухайн дэлгэцэнд тохируулж зургийг багасгах", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1323, "Shutdown", "Унтраах", "", "Компьютерийг унтраах", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1324, "Sideband", "Хажуугийн зурвас", "", "Синулэг зөөгч давтамжаас дээш болон доош байрлах давтамжийн зурвас ба модуляцаас үүсгэгдэх шаардлагатай спекрийн бүрэлдэхүүн хэсгүүдийг багтаасан байдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1325, "Sideband", "", "A frequency band lying above or below a sinusoidal carrier frequency and containing spectral components of significance produced by modulation.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1326, "Sig file", "", "", "Автоматаар и-мэйлийн төгсгөлд гарч ирэх текст хэсэг (жишээ нь холбоо барих мэдээлэл)", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1327, "Signal", "Дохио", "", "Дохио гэдэг нь зарим үзэгдлийн зан буюу шинж чанарын тухай мэдээллийг дамжуулах үйл ажиллагаа юм. Цахилгаан, цахилгаан эрчим хүч нь холбосон сувгийн дагуу өөр нэг хэлхээнд нэг цэгээс дамждаг. Дохио нь аналоги болон тоон өгөгдөл байж болно. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1328, "Signal Booster", "", "Compensates for loss of effect (weakening of the signal in the co-axial cable) between the outer antenna and the phone. Applies to both incoming and outgoing signals.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1329, "Signal System 7", "", "International standard protocol defined for open signaling in the digital public switched network. It is based on a 64 kbps channel and allows for information transfer for call control, database and billing management, and for maintenance functions.", "", "SS7", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1330, "Signaling Control Channel", "", "A logical channel used in the PDC system to convey signalling information between the mobile and the network.", "", "SCCH", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1331, "Signal-to-interference ratio", "", "See signal-to-interference ratio.", "", "S/I", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1332, "Signal-to-interference ratio", "", "The ratio of power in a signal to the interference power in the channel. The term is usually applied to lower frequency signals, such as voice waveforms, but can also be used to describe the carrier wave. See also carrier-to-interference ratio.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1333, "Signal-to-interference ratio; signal/interference ratio", "", "The ratio, generally expressed in decibels, of the power of the wanted signal to the total power of interfering signals and noise, evaluated in specified conditions at a specified point of a transmission channel.", "", "", "Note 1 – A distinction is made, for example, between: – at the receiver input, the radio-frequency (RF) signal-to-interference ratio; – at the receiver output, the audio-frequency (AF) signal-to-interference ratio and the video-frequency (VF) signal-to-interference ratio. Note 2 – In each individual case, the noise and interfering signals taken into account should be specified. Note 3 – The term “signal-to-disturbance ratio” or its abbreviated form “signal/disturbance ratio”, which is already used for electromagnetic compatibility, may be used as a synonym.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1334, "Signal-to-noise ratio", "", "See signal-to-noise ratio.", "", "S/N", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1335, "signal-to-noise ratio", "", "The ratio of power in a signal to the noise power in the channel. This term is usually applied to lower frequency signals, such as voice waveforms. See also carrier-to-noise ratio.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1336, "Signal-to-Noise Ratio", "", "See signal-to-noise ratio.", "", "SNR", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1337, "Silicon chip", "", "", "Түгээмэл нэршил нь  интеграл схем (силикон) чип юм. Тухайлбал микро хэлхээн дэхбүтцийн хувьд нягт(транзистор болон конденсатор зэрэг) цахиурын маш нимгэн зүсмэл бүрэлдэхүүн хэсгээс бүрэлдсэн чип юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1338, "Simple object access protocol ", "", "", "XML-д суурилсан сүлжээгээр мессеж харилцан солилцоход зориулагдсан стандарт бөгөөд  өөр үйлдлийн системүүд дээр ажиллаж байгаа програмуудад зөвшөөрөгддөг.", "SOAP", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1339, "Simplex", "Симплэкс", "", "Мэдээлэл дамжуулах чиглэл илэрхийлдэг.Тэр дуплекс буюу бүрэн дуплекс гэж нэрлэдэг. тэр үед аль аль чиглэлд, байж болно, эсвэл нэг чиглэлд зөвхөн энгийн гэж нэрлэдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1340, "Simulation", "Загварчлал", "", "Програм хангамжийн янз бүрийн загварын нөхцөлийн нөлөөг харуулахын тулд бүрэлдэхүүн хэсгийг ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1341, "Single sideband", "", "Pertaining to a transmission or emission where only either the lower sideband or the upper sideband resulting from amplitude modulation is preserved.", "", "SSB", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1342, "Single sideband ", "Дан хажуугийн зурвас", "", "Зөвхөн бага хажуугийн зурвасд буюу агуургийн модуляц үр дүнд дээд хажуугийн зурвасд аль хадгалагдан үлдсэн байдаг бөгөөд энэ нь дамжуулах эсвэл ялгаруулалт холбоотой.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1343, "Single spacing ", "Дан хоорондын зай", "", "Нэг зайтай текстийн мөрнүүдийн хооронд хоосон мөр байж болохгүй.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1344, "Single stepping", "Дан гишгүүр", "", "Алхам горим програм нь ганц гишгүүрийн хэрэглэгчийн хяналтын дор нэг удаа тайлангийн гүйцэтгэл юм. Үүнийг гүйцэтгэсний дараа хэрэглэгч тус бүр тайлангийн нөлөөг ажиглах боломжийг олгодог.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1345, "Single thread", "Ганц урсгалтай", "", "Нэг багц боловсруулах. Энэ нь илүү нарийн төвөгтэй систем боловсрогддог  эсвэл хэд хэдэн тутмын нэг урсгалтай (олон урсгалаар чиглэсэн биш ) уламжлалт энгийн програм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1346, "Single-user licence", "Ганц хэрэглэгчийн лиценз", "", "Ганц хэрэглэгчийн лиценз нь зөвхөн нэг компьютерт хэрэглэдэг програм хангамж.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1347, "Site licence", "Веб сайтын лиценз", "", "Сайтын лиценз нь дурын тооны компьютерийг нэг байрлалд ашиглаж болох програм хангамж юм. Нэг төлбөрөөр олон удаа суулгах боломжтой.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1348, "Slide", "Слайд", "", "Текст болон зургийн төрлийг мөн бусад хүссэн зүйлсийг хэсэгчлэн хуудаснуудад хийн танилцуулахад ашигладаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1349, "Slide bar", "Слайд бар", "", "Програм дэлгэцийн цонх хэтэрхий том үед, тухайн слайд бар босоо буюу хэвтээ програмын дэлгэц дээр тодорхой хэсгийг шилжүүлж өгнө. Мөн “гүйлгэх бар” гэж нэрлэдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1350, "Slide master", " Слайд мастер", "", "Пресентейшнд энэ нь тусгай слайд ба пресентейшний бүхий л слайдуудын загварыг тодорхойлдог.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1351, "Slideshow presentation", "Слайд танилцуулах", "", "Мультимедиа програм хангамжийг танилцуулах  слайд юм. Танилцуулгийг том дэлгэц эсвэл проектор тавьж  бүх  үзэгчид харх боломж олгоно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1352, "Slot", "Слот, нүхлэх, сэтлэх, ангархай, нүх, ховил, хором, зурвас, хоног", "", "Энэ нь  диск төхөөрөмж болон сүлжээний интерфэйсүүд гэх мэт нэмэлт  бүрэлдэхүүн хэсэгүүдийг  компьютерт холбоход ашигладаг залгуур(оролт) юмаа. Мөн эдгээр залгууруудыг өргөтгсөн слот гэж нэрлэдэг. Олон тооны солтуудаар хангасанаар нэмэлт бүрээлдэхүүн хэсэгүүдийн тоог илүү ихээр нэмэлтүүдийг боломжтой. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1353, "Slotted ALOHA ", "", "An access technique synchronizing the transmitters to time-slots in the channel and having the transmitter wait until the next available slot to send its packet", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1354, "Slow Associated Control Channel", "", "A low-speed control channel associated with a traffic channel and used to transmit supervision and control messages between the mobile and the network.", "", "SACCH", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1355, "Slow Dedicated Control Channel", "", "A low-speed bi-directional point-to-point control channel used to transmit service request, subscriber authentication, ciphering initiation, equipment validation and traffic channel assignment messages between the mobile and the network.", "", "SDCCH", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1356, "Slow fading", "", "A long term fading effect changing the mean value of the received signal. Slow fading is usually associated with moving away from the transmitter and experiencing the expected reduction in signal strength.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1357, "Slow Frequency Hopped Multiple Access", "", "A spread-spectrum system where the hop (dwell) time is much greater the information symbol period. When hopping is coordinated with other elements in the network, the multiple access interference in the network is greatly reduced.", "", "SFHMA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1358, "Small computer systems interface ", "Жижиг компьютерийн систем интерфэйс", "", "Жижиг компьютерийн систем интерфэйс  хамгийн бага зардлаар, микро төхөөрөмжөөр өргөн хүрээтэй өндөр өгөгдөл дамжуулах чадвартай болох зорилготой бас шаардлагатай бол диск төхөөрөмж болон сканнер холбож болно. Йим микро компьютерийг  өргөн хүрээнд ашиглах боломжтой.Тус бүр  интерфэйс долоон хүртэл дагалдах төхөөрөмжүүд  холбож болно.", "SCSI", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1359, "Small scale integration ", "Нимгэн метал ялтас нэгтгэх", "", " 20 хүртлэх логик гарцтай чип нэгдсэн хэлхээний төрөл ба голдуу (хатуу) утастай логик хэлхээнд ашиглаж байна.", "SSI", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1360, "Smard card ", "Смарт карт", "", "Кредит картны хэмжээтэй дотроо нэг эсвэл түүнээс илүү хагас дамжуулагч агуулсан байдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1361, "Snail mail", "Удаан хугацаагаар дамжуулдаг шуудан", "", "Жирийн  шуудантай зэрэгцүүлж байруулах хэрэглээний  шуудан", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1362, "Soft handoff", "", "A process of establishing a call connection simultaneously to two separate base stations in a CDMA system. This technique allows the use of a dual path in the handoff region to improve performance. Soft handoff can only occur between base stations using the same RF channel. See also hard handoff.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1363, "Software", "Программ хангамж", "", "Компьютер програм хэрэглэх мөн компьтерийн системийг удирдах  эсвэл  программ  хангамжийн  санал,маягт ажилын  хүснэгтээр ажиллаж сурах.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1364, "Software library ", "Программ хангамжийн цугуулга", "", "Энэ нь хэрэглэгчийн програмд ачаалахад зориулсан машины код буюу иж бүрэн програм хангамжийн багц.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1365, "Software Licence", "Программ хангамжийн лиценз", "", "Программ хангамжийг худалдан авах үед програм хангамж ашиглах  нөхцөл өгдөг лиценз бий болдог (мөн лиценз гэрээ буюу хэрэглэгчийн лицензийн гэрээ, EULA гэж нэрлэдэг). Эдгээр бүтээгдэхүүн хоорондох нөхцөл ихээхэн ялгаатай байна. Түүний лиценз нь зөвшөөрөгдөөгүй програм хангамжийг ашиглах нь хууль бус юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1366, "Space diversity", "", "A diversity technique widely used in wireless systems since the very beginning. It consists of two receive antennas physically (spatially) separated to provide decorrelated receive signals.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1367, "Space diversity reception", "", "Diversity reception in which several antennas are used at appropriate distances from each other in a radio station.", "", "", "Note – For line-of-sight radio-relay systems, separation is generally vertical, whereas for transhorizon radio-relay systems, it is generally horizontal.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1368, "Space Division Multiple Access", "", "Also known as multiple beam frequency reuse, this technique employs spot beam antennas to reuse frequencies by pointing the antenna beams using the same frequency in different directions.", "", "SDMA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1369, "Space probe", "", "A spacecraft designed for making observations or measurements in space.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1370, "Space radiocommunication", "Сансрын радио холбоо", "", "Нэг буюу хэд хэдэн сансрын станц ашиглах, эсвэл нэг буюу хэд хэдэн орон зайд хиймэл дагуул болон бусад обьектуудыг тусгасан ямар нэгэн Радио холбоо.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1371, "Space radiocommunication", "", "Any radiocommunication involving the use of one or more space stations or the use of one or more reflecting satellites or other objects in space.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1372, "Space station", "Сансрын станц", "", "Дэлхийн агаар мандлын үндсэн хэсэгт байрлах бөгөөд  дамжуулах үүрэгтэй  обьект дээр оршдог станц юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1373, "Space station", "", "A station located on an object which is beyond, is intended to go beyond, or has been beyond, the major portion of the Earth’s atmosphere.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1374, "Space system", "", "Any group of cooperating earth stations and/or space stations employing space radiocommunication for specific purposes", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1375, "Specialized Mobile Radio", "", "A service defined by the FCC operating in the 800 MHz and 900 MHz bands providing a variety of 2-way communications services to various users. This is the basic 2-way trunked service used by many companies in the construction and service industries. These frequencies are gradually being converted to ESMR operation.", "", "SMR", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1376, "Spectrum spreading", "", "The process of increasing the occupied spectrum of a signal well beyond that needed to transmit the information.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1377, "Speech coding", "", "An electronic process of sampling and digitizing a voice signal.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1378, "Spot noise factor, spot noise figure", "", "The ratio of the exchangeable power spectral density of the noise appearing at a given frequency at the output of a given linear two-port electrical network, to the spectral density which would be present at the output if the only source of noise were the thermal noise due to a one-port electrical network connected to the input and which is assumed to have at all frequencies a noise temperature equal to the reference thermodynamic temperature fixed, by convention, around 290 K.", "", "", "Note 1 – The spot noise factor F( f ) is related to the equivalent spot noise temperature T( f ) as follows: 0 ( ) ( ) 1 T T f F f = + where T0 is the thermodynamic reference temperature. Note 2 – The value of the ratio F( f ) may be expressed in decibels. In English, the term “noise factor” is generally employed when the ratio is expressed arithmetically, and “noise figure” is employed when the ratio is expressed in decibels.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1379, "Spot noise temperature", "Шуугианы температур", "", "Boltzmann байнгын хувааж нэг порт цахилгаан сүлжээний тухайн давтамж, дээр арилжиж дуу чимээ чадлын спектр.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1380, "spot noise temperature", "", "The exchangeable noise power spectral density at a given frequency of a one-port electrical network, divided by Boltzmann’s constant.", "", "", "Note 1 – This definition assumes that quantum effects are negligible. Note 2 – The spot noise temperature has the sign of the real part of the network impedance. Note 3 – If the network has an impedance with a positive real part, its noise temperature at a given frequency equals the thermodynamic temperature to which a resistor equal in value to the real part of the impedance should be brought in order to obtain an available power of thermal noise equal to the available power of the noise of the network at the same frequency. Note 4 – A receiving antenna can be regarded as a one-port electrical network when viewed from its output port.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1381, "Spread Rate", "", "Spread rate is also known as the chip rate and is the rate of the digital code used to spread the information. The spreading rate is typically at least 100x the information rate.", "", "SR", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1382, "Spread spectrum", "", "A term used to describe a system that uses spectrum spreading techniques in its operation.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1383, "Spread Spectrum", "", "See spread spectrum", "", "SS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1384, "Spreading loss", "", "The attenuation of an electromagnetic wave due uniquely to the fact that with increasing distance the energy is distributed over a wider area.", "", "", "Note – In a homogeneous and isotropic medium, the spreading loss is characterized by a decrease of the power flux-density in proportion to the reciprocal of the square of the distance to the source.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1385, "Spurious emission", "", "Emission on a frequency or frequencies which are outside the necessary bandwidth and the level of which may be reduced without affecting the corresponding transmission of information. Spurious emissions include harmonic emissions, parasitic emissions, intermodulation products and frequency conversion products, but exclude out-of-band emissions.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1386, "Standard frequency", "", "A frequency with a known relationship to a frequency standard.", "", "", "Note – The term standard frequency is often used for the signal whose frequency is a standard frequency.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1387, "Standard-time-signal emission", "", "An emission which disseminates a sequence of time signals at regular intervals with a specified accuracy.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1388, "Station", "", "One or more transmitters or receivers of a combination of transmitters and receivers, including the accessory equipment, necessary at one location for carrying on a radiocommunication service, or the radioastronomy service.", "", "", "Note 1 – In the RR, each station shall be classified by the service in which it operates permanently or temporarily.Note 2 – Radiocommunication service; Service de radiocommunication; Servicio de
radiocomunicación (RR 1.19).");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1389, "Station (radio)", "станц ", "", "Сансрын радио сүлжээ болон радио холбооны сүлжээг ашиглан дамжуулахад 1 газарт байрласан нэвтрүүлэгч ба дамжуулагч мөн нэмэлт төөөрөмжүүд шаардлагатай.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1390, "Stationary satellite", "", "A satellite which remains fixed in relation to the surface of the primary body; by extension, a satellite which remains approximately fixed in relation to the surface of the primary body.", "", "", "Note – A stationary satellite is a synchronous satellite with an orbit which is equatorial, circular and direct.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1391, "Station-keeping satellite", "", "A satellite, the position of the centre of mass of which is controlled to follow a specified law, either in relation to the positions of other satellites belonging to the same space system or in relation to a point on Earth which is fixed or moves in a specified way.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1392, "Streaming media ", "Өгөгдөл дамжуулах", "", "Цахим хуудсанд өгөгдөл татаж байхад мэдээлэл (дуу болон видео)–ийг саатал багатайгаар үзүүлэх техник. Тоглуулагдахаас өмнө буфер хийхийн тулд видеоны эхний хэдэн секунд татагдсан байх хэрэгтэй. Тоглуулагч програм ажиллаж эхлэхэд эхний дүрсүүд тоглогдож байх үед дараа дараагийн дүрсүүд буферлэгдэн татагдсан байна. Энэ аргын онцлог нь хэрэглэгчийн компьютер дээр тоглуулагдаад дуусан дүрсний өгөгдөл хадгалагддаггүй.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1393, "String ", "Тэмдэгт мөр, цуваа, жагсаалт, урсгал", "", "Тэмдэгтүүдийн жагсаалт бүхий текстэн өгөгдөл юм. Тухайлбал үг болон цэг таслал гэх мэт.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1394, "String data ", "Стринг дата", "", "Стринг дата нь тэмдэгтүүдийн жагсаалт бүхий текстэн өгөгдөл юм. Жишээ нь үг болон цэг таслал гэх мэт. Стринг өгөгдөл нь тэмэгтүүдээс бүрдэх ба хэмжээ нь өөр өөр байж болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1395, "String operator ", "Стринг оператор", "", "Стринг оператор нь нийлүүлэлт сэлгэлт гэх мэт үйлдлүүдийг ашиглан стрингүүдийг нийлүүлэх эсвэл өөрчлөх үйлдлийг хийнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1396, "Structure diagram ", "Бүтцийн зураг", "", "Програмын эсвэл системийн загварыг төлөөлөн хийх зорилготой. Үүнд түвшингүүдийн дугаар, тус бүр нь бүх л загварыг гэхдээ хэсгүүдийн ахисан түвшинд тодорхойлдог.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1397, "Structured Systems Analysis and Design Method ", "", "", "Нэгдсэн Вант улсын засгийн газар болон  арилжааны байгуулагуудын өргөн ашигладаг том хэмжээний програмын багцын шинжилгээний болон загварчилалын стандарт арга юм.", "SSADM", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1398, "Style ", "Хэв маяг, загвар", "", "Үг боловсруулах болон бусад програмын тэмдэгтүүдийг тодруулах, налуу болгох гэх мэтийг хийдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1399, "Style sheet ", "Хуудасны хэв маяг загвар ", "", "Үүний тусламжтайгаар баримт бичигт ямар фонт, хэмжээтэйгээр бичих вэ гэдгээ хэрэглэгч тодорхойлж болно. Толгой хэсэг, текстийн хэсэг болон тайлбарын хэсгүүд нь тухайн хуудсанд бүгд өөр өөр хэв маягтай байлгаж болно. Шинэ тэмдэгтүүдийн хэлбэрийн багцыг загвар болгон хадгалаж болно. Энэ хуудас нь эдгээр бүх загваруудыг өөртөө агуулсан байна.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1400, "Stylus ", "Бичигч элемент", "", "График таблет болон PDA-д зурахад ашиглагддаг үзэг хэлбэртэй төхөөрөмж.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1401, "Subcript ", "Индекс", "", "Текстийн үндсэн шугамаас доор тэмдэглэгдсэн тэмдэгт.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1402, "Subdirectory ", "Дэд каталоги", "", "Дэд директори гэдэг нь директори доторх директорийг хэлнэ. Дэд директорид файл эсвэл өөр нэг дэд директори эсвэл аль аль  нь байж болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1403, "Subfolder ", "Дэд хавтас", "", "Дэд хавтас гэдэг нь хавтас доторх хавтасыг хэлнэ. Үүнд файл эсвэл өөр нэг дэд хавтас эсвэл аль аль нь байх боломжтой.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1404, "Subheading ", "Дэд сэдэв", "", "Баримт бичигтэй хамааралтай толгой мэдээлэл эсвэл гарчиг. Энэ нь ихэвчлэн мөрийн зүүн гар талд байрладаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1405, "Subprogram ", "Дэд програм", "", "Том цогц даалгавруудыг арилган жижиг даалгавруудад хуваах зорилготой програмчлалын арга юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1406, "Subprogram ", "Дэд програм", "", "Онгцой даалгавар гүйцэтгэдэг програмын багц юм. Гэхдээ бүрэн програм биш юм. Зөвхөн шаардлагатай үед л програмд орж ажиллана.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1407, "Subscribe ", "Мэйлд жагсаалт үүсгэх", "", "Цахим шуудангын жагсаалтад эсвэл мэдээний бүлэг хэсэгт нэгдэхийг хэлнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1408, "Subscribe ", "Захиалах, зөвшөөрөх ", "", "Зарим цахим хэлэлцүүлэг өөртөөгөө нэгтгэхийн тулд бүртгүүлж хэлэлцүүлэгт шинэ зурвас нийтлэгдсэн тохиолдолд автоматаар таны шууданд мэдээллэх болно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1409, "Subscriber Identity Module Card", "", "A small printed circuit board that must be inserted in any GSM-based mobile phone when signing on as a subscriber. It contains subscriber details, security information and memory for a personal directory of numbers. A Subscriber Identity Module is a card commonly used in a GSM phone. The card holds a microchip that stores information and encrypts voice and data transmissions, making it close to impossible to listen in on calls. The SIM card also stores data that identifies the caller to the network service provider.", "", "SIM Card", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1410, "Sub-synchronous (super-synchronous) satellite", "", "A satellite for which the mean sidereal period of revolution about the primary body is a submultiple (an integral multiple) of the sidereal period of rotation of the primary body about its own axis.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1411, "SUM ", "Дүн, дүгнэлт, хэмжээ, нийт дүн, нийлбэр", "", "Тоонуудыг нэмсэний үр дүн. Хүснэгттэй ажиллаж байгаа үед НИЙЛБЭР буюу SUM нь функц болдог.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1412, "Super High Frequency", "", "The RF spectrum between 3 GHz and 30 GHz.", "", "SHF", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1413, "Super-computer ", "Хүчин чадал сайн компьютер", "", "Асар хурдан ажилладаг маш том компьютерийг хэлнэ. Энэ нь ихэвчлэн олон процессортой параллель ажиллагаатай байдаг. Өгөгдөл нэгэн зэрэг боловсруулагдсанаар ажлуудыг маш хурдтай гүйцэтгэдэг. Жишээ нь цаг агаарын мэдээ цуглуулдаг компьютер болон нисэх онгоцны загварыг турших гэх мэт.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1414, "Supplementary services", "", "A group of network layer protocol functions that provide call independent functions for mobile phones. These include: call forwarding, follow-me, advice of charge, reverse charging, etc.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1415, "Suppressed carrier", "нам зөөгч дохио", "", "Синуслэг зөөгч бүрэлдэхүүн хүч нь ерөнхийдөө шинээр зохион demodulation ашиглаж чадахгүй байгаа тийм гэж түвшинд хүртэлбуурсан байна далайцын модуляц нь дамжуулах эсвэл дэгдэлт холбоотой.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1416, "Suppressed carrier", "", "Pertaining to a transmission or emission with amplitude modulation where the power of the sinusoidal carrier component is reduced to a level such that it generally cannot be reconstituted and used for demodulation.", "", "", "Note – A carrier is regarded as being supressed when its level is at least 32 dB and preferably 40 dB or more below the peak envelope power of the emission.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1417, "Survival craft station", "", "A mobile station in the maritime mobile service or the aeronautical mobile service intended solely for survival purposes and located on any lifeboat, life-raft or other survival equipment.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1418, "Symbian", "", "Symbian is a bold new venture formed by Nokia, Ericsson, Motorola, and Psion to create easy to use operating systems for wireless devices and personal digital assistants (PDAs). The first operating system is called EPOC and was launched earlier this year.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1419, "Synchronization Channel", "", "A logical channel used by mobile stations to achieve time synchronization with the network. Used in GSM, cdma2000, and W-CDMA systems.", "", "SCH", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1420, "Synchronized satellite, phased satellite", "", "A satellite controlled so as to have an anomalistic period or a nodal period equal to that of another satellite or planet, or to the period of a given phenomenon, and to pass a characteristic point in its orbit at specified instants.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1421, "Synchronous Digital Hierarchy", "", "An international standard for synchronous optical transmission. This standard allows the world-wide connection of digital networks.", "", "SDH", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1422, "Synchronous Mode", "", "Type of transmission in which the transmission and reception of all data is synchronized by a common clock and transmitted in blocks rather than individual characters. This mode gives higher data throughput than asynchronous mode, but can be less secure. Synchronous mode does not require a start and stop codes as in asynchronous mode. Can also mean that the data stream has the same capacity in both directions. See also asynchronous mode.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1423, "Synchronous satellite", "", "A satellite for which the mean sidereal period is equal to the sidereal period of rotation of the primary body about its own axis; by extension, a satellite for which the mean sidereal period of revolution is approximately equal to the sidereal period of rotation of the primary body.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1424, "System loss", "", "The ratio, usually expressed in decibels, for a radio link, of the radio frequency power input to the terminals of the transmitting antenna and the resultant radio frequency signal power available at the terminals of the receiving antenna.", "", "", "Note 1 – The available power is the maximum active power which a source can deliver to a load i.e. the power which would be transferred if the impedances were conjugately matched. Note 2 – The system loss may be expressed by:Note 3 – The system loss excludes losses in feeder lines but includes all losses in radiofrequency circuits associated with the antenna, such as ground losses, dielectric losses, antenna
loading coil losses, and terminating resistor losses. ");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1425, "Task Bar", "", "", "Ихэвчлэн дэлгэцний доод талд байрлах цонхийг task bar гэнэ. Одооуншиж байгаа хэрэглэгдэхүүнүүдийг харуулдаг, тэдгээрийг хооронд нь сольж байрлуулах боломжтой.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1426, "Technician", "Техникч", "", "Албан байгуулгийнkомпьютерийн системийн эвдрэл, гэмтлийг хариуцан, тогтвортой ажиллагааг хангах үүрэгтэй ажилтныг хэлнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1427, "Telecommuncations ", "Цахилгаан  Холбоо", "", "Алсын зайгаар мэдээллийг хүлээн авах ба дамжуулахыг тайлбарласан нэр томъёо юм. Холболтонд кабель,цахилгаан утас, шилэн кабель эсвэл радио/хүлээн авагч   зэргийг хэрэглэдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1428, "Telecommunications Industry Association (U.S.)", "", "One of the Telecommunications standards setting bodies in the United States.", "", "TIA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1429, "Telecommunications Technology Association (Korea)", "", "A telecommunications standards setting body in Korea.", "", "TTA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1430, "Telecommunications Technology Committee (Japan)", "", "A private-sector corporate body established in 1985 to prepare domestic standards relevant to Japanese telecommunications.", "", "TTC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1431, "Telecommute", "Цахилгаан холболт", "", "Алсын зайнаас ажиллахыг тайлбарласан нэр томъёо юм. Жишээ нь: Гэрээсээ албан байгууллага уруугаа цахилгаан холболтоор харилцан ажиллахыг хэлнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1432, "Tele-conferencing", "Теле хурал", "", "Газарзүйн байрлалаас хамааралгүйгээр хүмүүсийг хооронд нь холбоход зориулагдсан харилцаа холбооны шугамууд юм. Теле хурал нь зөвхөндуу хоолойгоор харилцан ярьдаг. Хэрвээ дүрсээ харах шаардлагтайvideo-conferencing хийнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1433, "Tele-working", "Теле ажилгаа", "", "Хүмүүс өөрсдийн гэрээcээ албан байгуулагтайгаа холбогдон ажилхийг мэдээллийн технологит хүлээн зөвшөөрхөд үүнийг ашигладаг. Төв компьютер эсвэл сүлжээнд холбогдсон хэрэглэгчийн холболтыг таслах, ерөнхийдөө бол email ба fax ашиглана.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1434, "Template", "Үлгэр загвар ", "", "Тодорхой, тогтсон форматтай бичиг баримтыг ашиглах нь таны бүтээмжийг ихэсгэж, цаг хугацааг хэмнэх зориулалттай.Үүний тулд үлгэр загвар ба форматыг бэлтгэдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1435, "Temporary Directory Number", "", "A temporary identification number assigned to a mobile while attached to the network.", "", "TDN", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1436, "Temporary Mobile Station Identity", "", "An identification number assigned to a mobile station while it is attached to the network. This number is maintained in the VER and SIM while the mobile is attached to the network and is used to route calls to and from the mobile.", "", "TAMS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1437, "Terminal ", "Терминал, төгсгөлийн төхөөрөмж", "", "Сүлжээнд холбогдсон хэрэглэгчийн хандалтыг хангах компьютер эсвэл төгсгөлийн төхөөрөмжүүдийг хэлнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1438, "Terminated ", "Тусгайлсан хэмжигдэхүүн ", "", "Энэ нь өгөдлийн жагсаалтын төсгөлийг тэмдэглэхэд ашигладаг. Программ нь терминатор хүртэл өгөгдлийг боловсруулдаг. ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1439, "Terrestrial radiocommunication", "Газрын Радио холбоо", "", "Сансрын Радио холбоо , рентген одон орон болон бусад ямар нэгэн Радио холбоо.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1440, "Terrestrial radiocommunication", "", "Any radiocommunication other than space radiocommunication or radio-astronomy.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1441, "Terrestrial station", "", "A station effecting terrestrial radiocommunication.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1442, "Test plan ", "Тестын бүдүүвч зураг", "", "Системд тест хийхэд ашиглагдах тестийнерөнхий загвар юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1443, "Text  line", "Текст мөр", "", "Үг ба тэмдэгтүүдээр бүтсэн, текстийн ганц мөрийг хэлнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1444, "Text box", "Текстын Хүрээ", "", "Хүснэгтэн дотор текстийг байрлуулах ба тухайн текстийн өгөгдлийг боловсруулахад ашигладаг. Жишээ нь веб загварчлал гэх мэт.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1445, "Text data", "Текст өгөгдөл", "", "Програмын шалгах явцад тухайн програмын зөв ажиллагааг харахын тулд програмист шалгах өгөгдлийг ашигладаг байна.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1446, "Text only", "зөвхөн текст", "", "Ихэнх веб сайтуудад text only гэсэн  сонголт байдаг. Энэ нь хэрэглэгчдэд уншихад хялбар байдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1447, "Thesaurus", "Тайлбар толь ", "", "Зөв бичгийн дүрмийн агуулгуудыг төлөөлөх  толь бичгийг хэлнэ.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1448, "Thin film transistor ", "Нимгэн давхрагат транзистор", "", "LCD дэлгэц, зөөврийн ба суурин компьютерт нийтлэг байдаг.", "TFT", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1449, "Third-generation computer ", "Гуравдагч үеийн компьютер", "", "Транзисторуудыг интеграль хэлхээгээр орлуулан дараагийн үеийн компьютеруудыг гарган авсан ба одоогийн бидний хэрэглэж байгаа компьютерууд юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1450, "Third-generation language", "Гуравдагч үеийн хэл", "", "3дагч үеийн хэл буюу програмчлалын хэл бөгөөд,өдөр тутамд ашиглагдах утасны апплэкейшн болон компьютерийн хэрэглээны програмуудыг уг хэлээр бичдэг.Жишээ нь: Cobol,C,C++,JavaScript,C# гэх мэт.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1451, "Thread", "Трейд", "", "Сүлжээнд холбогдсон үед дэх ижил төрлийн агуулгатай мэдээнүүдийн жагсаалт.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1452, "Thread ", "Трейд", "", "Систем дэх өгөгдлийн нэг багц дээр хийгдэж байгаа боловсруулалт юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1453, "Three dimensional ", "Гурван талт хэмжээс", "", "Урт өргөн өндөр гуравыг харуулсан шугам зурагт дүрс", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1454, "Three letter acronym ", "Үг товчлох ", "", "Энэ нь цаг хугацааг хэмнэхын тулд товчилж бичихэд хэрэглэдэг", "TLA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1455, "Three-dimensional array ", "Гурван талт хэмжээст хүснэгт", "", "Хүснэгтэд гурван талт хэмжээсыг дүрслэн үзүүлэх", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1456, "Throw ", "Шинэчлэх", "", "Жавагын нууц үгийг онцгой тохиолдолд шинэчлэхыг зөвшөөрдөг", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1457, "Thumbnail ", "Хураангуй товчлол", "", "Зурагны том хэмжээсын жижиг хувилбар ", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1458, "Ticker tape ", "Цаасан тууз", "", "Дэлгэцэн дээр байгаа тэкстэнд хөдөлгөөн оруулах", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1459, "Time dispersion", "", "Time dispersion is a manifestation of multipath propagation that stretches the signal in time so that the duration of the received signal is greater than the transmitted signal.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1460, "Time diversity", "", "The technique used by CDMA systems to overcome the effects of multipath fading. Through the use of a rake receiver, individual elements, or fingers, can be offset in time to account for different arrival times of multipath signals.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1461, "Time Division Duplex", "", "A duplexing technique dividing a radio channel in time to allow downlink operation during part of the frame period and uplink operation in the remainder of the frame period. See also duplex.", "", "TDD", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1462, "Time Division Multiple Access", "", "A technology for digital transmission of radio signals between, for example, a mobile telephone and a radio base station. In TDMA, the frequency band is split into a number of channels which in turn are stacked into short time units so that several calls can share a single channel without interfering with one another. Networks using TDMA assign 6 timeslots for each frequency channel. TDMA is also the name of a digital technology based on the IS-136 standard. TDMA is the current designation for what was formerly known as D-AMPS. See also IS-136 and D-AMPS.", "", "TDMA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1463, "Time Division Transmit Diversity", "", "A technique utilizing multiple transmit stations to originate the downlink signal and improve performance. The transmit station used can be determined by either a fixed pattern or based on a QoS measurement made at the mobile. See also STD and TSTD.", "", "TDTD", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1464, "Time Switched Transmit Diversity", "", "A technique utilizing multiple transmit stations to originate the downlink signal and improve performance. The transmit station used is determined by a fixed selection pattern similar to frequency hopping. See also STD and TDTD.", "", "TSTD", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1465, "Timeline ", "Цаг товлох", "", "Олон мэдээллын хэрэгсэл түүнчлэн видио болон дуу бичлэгт буюу онцгой арга хэмжээнд цаг товлох", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1466, "Toggle ", "Товчлуур ", "", "Өөр эсвэл нэг төлөв хооронд хөдөлгөх, унтраалга нээх хаах товчлуур", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1467, "Toner ", "Хор хэвлэлийн будаг ", "", "Зураг хувилах эсвэл лазердан хэвлэхэд хэрэглэдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1468, "Tool ", "Хэрэгсэл", "", "Компьютерын програм хангамжын жижиг хэсэг, том програмын хэсэгт онцгой тохиолдолд даалгавар дамжуулдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1469, "Toolbar ", "Хэрэгслын самбар ", "", "Хөндлөн босоо самбарт аппликэйшн дахь өөр өөр хэрэгслүүдыг үзүүлдэг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1470, "Toolbox ", "", "", "Хайрцаганд аппликэйшин дахь өөр өөр хэрэгслүүдийг чөлөөт үзүүлдэг ба агуулдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1471, "Top down programming ", "Програмыг сайжруулах ", "", "Програмыг онцгой тохиолдолд бүтэц хэлбэр нь аажмаар сайжруулдаг", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1472, "Top level domain ", "Өндөр түвшний бүс", "", "Системийн нэрний бүс, өндөр түвшний нэрны бүс", "TLD", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1473, "Total Access Communications System", "", "An analog cellular communications system derived from AMPS. It has been adopted in the UK (ETACS) and operates in the 900 MHz band. Likewise adopted in Japan first as JTACS, it exists at present as the further evolved NTACS with narrower bandwidth.", "", "TACS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1474, "Total loss", "", "The ratio, usually expressed in decibels, between the radio-frequency power supplied by the transmitter of a radio link and the radio-frequency power supplied to the corresponding receiver in real installation, propagation and operational conditions.", "", "", "Note – It is necessary to specify in each case the points at which the power supplied by the transmitter and the power supplied to the receiver are determined, for example: – before or after the radio frequency filters or multiplexers that may be employed at the sending or the receiving end; – at the input or at the output of the transmitting and receiving antenna feed lines.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1475, "Touch pad ", "Хулганы оролтонд хэрэглэдэг төхөөрөмж", "", "Дисплей дэлгэцэн дээрх курсорыг хөдөлгөхын тулд жижиг хавтгай гадарга дээгүүр хуруугаа гулсуулдаг, хуруугаа товших", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1476, "Touchscreen ", "Мэдрэмхий видио дисплей", "", "Хуруугаа хүргэхэд оролтын дохио хүлээн авдаг мэдрэмхий видио дисплей дэлгэц", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1477, "Tower ", "Суурин компьютер", "", "Гэрийн компьютерын дэлгэцны төрөлын  бүрэлдэхүүн хэсгийг хөндлөн биш босоогоор зохицуулдаг. Энэ нь ширээн дээр тавихад зориулагдсан байдаг", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1478, "Tower ", "Цамхаг", "", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1479, "Track ", "Тойрог зам", "", "Хатуу болон уян дискний гадарга дахь соронзон аргаар бичиж, уншиж болдог дугуй тойрог зам", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1480, "Track changes ", "Тойрог зам өөрчлөх", "", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1481, "Trackerball ", "Трэнбол", "", "Хулганы оронд хэрэглэдэг оруулалтын төхөөрөмж. Эргэлддэг бөмбөг – г алгаараа эсвэл хуруугаараа өнхрүүлэх замаар", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1482, "Traffic Channel", "", "A logical channel that allows the transmission of speech or data. In most second generation systems, the traffic channel can be either full or half-rate.", "", "TCH", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1483, "Traffic Channel - full rate", "", "A traffic channel using full rate voice coding.", "", "TCH/F", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1484, "Traffic Channel - half rate", "", "A traffic channel using half rate voice coding.", "", "TCH/H", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1485, "Trans- horizon radio-relay system", "Трансын үе  радио релей станц", "", "Нэвт зааг трофосфер тархалт, гол төлөв форвард сарнилт ашигладаг рентген релей систем.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1486, "Transceiver", "", "A transmitter and receiver contained in one package. A 2- way radio or cell phone is an example of a transceiver.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1487, "Trans-horizon propagation", "Туйлшралын давхарга ", "", "Радио далайц нь хүлээн авахаас гадна агаар мандалын туйлшралын хороондын байршилыг дамжуулдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1488, "Trans-horizon propagation", "", "Tropospheric propagation between points close to the ground, the reception point being beyond the radio horizon of the transmission point.", "", "", "Note – Trans-horizon propagation may be due to a variety of tropospheric mechanisms such as diffraction, scattering, reflection from tropospheric layers. However ducting is not included because in a duct there is no radio horizon.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1489, "Trans-horizon radio-relay system", "", "Radio-relay system using trans-horizon tropospheric propagation, chiefly forward scatter.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1490, "Trans-ionospheric propagation", "", "Radio propagation between two points situated below and above the height of the maximum electron density of the ionosphere.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1491, "Transmission Control Protocol", "", "TCP/IP is the standard communications protocol required for computers communicating over the Internet. To communicate using TCP/IP, computers need a set of software instructions or components called a TCP/IP stack.", "", "TCP", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1492, "Transmission loss", "", "The ratio, usually expressed in decibels, for a radio link, of the power radiated by the transmitting antenna to the power that would be available at the receiving antenna output if there were no loss in the radio-frequency circuits of the antennas, it being assumed that the antenna radiation characteristics are retained.", "", "", "Note 1 – Transmission loss is equal to system loss minus the loss in the radio-frequency circuits which are integral parts of the antennas. Note 2 – The transmission loss may be expressed by: L = Ls – Ltc – Lrc dB where Ltc and Lrc are the losses, expressed in decibels, in the transmitting and receiving antenna
circuits respectively, excluding the dissipation associated with the antennas radiation, i.e., the
definitions of Ltc and Lrc are 10 lg (r′/r), where r′ is the resistive component of the antenna
circuit and r is the radiation resistance. ");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1493, "transmit diversity", "", "A technique utilizing multiple transmit stations to originate the downlink signal and improve performance. The station used is determined by either a fixed pattern or a quality measurement at the mobile. See also TDTD, STD and TSTD.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1494, "Transmitter", "", "Equipment which feeds the radio signal to an antenna, for transmission. It consists of active components such as the mixer, driver and PA and passive components such as the TX filter. Taken together, these components impress a signal onto an RF carrier of the correct frequency by instantaneously adjusting its phase, frequency, or amplitude and provide enough gain to the signal to project it through the ether to its intended target.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1495, "Transmitter (radio) ", "", "Apparatus producing radio-frequency energy for the purpose of radiocommunication.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1496, "Trellis Code Modulation", "", "A type of channel coding that, unlike block and convolutional codes, provide coding gain by increasing the size of the signal alphabet and use multi-level phase signalling.", "", "TCM", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1497, "Triple mode (tri-mode)", "", "A combined analog and digital mobile phone. Allows operation of the phone in the existing analog system at 800 MHz and in digital systems at both 800 MHz and 1900 MHz.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1498, "Troposphere", "", "The lower part of the Earth’s atmosphere extending upwards from the Earth’s surface, in which temperature decreases with height except in local layers of temperature inversion. This part of the atmosphere extends to an altitude of about 9 km at the Earth’s poles and 17 km at the equator.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1499, "Tropospheric", "Агаар мандалын хамгийн доод үе ", "", "Тропонцфэр нь дэлхийн гадрагын ойрлцоо орших атмосфэрийн доод үе давхарг бөгөөд дэлхийн өргөргөөс хамаарч түүний үе давхаргийн өндөр өөр өөр байдаг.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1500, "Tropospheric  propagation", "Агаар мандалын тархалт ", "", "Ионцфэр ба тропонсфэрийн орчин дахь санамсаргүй өөрчлөлтэй харилцан хамаарл бүхий олон цацаргын үйлчлэлийн нөлөөгөөр анх нэвтэрүүлсэн долгионы хэлбэр янз бүрийн гаржуудалд орно.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1501, "Tropospheric propagation", "", "Propagation within the troposphere and by extension, propagation beneath the ionosphere, when not influenced by the ionosphere.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1502, "Tropospheric radio-duct", "Радио сувгийн агаар мандал ", "", "Өндөр давтамжтай радио нь агаар мандалд маш бага эрчим хүчийг тараадаг. Үелсэн байдал нь нэгэн жигд хязгаарлагдмал байна.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1503, "Tropospheric radio-duct", "", "A quasi-horizontal stratification in the troposphere within which radio energy of a sufficiently high frequency is substantially confined and propagates with much lower attenuation, than would be obtained in a homogeneous atmosphere.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1504, "Tropospheric-scatter propagation", "Агаар мандалд тархалт саринх ", "", "Гадаргын долгионы тархалтанд нөлөөлх нэгэн төрлийн бус орчин тропосферийн нөлөөл нь түүний рефекарцийн процесстой холбоотой юм.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1505, "Tropospheric-scatter propagation", "", "Tropospheric propagation by scattering from many inhomogeneities and/or discontinuities in the refractive index of the atmosphere.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1506, "Ttopocentric angle", "", "The angle formed by imaginary straight lines that join any two points in space with a specific point on the surface of the Earth.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1507, "Twisted Pair", "", "Two insulated copper wires twisted together with the 'twists' or 'lays' varied in length to reduce potential signal interference between the pairs. Where cables comprise more than 25 pairs, they are usually bundled together and wrapped in a cable sheath. Twisted pair is the most commonly used medium for connecting telephones, computers and terminals to PABXs, supporting speeds up to 64kbits/sec.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1508, "Ultra High Frequency", "", "The RF spectrum between 300 MHz and 3 GHz", "", "UHF", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1509, "UMTS Terrestrial Radio Access", "", "A W-CDMA standard developed ETSI, ARIB and the TIA. This system uses DSSS and either FDD or TDD depending on its frequency assignment and application.", "", "UTRA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1510, "Unit Under Test", "", "An acronym describing some type of electrical apparatus connected to test instrumentation. The apparatus can range from a simple circuit a complex subsystem such as a mobile phone, base station or MSC. See also DUT.", "", "UUT", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1511, "Universal Mobile Telecommunications System", "", "Third generation telecommunications system based on WCDMA DS.", "", "UMTS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1512, "Universal Personal Telecommunications", "", "A set of standards developed by the CCITT for wireline personal communications.", "", "UPT", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1513, "Universal Time", "", "– UT0 is the mean solar time of the prime meridian obtained from direct astronomical observation; – UT1 is UT0 corrected for the effects of small movements of the Earth relative to the axis of rotation (polar variation) (see Recommendation ITU-R TF.460); – UT2 is UT1 corrected for the effects of a small seasonal fluctuation in the rate of rotation of the Earth.", "", "UT", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1514, "Universal Wireless Consortium", "", "Body of vendors and operators promoting and implementing the IS-136 digital standard. Also specifying the future development of the standard and facilitating roaming agreements between IS-136 operators.", "", "UWC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1515, "Unwanted emissions", "", "Emissions consisting of spurious emissions and out-of-band emissions.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1516, "Uplink", "", "The transmission path from the mobile station up to the base station.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1517, "Up-link", "", "A radio link between a transmitting earth station and a receiving space station.", "", "", "Note 1 – The term is also used in terrestrial communications for a link between a transmitting mobile station and a receiving base station. Note 2 – The symbol ↑ is used as a subscript for letter symbols representing quantities associated with an up-link");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1518, "Up-link ", "Илгээх шугам", "", "Дэлхийн дамжуулах станц болон хүлээн авагч сансрын станц хоорондын радио холбоос.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1519, "Urban cells", "", "The coverage provided by base stations located in urban areas. The radius of these cells is usually much smaller than suburban and rural cells due to the more difficult propagation environment.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1520, "usable field-strength, [usable power flux-density]", "", "Minimum value of the field-strength [minimum value of the power flux-density] necessary to permit a desired reception quality, under specified receiving conditions, in the presence of natural and man-made noise and of interference, either in an existing situation or as determined by agreements or frequency plans.", "", "", "Note 1 – The desired quality is determined in particular by the protection ratios against noise and interference and in the case of fluctuating noise or interference, by the percentage of time during which the required quality must be ensured. Note 2 – The receiving conditions include, inter alia: – the type of transmission and frequency band used; – the receiving equipment characteristics (antenna gain, receiver characteristics, siting, etc.); – receiver operating conditions, particularly the geographical zone, the time and the season, or the fact that, if the receiver is mobile, a median field strength for multipath propagation must be considered. Note 3 – The term “usable field-strength” corresponds to the term “necessary field-strength” which appears in many ITU texts.");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1521, "Vector Sum Excited Linear Predictive", "", "A type of speech coding using an excitation signal generated from three components: the output of a long term or pitch filter and two codebooks. VSELP was used in the IS-54 standard and operated at a rate of 8 kbps.", "", "VSELP", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1522, "Vertical directivity pattern", "босоо чиглүүлийн диаграмм", "", "Антенны чиглүүлэлтийн диаграмм нь тодорхой босоо гадаргууд оршино.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1523, "Vertical directivity pattern", "", "An antenna directivity diagram in a specified vertical plane.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1524, "Very High Frequency", "", "The RF spectrum between 30 MHz and 300 MHz.", "", "VHF", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1525, "Vestigial sideband", "", "A sideband in which only the spectral components corresponding to the lower frequencies of the modulating signals, are preserved, the other components being strongly attenuated.", "", "VSB", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1526, "Vestigial-sideband", "Хэсэгчлэн дарагдсан хажуугийн зурвас", "", "Нэг иж бүрэн хажуугийн зурвас, түүний нэмэлт хэсэгчлэн дарагдсан хажуугийн зурвасыг ашиглаж байгаа нь дамжуулах эсвэл ялгаралтай холбоотой.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1527, "Vestigial-sideband", "", "Pertaining to a transmission or emission in which one complete sideband and its complementary vestigial sideband are utilized.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1528, "Virtual Private Network", "", "A private telecommunications network created using the resources of the PSTN and customized dialing, switching and routing functions.", "", "VPN", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1529, "Visible arc", "", "The common part of the arc of the geostationary satellite over which the space station is visible above the local horizon from each associated earth station in the service area.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1530, "Visitor Location Register", "", "The functional unit responsible for managing mobile subscribers currently attached to the network. Two types of information reside in the VLR: subscriber information and the part of the mobile information that allows incoming calls to be routed to the mobile subscriber. The VLR stores the MSRN, TMSI, the location area, data on supplementary services, IMSI, MS ISDN number, HLR address or GT, and local MS identity, if used.", "", "VLR", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1531, "Viterbi algorithm", "", "A technique for searching a decoding trellis to yield a path with the smallest distance. This is also known as maximum likelihood decoding.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1532, "Vocoder", "", "Refers to a voice encoder which is a device that codes and decodes the human voice (sound waves) into digital transmission. Higher vocoder speeds offer enhanced sound quality.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1533, "Voice activity detector", "", "The device that detects voice activity and allows DTX to operate. VAD, in conjunction with DTX reduces power consumption in the mobile station and RF interference in the system by muting the transmitter when there is no voice to transmit.", "", "VAD", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1534, "Voice Over Internet Protocol", "", "A technology for transmitting ordinary telephone calls over the Internet using packet-linked routes. VoIP is not simply for voice over IP, but is designed to accommodate two-way video conferencing and application sharing as well.", "", "VOIP", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1535, "Walsh Code", "", "A group of spreading codes having good autocorrelation properties and poor crosscorrelation properties. Walsh codes are the backbone of CDMA systems and are used to develop the individual channels in CDMA. For IS-95, here are 64 codes available. Code 0 is used as the pilot and code 32 is used for synchronization. Codes 1 though 7 are used for control channels, and the remaining codes are available for traffic channels. Codes 2 through 7 are also available for traffic channels if they are not needed. For cdma2000, there exists a multitude of Walsh codes that vary in length to accommodate the different data rates and Spreading Factors of the different Radio Configurations. For more information see Agilent application note 'Performing cdma2000 Measurements Today'.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1536, "Wavelength Division Multiplexing", "", "A new technology that uses optical signals on different wavelengths to increase the capacity of fiber optic networks in order to handle a number of services simultaneously.", "", "WDM", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1537, "Wideband Code Division Multiple Access", "", "See W-CDMA.", "", "WCDMA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1538, "wireless access", "Утасгүй хандалт", "", "Радио холболт болон  радио хэрэглэгч хоорондын үндсэн сүлжээ. Утасгүй сүлжээний холболтын жишээ: - тогтмол утасгүй хандалт -хөдөлгөөнт утасгүй хандалт  тогтворгүй утасгүй хандалт", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1539, "wireless access", "", "Radio connection between a radio user and a core network.", "", "", "Note – Examples of wireless access: – fixed wireless access (FWA); – mobile wireless access (MWA); – nomadic wireless access (NWA).");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1540, "Wireless Application Protocol", "", "A free, unlicensed protocol for wireless communications that makes it possible to create advanced telecommunications services and to access Internet pages from a mobile telephone. WAP is a de facto standard that is supported by a large number of suppliers.", "", "WAP", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1541, "Wireless Communications Association", "", "The Wireless Communications Association represents the fixed broadband wireless access industry worldwide. It's mission is to advance the interests of the wireless systems that provide data (including Internet and e-commerce), voice and video services on a subscription basis through land-based towers to fixed reception/transmit devices.", "", "WCA", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1542, "Wireless Local Area Network", "", "A wireless version of the LAN. Provides access to the LAN even when the user is not in the office.", "", "WLAN", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1543, "Wireless Local Loop", "", "A wireless connection of a telephone in a home or office to a fixed telephone network.", "", "WLL", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1544, "Wireless Markup Language", "", "A markup language developed specifically for wireless applications. WML is based on XML.", "", "WML", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1545, "Wireless Office Systems", "", "A technology that allows the user to transfer calls to a mobile telephone.", "", "WOS", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1546, "Wireless Private Automatic Branch Exchange", "", "A customer premise telephone switching system using wireless technology to link the individual user stations to the central switching unit. The WPABX is capable of interfacing to a telephone central office with trunk groups and routing calls based on a 3 or 4 digit telephone extension number.", "", "WPABX", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1547, "World Wide Web Consortium", "", "A sector-wide body which promotes standardization of WWW technology. Major Internet related vendors are consortium members, and to date the body has standardized a range of crucial technologies including HTTP, HTML, XML, etc.", "", "W3C", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1548, "X federation", "Нэгдсэн холбоо", "", "Нэгдмэл <x>чанарыг тодорхойлох.", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1549, "X.25 protocol", "", "A CCITT recommendation defining the interface between Data Terminal Equipment (DTE) and Data Communications Equipment (DCE) operating in the packet mode on public data networks. It is a set of 3 peer protocols: a physical layer X.21), a link control layer (HDLC) and a network layer. X.25 is based on the concept of virtual circuits, which can be temporary or permanent.", "", "", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1550, "", "", "", "Тайлбарлах кодыг маш хурдан ажиллуулах боломжтой үр дүнтэй боловч сурахад хэцүү.", "1GL", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1551, "", "", "", "2 дахь үеийн програмчлалын хэл (2GL) 3-р үеийн програмчлалын хэл болон түүнээс өмнөх машины хэлнээс ялгахын тулд энэ нэр томьёог гаргаж ирсэн. Код нь уншиж программист бичнэ. ", "2GL", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1552, "", "", "", "2 дугаар зеийн хэлэнд машин хамааралтай байдаг бол 3 дугаар үеийн хэлэнд програм илүү хамааралтай байдаг.", "3GL", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1553, "", "", "", "3 дугаар үеийн хэлийг бодвол хэв маяг нь илүү боловсронгуй хүчирхэг, уян хатан, техник хангамжийг хөгжүүлэх зорилготой.", "4GL", "");
