INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1558, "", "Хувийн хугацаанд зориулсан шуудан", "", "Хувийн хугцаанд зориулсан шууданийг электрон шуудантай саад учруулахгүйгэр хэрэглэг.", "SMTP", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1559, "", "Зурган файлыг хэлбэрт оруулан холбох үгний товчлол", "", "Графикин файлыг хэлбэрт оруулахдаа ихэнхи тохиолдолд зурагнуудыг хуулбарлан компьютерт оруулдаг ", "TIFF ", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1560, "", "", "An open architecture, Ericsson's communications platform. A system for computer-controlled digital exchanges that constitute the nodes in large public telecommunications networks. The basis for Ericsson's wireline and mobile systems.", "", "AXE", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1561, "", "", "A term, usually associated with a TDMA system, describing a group of bits or other information transmitted by the system. Also refers to the time the transmitter is on and radiating.", "", "burst", "");
