INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1564, "", "", "An operating system that turns voice-oriented handsets into Mediaphones and Wireless Information Devices. EPOC places a lighter load on the processor compared to present PDA operating systems and thus has the capacity to enhance the multimedia capacity of mobile phones. EPOC is being developed by Symbian, a joint company of Psion, Nokia, Ericsson, Motorola and Matsushita (Panasonic). It constitutes an open platform optimized for mobile phone use.", "", "EPOC", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1565, "", "", "GSM 900, or just GSM, is the world's most widely used digital network and now operating in over 100 countries around the world, particularly in Europe and Asia Pacific.", "", "GSM 900", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1566, "", "", "Standardized interface between a Radio Network Controller Network and Packet Subsystem (e.g. RNC3GSGSN).", "", "lu", "");
