INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1569, "", "", "A type of non-linear (logarithmic) quantizing, companding and encoding technique for speech signals based on the mlaw. This type of companding uses a m factor of 255 and is optimized to provide a good signal-to-quantizing noise ratio over a wide dynamic range", "", "m-law companding", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1570, "", "", "Historically, the Ministry of Post, Telecommunications and Telegraph. Now a term to describe the incumbent, dominant operator in a country, many of which are being or have been privatized.", "", "PTT", "");
INSERT or IGNORE INTO word(id, eng, mon, eng_desc, mon_desc, abb, text_desc) VALUES (1571, "", "", "A computer operating system. UNIX is designed to be used by many people at the same time and has TCP/IP built-in. It is a very common operating system for servers on the Internet.", "", "UNIX", "");
